// === SCENES ===
export const scenes = [
  { id:"uiia", bg:"chuong1.jpg", next:"s0" },
  { id:"s0", bg:"chuong1.jpg", next:"s1" },
  { id:"s1", bg:"vanphong.jpg", next:"s2" },
  { id:"s2", bg:"vanphong.jpg", char:"Narrator", text:"Văn phòng thám tử tư Heirlock vào buổi chiều mưa.", next:"s3" },
  { id:"s3", bg:"vanphong.jpg", char:"Narrator", text:"Có tiếng gõ cửa vang lên.", next:"s4" },
  { id:"s4", bg:"vanphong.jpg", char:"Heirlock.png", text:"Ai vậy?", next:"s5" },
  { id:"s5", bg:"vanphong.jpg", char:"Narrator", text:"Cánh cửa gỗ chậm rãi mở ra, mang theo mùi ẩm mốc.", next:"s6" },
  { id:"s6", bg:"vanphong.jpg", char:"Mainson.png", text:"Thám tử Siames Heirlock, tôi không làm phiền anh chứ?", next:"s7" },
  { id:"s7", bg:"vanphong.jpg", char:"Narrator", text:"Bóng dáng quen thuộc của chàng cảnh sát Mainson xuất hiện.", next:"s8" },
  { id:"s8", bg:"vanphong.jpg", char:"Narrator", text:"Mainson là một cảnh sát thuộc đội điều tra chuyên án, đã từng là người đứng ra hợp tác với những thám tử tư khác để hỗ trợ giải quyết vụ án nhiều lần.", next:"s9" },
  { id:"s9", bg:"vanphong.jpg", char:"Narrator", text:"Mainson bước vào, chẳng chút ngần ngại mà thả mình lên chiếc ghế sofa ở giữa căn phòng, ném tập hồ sơ trên bàn rồi thở dài một tiếng.", next:"s10" },
  { id:"s10", bg:"vanphong.jpg", char:"Heirlock.png", text:"Để tôi đoán nhé, là vụ một giáo viên tiếng Nhật của trường K.K tử vong tại nhà riêng?", next:"s11" },
  { id:"s11", bg:"vanphong.jpg", char:"Mainson.png", text:"Anh nắm bắt thông tin nhanh thật.", next:"s12" },
  { id:"s12", bg:"vanphong.jpg", char:"Heirlock.png", text:"Tin tức lan nhanh lắm. Tôi đoán nạn nhân là Axton?", next:"s13" },
  { id:"s13", bg:"vanphong.jpg", char:"Narrator", text:"Hắn đứng dậy, tiện tay rót cho mình một cốc cafe rồi đặt xuống trước mặt Mainson, mỉm cười.", next:"s14" },
  { id:"s14", bg:"vanphong.jpg", char:"Heirlock.png", text:"Cafe đen không đường không đá như bình thường, đúng chứ cảnh sát Mainson?", next:"s15" },
  { id:"s15", bg:"vanphong.jpg", char:"Mainson.png", text:"Đúng vậy, ngạc nhiên là anh vẫn còn nhớ khẩu vị đồ uống của tôi đấy.", next:"s16" },
  { id:"s16", bg:"vanphong.jpg", char:"Narrator", text:"Mainson nhận lấy cốc cafe rồi đưa lên uống trong khi vị thám tử đã bắt đầu lật tập hồ sơ, xem xét kĩ lưỡng.", next:"s17" },
  { id:"s17", bg:"vanphong.jpg", char:"Mainson.png", text:"Nạn nhân là giáo viên tiếng Nhật, 42 tuổi tử vong tại căn hộ riêng. Hàng xóm báo thấy mùi lạ, cảnh sát phá cửa vào thì phát hiện thi thể trong phòng khách.", next:"s18" },
  { id:"s18", bg:"vanphong.jpg", char:"Heirlock.png", text:"Có dấu hiệu bị tấn công chứ?", next:"s19" },
  { id:"s19", bg:"vanphong.jpg", char:"Mainson.png", text:"Không. Không có vết thương rõ ràng, nguyên nhân cái chết là mất mấu và bị siết cổ, vùng kín đã hoàn toàn bị hủy hoại, cửa đã khóa từ bên trong.", next:"s20" },
  { id:"s20", bg:"vanphong.jpg", char:"Narrator", text:"Hắn tặc lưỡi, đây là một vụ án trong phòng kín.", next:"s21" },
  { id:"s21", bg:"vanphong.jpg", char:"Heirlock.png", text:"Khóa từ trong à...", next:"s22" },
  { id:"s22", bg:"vanphong.jpg", char:"Mainson.png", text:"Bọn tôi đã nghĩ tới việc nạn nhân bị cướp nhưng không có dấu hiệu nào cho thấy điều đó, tuy nhiên có một vài chi tiết kì lạ.", next:"s23" },
  { id:"s23", bg:"vanphong.jpg", char:"Narrator", text:"Như đã bắt được điều quan trọng, vị thám tử tư vươn người về phía trước để nghe rõ hơn.", next:"s24" },
  { id:"s24", bg:"vanphong.jpg", char:"Heirlock.png", text:"Chi tiết nào?", next:"s25" },
  { id:"s25", bg:"vanphong.jpg", char:"Mainson.png", text:"Một tách trà đổ trên bàn, tách còn lại còn nguyên vẹn, ghế bị kéo lệch, và một cuốn sổ ghi chép đặt ngay ngắn.", next:"s26" },
  { id:"s26", bg:"vanphong.jpg", char:"Mainson.png", text:"Giống như hung thủ là người quen của nạn nhân vậy nên Axton mới chủ động mở cửa cho đối phương vào, thậm chí còn rót trà mời.", next:"s27" },
  { id:"s27", bg:"vanphong.jpg", char:"Heirlock.png", text:"Trà đổ, cuốn sổ ngay ngắn. Có vẻ như hung thủ đã chuẩn bị rất kĩ càng trước khi ra tay nên mọi thứ đều theo kế hoạch, nhanh gọn, hạn chế động chạm vào hiện trường.", next:"s28" },
  { id:"s28", bg:"vanphong.jpg", char:"Narrator", text:"Heirlock ngả người ra sau ghế, ánh mắt ánh lên tia tò mò.", next:"s29" },
  { id:"s29", bg:"vanphong.jpg", char:"Heirlock.png", text:"Cậu tới đây để cho tôi đi xem hiện trường, đúng chứ?", next:"s30" },
  { id:"s30", bg:"vanphong.jpg", char:"Narrator", text:"Mainson bật cười, cậu gật đầu rồi lên tiếng.", next:"s31" },
  { id:"s31", bg:"vanphong.jpg", char:"Mainson.png", text:"Đúng là không thể qua mắt anh được. Xe đang đợi dưới hầm rồi.", next:"s32" },
  { id:"s32", bg:"vanphong.jpg", char:"Heirlock.png", text:"Cho tôi ba phút. Tôi cần lấy áo khoác và vài thứ khác.", next:"s33" },
  { id:"s33", bg:"vanphong.jpg", char:"Narrator", text:"Mainson đứng dậy rồi bước ra khỏi văn phòng, bên ngoài, tiếng mưa dường như nặng hạt hơn khiến cậu có cảm giác bất an.", next:"s34" },
  { id:"s34", bg:"vanphong.jpg", char:"Narrator", text:"Cả hai rời khỏi tòa nhà của Heirlock, chiếc xe cảnh sát phóng đi giữa màn mưa dày đặc, chỉ còn ánh đèn chớp tắt liên tục trong một ngày đầu tuần.", next:"s35" },
  { id:"s35", bg:"hientruongAxton.jpg", next:"s36" },
  { id:"s36", bg:"hientruongAxton.jpg", char:"Narrator", text:"Hai người dừng chân ở căn nhà nhỏ ở khu ngoại ô. Những dải ruy băng vàng chắn ngang lối vào, ánh đèn xanh đỏ nhấp nháy phản chiếu lên bức tường gạch men ẩm thấp đã cũ kỹ của nơi này.", next:"s37" },
  { id:"s37", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Đây, nhà của Axton. Cảnh sát vẫn đang giữ nguyên hiện trường.", next:"s38" },
  { id:"s38", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Căn hộ nhỏ nhắn, đơn giản. Có vẻ như Axton sống một mình.", next:"s39" },
  { id:"s39", bg:"hientruongAxton.jpg", char:"Narrator", text:"Heirlock bước vào bên trong, ánh mắt nhanh chóng quét khắp hiện trường.", next:"s40" },
  { id:"s40", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Mùi ẩm và hơi cũ. Giống căn hộ của người sống một mình lâu năm.", next:"s41" },
  { id:"s41", bg:"hientruongAxton.jpg", char:"Narrator", text:"Bàn ghế được sắp xếp ngăn nắp, chỉ có một vài vật dụng cá nhân đặt rải rác quanh phòng.", next:"s42" },
  { id:"s42", bg:"hientruongAxton.jpg", char:"Narrator", text:"Hắn cúi xuống nhìn qua vết đổ của tách trà. Đôi mắt vị thám tử khẽ nheo lại.", next:"s43" },
  { id:"s43", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Trên cốc không có dấu tay?", next:"s44" },
  { id:"s44", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Đúng thế, hai tách trà, một cho nạn nhân một cho hung thủ, hai người này có lẽ có quen biết từ trước, nhưng hung thủ không hề động vào cốc dù chỉ một chút trong khi chiếc đã đổ thì có dấu hiệu đã được nạn nhân sử dụng nhiều lần vào ngày xảy ra án mạng.", next:"s45" },
  { id:"s45", bg:"hientruongAxton.jpg", char:"Narrator", text:"Cả hai bước sâu hơn vào căn hộ. Cảnh sát hiện trường khẽ gật đầu chào khi thấy Mainson đi cùng Heirlock. Không gian tĩnh lặng, chỉ còn tiếng mưa rơi ngoài cửa sổ lặp đi lặp lại.", next:"s46" },
  { id:"s46", bg:"hientruongAxton.jpg", char:"Narrator", text:"Trên sàn phòng khách, thi thể người đàn ông trung niên nằm ngửa. Khuôn mặt tái nhợt, hai mắt mở trừng, cổ hằn rõ vết bầm tím dài ngoằng quanh yết hầu.", next:"s47" },
  { id:"s47", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Bị siết cổ bằng dây mảnh… Có vẻ là dây vải, hoặc thắt lưng.", next:"s48" },
  { id:"s48", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Pháp y nói đó là sợi dây thừng nhỏ, loại chuyên dùng trong thể thao, đường kính khoảng hai ly. Không tìm thấy tại hiện trường.", next:"s49" },
  { id:"s49", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Hung thủ mang theo, tức là đã có chuẩn bị.", next:"s50" },
  { id:"s50", bg:"hientruongAxton.jpg", char:"Narrator", text:"Heirlock cúi xuống, lật nhẹ phần cổ áo nạn nhân.", next: "s51" },
  { id:"s51", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Vết siết nằm lệch một chút sang bên phải. Nghĩa là hung thủ thuận tay phải, đứng phía sau khi siết.", next:"s52" },
  { id:"s52", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Chính xác. Ngoài ra vùng kín bị rạch nhiều lần, có lẽ do thù oán cá nhân.", next:"s53" },
  { id:"s53", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Thù oán cá nhân à, nghe cũng hợp lý đấy.", next:"s54" },
  { id:"s54", bg:"hientruongAxton.jpg", text:"Hắn đứng dậy, bước vòng quanh thi thể. Dưới chân nạn nhân, có một vệt trượt dài, in dấu bàn chân mờ mờ.", next:"s55" },
  { id:"s55", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Dấu giày cỡ 41, kiểu đế da, không phải giày thể thao. Cũng không có bùn đất, hung thủ đến bằng ô tô, hoặc từ nơi rất gần đây.", next:"s56" },
  { id:"s56", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Cũng có khả năng là giày của chính nạn nhân.", next:"s57" },
  { id:"s57", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Không đâu. Axton đi dép trong nhà mà, phải không?", next:"s58" },
  { id:"s58", bg:"hientruongAxton.jpg", text:"Mainson im lặng một lúc, rồi mở tập hồ sơ.", next:"s59" },
  { id:"s59", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Đúng. Khi cảnh sát phá cửa, đôi dép vẫn để ngay ngắn bên kệ.", next:"s60" },
  { id:"s60", bg:"hientruongAxton.jpg", text:"Heirlock bước đến bàn, quan sát kỹ hai tách trà. Một tách đổ nghiêng, nước loang ra mặt bàn gỗ tạo thành vệt nhạt hình bán nguyệt.", next:"s61" },
  { id:"s61", bg:"hientruongAxton.jpg", text:"Hắn liếc qua cuốn sổ ghi chép đặt ngay ngắn trên bàn. Một trang bị gập lại cẩn thận, góc giấy hơi sờn.", next:"s62" },
  { id:"s62", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Tôi có thể xem qua chứ?", next:"s63" },
  { id:"s63", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Cứ tự nhiên.", next:"s64" },
  { id:"s64", bg:"hientruongAxton.jpg", text:"Heirlock lật trang giấy ra, chữ viết đều đặn ghi chép giờ giấc các buổi dạy, nhưng dòng cuối cùng dừng ở một câu cụt ngủn: “Trao đổi với Kael.”", next:"s65" },
  { id:"s65", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Kael?", next:"s66" },
  { id:"s66", bg:"hientruongAxton.jpg", text:"Ánh mắt Heirlock lóe sáng.", next:"s67" },
  { id:"s67", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Có ai trong vòng quan hệ bạn bè của nạn nhân có cái tên này không?", next:"s68" },
  { id:"s68", bg:"hientruongAxton.jpg", char:"Mainson.png", text:"Kael. Giáo viên thể dục, đồng nghiệp của nạn nhân.", next:"s69" },
  { id:"s69", bg:"hientruongAxton.jpg", text:"Heirlock khẽ mỉm cười, đặt cuốn sổ lại vị trí cũ.", next:"s70" },
  { id:"s70", bg:"hientruongAxton.jpg", char:"Heirlock.png", text:"Có vẻ chúng ta đã biết người đầu tiên cần nói chuyện rồi.", next:"s71" },
  { id:"s71", bg:"hientruongAxton.jpg", text:"Ngoài trời, tiếng mưa dội mạnh hơn lên mái ngói. Trong căn phòng nhỏ, hương trà nhài và hơi lạnh của tử thi hòa vào nhau...", next:"s72" },

  { id:"s72", bg:"santruong.jpg", next:"s73" },
  { id:"s73", bg:"santruong.jpg", text:"Trời vẫn chưa dứt mưa khi xe của Mainson dừng lại trước cổng trường K.K...", next:"s74" },
  { id:"s74", bg:"santruong.jpg", text:"Heirlock bước xuống, kéo cao cổ áo khoác, ánh mắt quét qua dãy hành lang dài hun hút.", next:"s75" },
  { id:"s75", bg:"santruong.jpg", char:"Mainson.png", text:"Kael đang ở trong phòng giáo viên. Anh ta dạy lớp Sinh học buổi chiều nhưng hôm nay xin nghỉ.", next:"s76" },
  { id:"s76", bg:"santruong.jpg", char:"Heirlock.png", text:"Tốt, ta không cần chen giữa đám học sinh.", next:"s77" },

  { id:"s77", bg:"phongsinhhoc.jpg", next:"s78" },
  { id:"s78", bg:"phongsinhhoc.jpg", text:"Phòng thực hành Sinh học có mùi hóa chất nhè nhẹ.", next:"s79" },
  { id:"s79", bg:"phongsinhhoc.jpg", text:"Trên bàn, ống nghiệm xếp ngay ngắn, vài con mẫu vật ngâm trong lọ thủy tinh.", next:"s80" },
  { id:"s80", bg:"phongsinhhoc.jpg", text:"Một người đàn ông gầy gò... là Kael, gã đang cắm cúi ghi chép gì đó.", next:"s81" },
  { id:"s81", bg:"phongsinhhoc.jpg", char:"Mainson.png", text:"Xin lỗi, anh là thầy Kael đúng không?", next:"s82" },
  { id:"s82", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"À vâng. Hai người là?", next:"s83" },
  { id:"s83", bg:"phongsinhhoc.jpg", char:"Mainson.png", text:"Cảnh sát điều tra... Đây là thám tử Heirlock.", next:"s84" },
  { id:"s84", bg:"phongsinhhoc.jpg", text:"Gã ngẩng lên, thoáng giật mình.", next:"s85" },
  { id:"s85", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"À vụ đó. Tôi nghe rồi. Thật là khủng khiếp.", next:"s86" },
  { id:"s86", bg:"phongsinhhoc.jpg", char:"Heirlock.png", text:"Chúng tôi chỉ muốn hỏi vài câu, không làm mất nhiều thời gian của anh đâu.", next:"s87" },
  { id:"s87", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"Được, mời ngồi. Nhưng tôi nói trước, tôi với tên đó cũng không thân thiết gì mấy.", next:"s88" },
  { id:"s88", bg:"phongsinhhoc.jpg", char:"Heirlock.png", text:"Tôi đoán vậy. Trong trường, ai cũng nói anh ấy được mọi người quý mến...", next:"s89" },
  { id:"s89", bg:"phongsinhhoc.jpg", text:"Gã nhếch môi cười nhạt, ngả người ra ghế giáo viên.", next:"s90" },
  { id:"s90", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"“Quý mến” à? Cái đó còn tùy cách nhìn.", next:"s91" },
  { id:"s91", bg:"phongsinhhoc.jpg", char:"Heirlock.png", text:"Ý anh là sao?", next:"s92" },
  { id:"s92", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"Người ta chỉ quý kẻ biết làm màu...", next:"s93" },
  { id:"s93", bg:"phongsinhhoc.jpg", char:"Heirlock.png", text:"Giả tạo? Anh có thể nói rõ hơn không?", next:"s94" },
  { id:"s94", bg:"phongsinhhoc.jpg", text:"Kael im lặng vài giây rồi tiếp lời.", next:"s95" },
  { id:"s95", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"Thôi, chuyện nội bộ thôi. Nói ra lại bảo tôi nhỏ nhen.", next:"s96" },
  { id:"s96", bg:"phongsinhhoc.jpg", text:"Heirlock nhìn sang Mainson, hai người ngầm hiểu ý nhau mà gật đầu.", next:"s97" },
  { id:"s97", bg:"phongsinhhoc.jpg", text:"Hắn biết gặng hỏi thêm giờ chẳng thu được kết quả...", next:"s98" },
  { id:"s98", bg:"phongsinhhoc.jpg", char:"Heirlock.png", text:"Thầy Axton đã ghi trong sổ 'Trao đổi với Kael'. Anh giải thích sao về chuyện đó?", next:"s99" },
  { id:"s99", bg:"phongsinhhoc.jpg", text:"Gã sững lại một chút, ánh mắt có chút lay động.", next:"s100" },
  { id:"s100", bg:"phongsinhhoc.jpg", char:"Kael.png", text:"Cái gì cơ? Trong sổ có tên tôi?", next:"s101" },
  { id: "s101", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Đúng. Và hiện trường có hai tách trà, một đã đổ, một còn nguyên. Anh có thể nói tại sao nạn nhân lại hẹn gặp anh không?", next: "s102" },
  { id: "s102", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi… tôi không nhớ. Có thể là trao đổi chuyên môn thôi.", next: "s103" },
  { id: "s103", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Nhưng anh dạy Sinh, Axton dạy Nhật. Hai người đâu có gì để trao đổi chuyên môn?", next: "s104" },
  { id: "s104", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson bắt lấy thời điểm, giọng nghiêm nghị.", next: "s105" },
  { id: "s105", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Anh Kael, đề nghị anh hợp tác điều tra. Nếu phát hiện lời nói dối, chúng tôi sẽ đưa anh về đồn.", next: "s106" },
  { id: "s106", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Lời đe dọa khiến Kael co rúm, sợ hãi hiện rõ.", next: "s107" },
  { id: "s107", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Hai người có mâu thuẫn, phải chứ?", next: "s108" },
  { id: "s108", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Ừ, có. Một chút.", next: "s109" },
  { id: "s109", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Gã thở hắt ra, xoa hai bên thái dương.", next: "s110" },
  { id: "s110", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Mâu thuẫn gì?", next: "s111" },
  { id: "s111", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Anh ta lúc nào cũng được khen. Học sinh yêu quý, đồng nghiệp nể trọng. Tôi cũng tầm tuổi đó, cũng cố gắng, mà chẳng ai nhớ tới. Anh bảo sao tôi không khó chịu?", next: "s112" },
  { id: "s112", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Khó chịu đến mức cãi nhau à?", next: "s113" },
  { id: "s113", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Có một lần. Về… chuyện học sinh.", next: "s114" },
  { id: "s114", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Chính xác là chuyện gì, tôi có thể biết thêm được không?", next: "s115" },
  { id: "s115", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael đột nhiên lạnh nhạt hẳn, chẳng còn vẻ sợ hãi như vừa rồi nữa mà trực tiếp đứng dậy đuổi khéo hai người.", next: "s116" },
  { id: "s116", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Các anh cảnh sát thông cảm, tôi cần phải chuẩn bị cho buổi dạy học sắp tới, chúng ta có thể nói chuyện vào lần tiếp theo.", next: "s117" },
  { id: "s117", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Nhưng…", next: "s118" },
  { id: "s118", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock dùng tay ngăn cậu Mainson lại, lắc đầu.", next: "s119" },
  { id: "s119", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Hắn biết không thể chỉ hỏi một phía, liền thúc Mainson ra hiệu, cậu nhanh chóng hiểu ý mà tiếp lời.", next: "s120" },
  { id: "s120", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thám tử Heirlock, bên ngoài cũng gọi chúng ta rồi. Anh Kael, buổi sau nói chuyện rõ hơn nhé, cảm ơn vì đã hợp tác.", next: "s121" },
  { id: "s121", bg: "santruong.jpg", next: "s122" },
  { id: "s122", bg: "santruong.jpg", char: "Narrator", text: "Gió thổi nhẹ qua hành lang, tiếng bước chân vang lên đều đặn.", next: "s123" },
  { id: "s123", bg: "santruong.jpg", char: "Mainson.png", text: "Kael, anh ta không muốn hợp tác với chúng ta, phải làm sao đây?", next: "s124" },
  { id: "s124", bg: "santruong.jpg", char: "Heirlock.png", text: "Chỉ còn cách hỏi thăm những người khác trong trường thôi.", next: "s125" },
  { id: "s125", bg: "santruong.jpg", char: "Narrator", text: "Cả hai nhìn thấy một người phụ nữ mặc thường phục của giáo viên liền nhanh chóng tiến lại.", next: "s126" },
  { id: "s126", bg: "santruong.jpg", char: "Mainson.png", text: "Xin chào!", next: "s127" },
  { id: "s127", bg: "santruong.jpg", char: "Giáo viên.png", text: "Vâng, hai người là…", next: "s128" },
  { id: "s128", bg: "santruong.jpg", char: "Mainson.png", text: "Chúng tôi là cảnh sát điều tra vụ việc cái chết của thầy Axton tại nhà riêng, chúng tôi có thể hỏi cô vài điều về mối quan hệ giữa thầy Axton và thầy Kael được chứ?", next: "s129" },
  { id: "s129", bg: "santruong.jpg", char: "Narrator", text: "Người giáo viên chần chừ một hồi rồi cũng miễn cưỡng gật đầu, quay người trở lại trường.", next: "s130" },
  { id: "s130", bg: "santruong.jpg", char: "Giáo viên.png", text: "Được thôi, vậy chúng ta tới phòng giáo viên của tôi để nói chuyện nhé, đứng ngay sân trường như thế này không tiện cho lắm.", next: "s131" },
  { id: "s131", bg: "santruong.jpg", char: "Narrator", text: "Heirlock gật đầu, ba người bước tới trước phòng dành cho giáo viên.", next: "s132" },
  { id: "s132", bg: "phonggiaovien.jpg", next: "s133" },
  { id: "s133", bg: "phonggiaovien.jpg", char: "Giáo viên.png", text: "Anh cảnh sát đợi tôi chút nhé, để tôi rót nước mời hai người… ừm?", next: "s134" },
  { id: "s134", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Là Mainson, tôi là cảnh sát thuộc đội điều tra vụ án còn bên cạnh là thám tử tư Heirlock được ủy quyền hỗ trợ cho vụ án lần này.", next: "s135" },
  { id: "s135", bg: "phonggiaovien.jpg", char: "Giáo viên.png", text: "Được rồi, vậy anh Heirlock và cảnh sát Mainson đây muốn uống trà hay cafe?", next: "s136" },
  { id: "s136", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Cho chúng tôi nước lọc là ổn rồi, dù sao cũng chỉ là một vài câu hỏi ngắn gọn thôi, sẽ không tốn nhiều thời gian của cô đâu.", next: "s137" },
  { id: "s137", bg: "phonggiaovien.jpg", char: "Narrator", text: "Người giáo viên gật đầu rồi ngồi xuống chiếc ghế sofa đối diện với Heirlock.", next: "s138" },
  { id: "s138", bg: "phonggiaovien.jpg", char: "Giáo viên.png", text: "Tôi là Jessica, cứ gọi vậy là được.", next: "s139" },
  { id: "s139", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Được rồi, vậy cho phép chúng tôi hỏi thẳng, mối quan hệ của hai thầy Axton và thầy Kael như thế nào?", next: "s140" },
  { id: "s140", bg: "phonggiaovien.jpg", char: "Narrator", text: "Jessica có chút ngạc nhiên, như phát hiện được điều gì đó liền nhướng mày nói nhỏ.", next: "s141" },
  { id: "s141", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Ý anh thám tử Kael là nghi phạm giết thầy Axton sao?", next: "s142" },
  { id: "s142", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Không không, tôi chỉ điều tra những mối quan hệ xung quanh nạn nhân mà thôi, chưa có bất cứ bằng chứng nào để nghi ngờ hung thủ là bất cứ ai cả, kể cả thầy Kael.", next: "s143" },
  { id: "s143", bg: "phonggiaovien.jpg", char: "Narrator", text: "Jessica âm trầm một lúc như đang suy nghĩ gì đó rồi đáp lời.", next: "s144" },
  { id: "s144", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Tôi nghĩ ai cũng thấy rõ, thầy Kael ghét thầy Axton.", next: "s145" },
  { id: "s145", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Ghét ở mức độ nào?", next: "s146" },
  { id: "s146", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Kiểu như đố kỵ. Lúc họp hội đồng, thầy Kael hay châm chọc Axton, luôn nói móc rằng thầy Axton mua chuộc ban cán bộ mới lên được chức đó. Axton thì hiền, dạy tốt, hay được học sinh quý.", next: "s147" },
  { id: "s147", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Thầy Axton mất đi như vậy thực sự là một nỗi tiếc nuối lớn cho cả trường chúng tôi.", next: "s148" },
  { id: "s148", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Hai người đó có bao giờ nói chuyện thân thiết với học sinh nào không?", next: "s149" },
  { id: "s149", bg: "phonggiaovien.jpg", char: "Narrator", text: "Jessica im lặng.", next: "s150" },
  { id: "s150", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Hay kiểu như… tin đồn tình cảm giữa thầy Axton với học sinh nữ nào đó?", next: "s151" },
  { id: "s151", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Là Yanna. Học sinh cưng của thầy Kael, cũng thân với Axton.", next: "s152" },
  { id: "s152", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Yanna đó cũng học lớp thầy Hải đúng không?", next: "s153" },
  { id: "s153", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Đúng. Cô bé học giỏi, ngoan, xinh. Ai cũng nói Axton thương như em gái nhưng có vẻ thầy Kael không nghĩ vậy.", next: "s154" },
  { id: "s154", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Ý cô là?", next: "s155" },
  { id: "s155", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Là thầy Kael ghen. Có tình cảm với Yanna. Tôi từng thấy thầy ấy dung túng cho cô bé nhiều lần trong tiết học, hay tìm cách được gần gũi với Yanna dù con bé có vẻ không thoải mái lắm.", next: "s156" },
  { id: "s156", bg: "phonggiaovien.jpg", char: "Jessica.png", text: "Cũng có lần tôi thấy hai thầy cãi nhau to, tôi cũng đoán là về Yanna.", next: "s157" },
  { id: "s157", bg: "phonggiaovien.jpg", char: "Narrator", text: "Heirlock liếc sang Mainson, cả hai cùng hiểu ý.", next: "s158" },
  { id: "s158", bg: "phonggiaovien.jpg", char: "Narrator", text: "Họ quyết định rút lui. Tuy nhiên đêm đó Mainson vẫn để mắt tới hành tung của thầy Kael.", next: "s159" },
  { id: "s159", bg: "phongsinhhoc.jpg", next: "s160" },
  { id: "s160", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Sáng hôm sau, Kael đang sắp xếp dụng cụ thì thấy hai người bước vào. Gương mặt ông thoáng cứng lại.", next: "s161" },
  { id: "s161", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Chào thầy Kael, có lẽ hôm nay thầy đã rảnh rỗi hơn rồi phải chứ, vậy chúng ta nói chuyện tiếp nhé, đây là một phần cho việc điều tra, tôi mong thầy có thể hợp tác.", next: "s162" },
  { id: "s162", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Chúng tôi muốn hỏi thêm về việc thầy Axton từng hẹn gặp riêng thầy, tôi có thể hỏi lý do là gì không?", next: "s163" },
  { id: "s163", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Gương mặt gã thoáng khó chịu.", next: "s164" },
  { id: "s164", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi đã nói rồi, tôi không biết.", next: "s165" },
  { id: "s165", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Thầy có từng cãi nhau với Axton, đúng chứ?", next: "s166" },
  { id: "s166", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Ai nói vậy?", next: "s167" },
  { id: "s167", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Nhiều người trong trường. Họ nói hai người bất hòa từ lâu.", next: "s168" },
  { id: "s168", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Nếu hai người tin mấy lời đồn đó thì đi mà hỏi họ.", next: "s169" },
  { id: "s169", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Gã tỏ rõ sự bực dọc nhưng lại che giấu, tiếp tục với ống nghiệm.", next: "s170" },
  { id: "s170", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Thầy có vẻ rất ghét thầy Axton.", next: "s171" },
  { id: "s171", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael nhíu mày, ống nghiệm trên tay gã hơi rung nhẹ làm thứ chất lỏng đặc quánh trong đó khẽ sóng sánh.", next: "s172" },
  { id: "s172", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Còn không phải vì hắn ta là kẻ đáng ghét sao.", next: "s173" },
  { id: "s173", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Thầy nghĩ Axton giả tạo?", next: "s174" },
  { id: "s174", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Không giả tạo. Nhưng không thật lòng như mọi người tưởng. Hắn ta thân thiết với học sinh nữ quá mức.", next: "s175" },
  { id: "s175", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Ý anh là Yanna đúng chứ?", next: "s176" },
  { id: "s176", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael mở to mắt đầy kinh ngạc.", next: "s177" },
  { id: "s177", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Sao anh biết về cái tên này?", next: "s178" },
  { id: "s178", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Tôi chỉ hỏi một vài người trong trường mà thôi, mối quan hệ của anh và thầy Axton có vẻ căng thẳng hơn là một cuộc xích mích thông thường nhỉ?", next: "s179" },
  { id: "s179", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Gã thoáng ngạc nhiên nhưng rồi cười nhẹ.", next: "s180" },
  { id: "s180", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Có chuyện gì sao?", next: "s181" },
  { id: "s181", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi chỉ không muốn cô bé bị kéo vào mấy mối quan hệ mập mờ. Anh ta mời con bé đi ăn, đưa đón hàng ngày. Như thế có hợp lý đối với một giáo viên bình thường không?", next: "s182" },
  { id: "s182", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson nhíu mày, nghe cách nói chuyện của Kael, cậu có thể đoán được đây là một mối tình tay ba phức tạp.", next: "s183" },
  { id: "s183", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thầy có bằng chứng không?", next: "s184" },
  { id: "s184", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Cô bé từng kể với tôi. Nói là thầy Axton hay giúp đỡ, mua sách, dạy thêm không công. Ai mà không hiểu ý đồ thật của tên đó chứ.", next: "s185" },
  { id: "s185", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Hay là thầy quan tâm Yanna quá mức nên mới để ý từng chuyện như vậy?", next: "s186" },
  { id: "s186", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Gã siết chặt tay, ánh mắt giận dữ.", next: "s187" },
  { id: "s187", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi chỉ muốn cô ấy yên ổn học hành. Nếu hắn ta không xen vào, mọi chuyện đã khác!", next: "s188" },
  { id: "s188", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock im lặng nhìn đối phương, ánh mắt không rời khuôn mặt run run vì tức.", next: "s189" },
  { id: "s189", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thầy Kael, hôm Axton chết, thầy có ở nhà suốt chứ?", next: "s190" },
  { id: "s190", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi có việc ra ngoài. Nhưng không liên quan gì tới cái chết đó.", next: "s191" },
  { id: "s191", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Không khí chùng xuống. Tiếng kim đồng hồ kêu đều. Heirlock gật đầu với Mainson, ánh mắt đăm chiêu.", next: "s192" },
  { id: "s192", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Có ai làm chứng cho việc thầy không có mặt tại hiện trường không? Và 'chút việc' đó là gì, chúng tôi có thể biết được chứ?", next: "s193" },
  { id: "s193", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael im lặng một lúc, ánh mắt thoáng lo lắng.", next: "s194" },
  { id: "s194", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi ra ngoài, chỉ đi dạo một chút sau giờ học, không liên quan gì tới vụ việc.", next: "s195" },
  { id: "s195", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Đi dạo sao, vào khoảng thời gian nào?", next: "s196" },
  { id: "s196", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Từ chiều sau khi tan lớp tới tối. Làm sao tôi đoán trước được Axton sẽ chết để mà tìm nhân chứng trong lúc đi dạo chứ?", next: "s197" },
  { id: "s197", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock gật đầu lắng nghe trong khi bên cạnh Mainson đang chăm chú ghi chép lời khai của nhân chứng vào sổ tay.", next: "s198" },
  { id: "s198", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Vậy không có ai làm chứng cho anh, được rồi. Chúng tôi sẽ xác minh, cảm ơn thầy Kael đã hợp tác.", next: "s199" },
  { id: "s199", bg: "santruong.jpg", next: "s200" },
  { id: "s200", bg: "santruong.jpg", char: "Narrator", text: "Hai người bước ra khỏi lớp học, Mainson lên tiếng.", next: "s201" },
  { id: "s201", bg: "santruong.jpg", char: "Mainson.png", text: "Không lấy được thêm thông tin gì ở Kael rồi, có lẽ chúng ta phải hỏi tới cô bé Yanna trong câu chuyện này thôi.", next: "s202" },
  { id: "s202", bg: "santruong.jpg", char: "Narrator", text: "Heirlock gật đầu, hai người bước tới lớp học của Yanna.", next: "s203" },
  { id: "s203", bg: "lophochochoc.jpg", next: "s204" },
  { id: "s204", bg: "lophochochoc.jpg", char: "Narrator", text: "Phòng học lớp 12 yên ắng, chỉ còn vài học sinh chuẩn bị đồ. Yanna ngồi ngay ngắn, tập trung vào sách vở khi Heirlock và Mainson bước vào.", next: "s205" },
  { id: "s205", bg: "lophochochoc.jpg", char: "Heirlock.png", text: "Chào Yanna. Chúng tôi muốn hỏi một số chuyện liên quan tới thầy Kael và thầy Axton.", next: "s206" },
  { id: "s206", bg: "lophochochoc.jpg", char: "Narrator", text: "Yanna ngẩng đầu, ánh mắt ngoan ngoãn cùng giọng nhỏ nhẹ, cất tiếng đáp lời.", next: "s207" },
  { id: "s207", bg: "lophochochoc.jpg", char: "Yanna.png", text: "Vâng, em có thể trả lời được. Nhưng anh cảnh sát có thể đợi thêm chút nữa được chứ, em không muốn bị bạn bè hiểu nhầm.", next: "s208" },
  { id: "s208", bg: "phonggiaovien.jpg", next: "s209" },
  { id: "s209", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mainson và Heirlock tới phòng giáo viên trước, cậu lên tiếng trước.", next: "s210" },
  { id: "s210", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Heirlock, anh có thấy con bé đó, tên gì nhỉ?", next: "s211" },
  { id: "s211", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Yanna.", next: "s212" },
  { id: "s212", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Phải rồi, là Yanna, con bé này ngoan thật, một câu vâng hai câu dạ, lúc nào cũng tươi cười với mọi người xung quanh. Nếu tôi là thầy Axton hay Kael có lẽ cũng sẽ vô thức mà thiên vị con bé hơn một chút thôi.", next: "s213" },
  { id: "s213", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mainson nói không ngừng nghỉ khiến Heirlock mệt mỏi bóp lấy thái dương của mình, tặc lưỡi đầy lười biếng.", next: "s214" },
  { id: "s214", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Cậu im lặng giúp tôi chút đi.", next: "s215" },
  { id: "s215", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Hơn nữa, cậu thực sự nghĩ con bé đó đơn giản à?", next: "s216" },
  { id: "s216", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Anh không thấy vậy sao, Yanna nó như một mặt trời nhỏ vậy, người đơn thuần như thế sao có thể có vấn đề gì được chứ.", next: "s217" },
  { id: "s217", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Mainson, nghe này.", next: "s218" },
  { id: "s218", bg: "phonggiaovien.jpg", char: "Narrator", text: "Heirlock đặt một tay lên vai cậu khiến Mainson im lặng, khó hiểu.", next: "s219" },
  { id: "s219", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Vâng?", next: "s220" },
  { id: "s220", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Cậu không xứng làm một cảnh sát, anh chàng tin người của tôi à.", next: "s221" },
  { id: "s221", bg: "phonggiaovien.jpg", char: "Narrator", text: "Trong lúc Mainson còn đang không hiểu chuyện gì thì đã vang lên tiếng gõ cửa.", next: "s222" },
  { id: "s222", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Vào đi!", next: "s223" },
  { id: "s223", bg: "phonggiaovien.jpg", char: "Narrator", text: "Yanna bước vào, mái tóc ngắn khẽ lung lay dưới làn gió mát khiến Mainson không thể rời mắt.", next: "s224" },
  { id: "s224", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Em tới rồi ạ.", next: "s225" },
  { id: "s225", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Ừm, em ngồi đi, để anh lấy nước cho em nhé.", next: "s226" },
  { id: "s226", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mainson nhanh nhảu muốn đứng dậy nhưng đã bị Heirlock ngăn lại.", next: "s227" },
  { id: "s227", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Mainson, đây không phải sở cảnh sát của cậu đâu.", next: "s228" },
  { id: "s228", bg: "phonggiaovien.jpg", char: "Narrator", text: "Cậu không ngu ngốc tới mức chẳng nhận ra đây là một lời nhắc nhở rằng bản thân đã làm những thứ vượt quá sự cho phép ở nơi này liền ngoan ngoãn ngồi về chỗ cũ, bên cạnh vị thám tử tư.", next: "s229" },
  { id: "s229", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Em có thường gặp thầy Kael sau giờ học không?", next: "s230" },
  { id: "s230", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Dạ, thầy ấy nhiều lần nhờ em làm việc riêng, cũng hay gọi ra ngoài để nói chuyện. Em cũng không thích nhưng không biết từ chối thế nào.", next: "s231" },
  { id: "s231", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Em cảm thấy phiền chứ?", next: "s232" },
  { id: "s232", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Dạ có. Nhưng em sợ nếu từ chối, thầy sẽ khó chịu, nên em chỉ cố làm cho xong.", next: "s233" },
  { id: "s233", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Còn thầy Axton thì sao?", next: "s234" },
  { id: "s234", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Thầy Axton chỉ giải đáp bài vở trong lớp thôi. Chúng em ít khi gặp riêng ngoài giờ học, có một lần em lỡ chuyến xe bus về nhà, thầy có nói vì thầy tiện đường nên ngỏ ý chở em về, đó là lần duy nhất. Em tôn trọng thầy lắm, nhưng không quá thân thiết.", next: "s235" },
  { id: "s235", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Có bạn nào khác biết thầy Kael làm phiền em không?", next: "s236" },
  { id: "s236", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Dạ, một vài bạn có biết nhưng em không muốn mọi người can thiệp, em không muốn dính vào rắc rối. Dù sao thấy Kael dù làm phiền nhưng cũng chưa làm ra hành động gì quá đáng. Em chỉ muốn tập trung học.", next: "s237" },
  { id: "s237", bg: "phonggiaovien.jpg", char: "Narrator", text: "Yanna kể chuyện một cách ngoan ngoãn, ánh mắt sáng lên khi nhắc về Axton và khẽ run khi nói tới Kael.", next: "s238" },
  { id: "s238", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Được rồi, tôi có thể hỏi một điều cuối không?", next: "s239" },
  { id: "s239", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Vâng, anh thám tử cứ hỏi ạ, em sẽ trả lời trong khả năng cho phép.", next: "s240" },
  { id: "s240", bg: "phonggiaovien.jpg", char: "Narrator", text: "Heirlock lật cuốn sổ tay của Mainson trên bàn, tới trang lời khai của Kael thì ngưng lại.", next: "s241" },
  { id: "s241", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Vào thời gian sau khi tan học ngày Axton xảy ra biến cố, em đang ở đâu?", next: "s242" },
  { id: "s242", bg: "phonggiaovien.jpg", char: "Narrator", text: "Yanna bất ngờ, em run lên khi nhận ra lí do của câu hỏi này nhưng vẫn cố gắng giữ sự bình tĩnh.", next: "s243" },
  { id: "s243", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Hôm đó thầy Kael đã nằng nặc đòi đưa em về nhà, em và thầy có nói qua lại một lúc lâu ở khu đợi bus, vào lúc đấy em đang ở cùng thầy Kael.", next: "s244" },
  { id: "s244", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Cái tên Kael này, hắn làm phiền cả học sinh của chính mình nữa.", next: "s245" },
  { id: "s245", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Mainson, để ý ngôn từ.", next: "s246" },
  { id: "s246", bg: "phonggiaovien.jpg", char: "Narrator", text: "Heirlock nói rồi nhìn vào cuốn sổ tay trên bàn mà gật gù, vậy là thời điểm Axton chết, Kael nói mình có việc đi dạo nhưng thực chất là tìm cách dây dưa với học sinh, còn Yanna chỉ đơn giản là từ chối.", next: "s247" },
  { id: "s247", bg: "phonggiaovien.jpg", char: "Narrator", text: "Muốn xác nhận lại điều này không quá khó, chỉ cần hỏi mấy người ở gần trạm xe bus là rõ, dù sao Yanna cũng thường xuyên về bằng chuyến này mỗi ngày, những người đi làm, đi học tại chuyến bus đó có lẽ cũng nghe được phần nào câu chuyện.", next: "s248" },
  { id: "s248", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Được rồi, cuộc nói chuyện tới đây thôi, cảm ơn em đã hợp tác.", next: "s249" },
  { id: "s249", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Vâng, vậy em xin phép về trước.", next: "s250" },
  { id: "s250", bg: "phonggiaovien.jpg", char: "Narrator", text: "Sau khi trò chuyện xong, Yanna cúi đầu chào, ngoan ngoãn rời khỏi phòng giáo viên. Heirlock và Mainson cũng theo đó mà ra ngoài, cậu nhanh chóng kéo lấy Heirlock.", next: "s251" },
  { id: "s251", bg: "santruong.jpg", next: "s252" },
  { id: "s252", bg: "santruong.jpg", char: "Mainson.png", text: "Thám tử, tên Kael này đúng là một kẻ khốn nạn.", next: "s253" },
  { id: "s253", bg: "santruong.jpg", char: "Heirlock.png", text: "Được rồi, nếu cậu muốn lấy lại công bằng cho Yanna thế thì đi tới trạm bus hỏi mọi người về việc có thấy cuộc cãi vã nào giữa con bé và Kael chiều ngày hôm đó không đi.", next: "s254" },
  { id: "s254", bg: "santruong.jpg", char: "Mainson.png", text: "Ơ, không phải chúng ta hoạt động cùng nhau sao?", next: "s255" },
  { id: "s255", bg: "santruong.jpg", char: "Narrator", text: "Heirlock liếc mắt, lười biếng phẩy tay.", next: "s256" },
  { id: "s256", bg: "santruong.jpg", char: "Heirlock.png", text: "Đem theo cậu suốt như vậy sợ rằng cậu sẽ nói hết tiến trình vụ án ra mất.", next: "s257" },
  { id: "s257", bg: "santruong.jpg", char: "Narrator", text: "Mainson xụ mặt nhưng vẫn phải làm theo lời hắn, chậm rãi lết thân mình đi về phía bến xe bus duy nhất ở gần trường trong khi Heirlock trở về cục cảnh sát. Hắn cần phải nghe kết quả khám nghiệm chi tiết.", next: "s258" },
  { id: "s258", bg: "cuccanhsat.jpg", next: "s259" },
  { id: "s259", bg: "cuccanhsat.jpg", char: "Narrator", text: "Ngay khi vừa trở về cục cảnh sát, một tiếng động ồn ào quen thuộc từ phía bên kia hành lang đã vang lên khiến Heirlock ngay lập tức nhận ra âm thanh từ phòng pháp y.", next: "s260" },
  { id: "s260", bg: "cuccanhsat.jpg", char: "Narrator", text: "Cửa phòng pháp y nằm cuối hành lang, ánh đèn trắng mờ hắt ra. Mùi thuốc sát trùng nồng nặc. Heirlock đẩy cửa, tiếng bản lề kẽo kẹt vang lên.", next: "s261" },
  { id: "s261", bg: "phongphapy.jpg", next: "s262" },
  { id: "s262", bg: "phongphapy.jpg", char: "Narrator", text: "Pháp y ngẩng lên, tháo găng tay, khẽ gật đầu chào.", next: "s263" },
  { id: "s263", bg: "phongphapy.jpg", char: "Kam.png", text: "À, thám tử Heirlock, anh đến đúng lúc đấy. Kết quả vừa có xong.", next: "s264" },
  { id: "s264", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Kam à, trông cậu như đã thức trắng mấy đêm rồi vậy.", next: "s265" },
  { id: "s265", bg: "phongphapy.jpg", char: "Kam.png", text: "Thám tử của tôi đoán hay lắm, chính xác, tôi đã thức hơn 48 tiếng rồi đây, và giờ thì mau lại đây, nghe kĩ từng thứ và viết toàn bộ chúng vào cái cuốn sổ tay của anh đi, đừng bắt tôi phải nói thêm bất cứ lần nào.", next: "s266" },
  { id: "s266", bg: "phongphapy.jpg", char: "Narrator", text: "Anh chàng pháp y trẻ tuổi với đôi mắt thâm đen tựa gấu trúc sau một thời gian dài bận rộn mệt mỏi ngồi phịch xuống chiếc ghế gỗ trước mặt, cảm tưởng như chỉ cần thêm một giây im lặng nữa thôi anh ta có thể ngủ gật ngay tại đây, trước cái xác đã khô.", next: "s267" },
  { id: "s267", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Tôi muốn nghe toàn bộ chi tiết, từng phần một.", next: "s268" },
  { id: "s268", bg: "phongphapy.jpg", char: "Narrator", text: "Kam chỉ vào tấm khăn trắng phủ thi thể, kéo ra một góc. Vết siết tím bầm lộ rõ dưới ánh đèn lạnh.", next: "s269" },
  { id: "s269", bg: "phongphapy.jpg", char: "Kam.png", text: "Nguyên nhân tử vong là do siết cổ. Hướng lực lệch phải, hung thủ thuận tay phải.", next: "s270" },
  { id: "s270", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Có dấu hiệu vật lộn không?", next: "s271" },
  { id: "s271", bg: "phongphapy.jpg", char: "Kam.png", text: "Không. Tay, móng, da đều sạch. Có vẻ nạn nhân không kịp phản ứng.", next: "s272" },
  { id: "s272", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Còn điều gì bất thường?", next: "s273" },
  { id: "s273", bg: "phongphapy.jpg", char: "Kam.png", text: "Có, là vết thương ở vùng kín.", next: "s274" },
  { id: "s274", bg: "phongphapy.jpg", char: "Kam.png", text: "Nạn nhân bị chém nhiều nhát vào bộ phận sinh dục. Vết cắt rất đều tay, độ sâu gần như y hệt nhau, dương vật hoàn toàn rời ra khỏi cơ thể.", next: "s275" },
  { id: "s275", bg: "phongphapy.jpg", char: "Kam.png", text: "Bộ phận sinh dục bị tổn thương nặng, một số vết hằn sâu nhưng rất chuẩn xác. Có thể hung thủ có chuyên môn nên vết cắt mới ngọt được như vậy.", next: "s276" },
  { id: "s276", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock gật đầu, nhanh chóng ghi vào sổ tay những điều quan trọng.", next: "s277" },
  { id: "s277", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Vậy còn vết siết cổ?", next: "s278" },
  { id: "s278", bg: "phongphapy.jpg", char: "Kam.png", text: "Vết siết cổ hằn sâu trên da, có nhiều vết chồng lên nhau.", next: "s279" },
  { id: "s279", bg: "phongphapy.jpg", char: "Kam.png", text: "Hướng lực lệch sang bên phải, chứng tỏ hung thủ thuận tay phải nhưng không có tính toán từ trước.", next: "s280" },
  { id: "s280", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Một hung thủ không có kinh nghiệm, giết người vì thù oán cá nhân à.", next: "s281" },
  { id: "s281", bg: "phongphapy.jpg", char: "Kam.png", text: "Nạn nhân có thể có vóc dáng nhỏ con nên vết siết hằn sâu hơn mức bình thường và chồng lên nhau do nạn nhân chống cự.", next: "s282" },
  { id: "s282", bg: "phongphapy.jpg", char: "Kam.png", text: "Trong phổi vẫn còn khí, chứng tỏ bị siết khi còn sống, không phải sau khi ngạt, tức là nguyên nhân cái chết không phải là do mất máu, dù mất nhiều máu nhưng nạn nhân vẫn còn sống, thời điểm chính thức rời đi là khi bị siết cổ.", next: "s283" },
  { id: "s283", bg: "phongphapy.jpg", char: "Narrator", text: "Nói rồi Kam cũng nhìn về phía cuốn sổ tay của người trước mặt, tiếp lời.", next: "s284" },
  { id: "s284", bg: "phongphapy.jpg", char: "Kam.png", text: "Thời gian tử vong ước tính khoảng 20h15 tới 20h30 ngày 4/8/2025.", next: "s285" },
  { id: "s285", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock cau mày, khẽ chạm tay lên mép bàn inox, trầm ngâm vài giây.", next: "s286" },
  { id: "s286", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Vậy tại sao cửa lại khóa từ trong?", next: "s287" },
  { id: "s287", bg: "phongphapy.jpg", char: "Kam.png", text: "Đó mới là điểm lạ. Mọi người đã xem qua ổ khóa, không có dấu cạy, không có vết va chạm. Có thể ai đó đã dùng chìa dự phòng.", next: "s288" },
  { id: "s288", bg: "phongphapy.jpg", char: "Narrator", text: "Căn phòng lại chìm vào im lặng, chỉ còn tiếng kim loại chạm nhẹ vào khay đựng dụng cụ. Heirlock khép sổ, ánh mắt nặng trĩu suy tính.", next: "s289" },
  { id: "s289", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Cảm ơn, tôi sẽ quay lại khi có thêm dữ liệu.", next: "s290" },
  { id: "s290", bg: "phongphapy.jpg", char: "Kam.png", text: "Ừ, nhớ đem cà phê lần sau nhé, Heirlock. Tôi thức với anh cả đêm rồi đấy.", next: "s291" },
  { id: "s291", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock khẽ nhếch môi, chẳng đáp mà chỉ xoay người rời khỏi phòng, bóng hắn dần khuất sau cánh cửa, chỉ còn lại tay pháp y khẽ mỉm cười.", next: "s292" },
  { id: "s292", bg: "vanphong.jpg", next: "s293" },
  { id: "s293", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock trở lại văn phòng, đẩy cửa gỗ nặng nề, tiếng bản lề khẽ kẽo kẹt vang lên.", next: "s294" },
  { id: "s294", bg: "vanphong.jpg", char: "Narrator", text: "Không gian yên tĩnh, ánh sáng đèn bàn vàng nhạt rọi lên sổ tay, giấy tờ và vài bản đồ treo trên tường.", next: "s295" },
  { id: "s295", bg: "vanphong.jpg", char: "Narrator", text: "Hắn ngồi xuống ghế da, đặt cuốn sổ tay lên bàn, nhìn chăm chú vào các ghi chú vừa có được sau khi nói chuyện với bên pháp y.", next: "s296" },
  { id: "s296", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock thở dài, ngón tay gõ nhẹ lên bàn, suy nghĩ về những mảnh thông tin vừa thu thập được.", next: "s297" },
  { id: "s297", bg: "vanphong.jpg", char: "Narrator", text: "Một lúc sau, tiếng cửa mở khẽ vang lên, Mainson bước vào, vẻ mặt đã thấm chút mệt mỏi.", next: "s298" },
  { id: "s298", bg: "vanphong.jpg", char: "Mainson.png", text: "Thám tử, tôi vừa điều tra xong về việc của Yanna và Kael từ bến xe bus. Ở đó, đúng là có thấy Kael và Yanna cãi nhau nhưng chỉ khoảng 15 phút thôi, không lâu lắm.", next: "s299" },
  { id: "s299", bg: "vanphong.jpg", char: "Mainson.png", text: "Xung quanh mọi người cũng có chứng kiến vài câu qua lại, nhưng không có gì căng thẳng quá, Kael cố gắng muốn con bé lên xe mình nhưng Yanna từ chối thẳng thừng và tự lên xe bus còn Kael thì bỏ về.", next: "s300" },
  { id: "s300", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock nhướng mày, đặt tay lên cuốn sổ, ánh mắt tập trung vào những dòng chữ viết trên đó.", next: "s301" },
  { id: "s301", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy à, còn tôi thì vừa từ phòng pháp y về, thời gian tử vong khoảng 8hqt tới 8h30 tối.", next: "s302" },
  { id: "s302", bg: "vanphong.jpg", char: "Heirlock.png", text: "Cậu nói cãi nhau ở bến bus vào khoảng 6h hơn, đúng không?", next: "s303" },
  { id: "s303", bg: "vanphong.jpg", char: "Mainson.png", text: "Đúng, khoảng 18h10 tới 18h25 là thời điểm cãi nhau.", next: "s304" },
  { id: "s304", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock gật đầu, ánh mắt đăm chiêu.", next: "s305" },
  { id: "s305", bg: "vanphong.jpg", char: "Heirlock.png", text: "Như vậy Kael vẫn còn đủ thời gian để rời khỏi bến, di chuyển và sát hại nạn nhân trước 20h15.", next: "s306" },
  { id: "s306", bg: "vanphong.jpg", char: "Heirlock.png", text: "Thời gian giữa hai sự kiện kéo dài hơn một tiếng rưỡi, Kael hoàn toàn có khả năng thực hiện hành động đó mà không ai nhìn thấy.", next: "s307" },
  { id: "s307", bg: "vanphong.jpg", char: "Narrator", text: "Mainson đứng im, nhìn Heirlock ghi chú trong sổ tay. Hắn cẩn thận đối chiếu thời gian, ánh sáng từ đèn bàn hắt lên nét mặt tập trung.", next: "s308" },
  { id: "s308", bg: "vanphong.jpg", char: "Heirlock.png", text: "Điều này trùng khớp với phân tích pháp y, nạn nhân chết khoảng 20h15 tới 20h30, vết siết cổ còn mới, khí vẫn trong phổi, mất máu nhiều nhưng không phải nguyên nhân chính.", next: "s309" },
  { id: "s309", bg: "vanphong.jpg", char: "Mainson.png", text: "Vậy Kael thực sự có cơ hội để làm chuyện đó sau khi rời khỏi bến xe bus?", next: "s310" },
  { id: "s310", bg: "vanphong.jpg", char: "Heirlock.png", text: "Đúng. Mọi thứ đều phù hợp với khả năng rằng Kael là hung thủ.", next: "s311" },
  { id: "s311", bg: "vanphong.jpg", char: "Heirlock.png", text: "Thời gian giữa hai sự kiện đủ dài, Yanna chỉ là nhân chứng gián tiếp, không thể xác nhận trực tiếp hành vi sát hại vậy nên chúng ta vẫn chưa đủ bằng chứng.", next: "s312" },
  { id: "s312", bg: "vanphong.jpg", char: "Narrator", text: "Mainson khẽ thở dài, cảm giác nặng nề lan ra khắp phòng. Cậu ngồi xuống ghế, nhìn Heirlock với ánh mắt như chờ đợi câu trả lời.", next: "s313" },
  { id: "s313", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock ngồi im một lúc, nhìn ra cửa sổ văn phòng, ánh đèn đường bên ngoài hắt vào, tạo những dải sáng mờ trên sàn.", next: "s314" },
  { id: "s314", bg: "vanphong.jpg", char: "Narrator", text: "Mọi mảnh thông tin đang xếp chồng lên nhau, thời gian, hành vi, nhân chứng và chứng cứ pháp y, tất cả đều tạo nên bức tranh khả nghi, mà trung tâm vẫn là Kael.", next: "s315" },
  { id: "s315", bg: "vanphong.jpg", next: "s316" },
  { id: "s316", bg: "vanphong.jpg", char: "Narrator", text: "Tiếng đồng hồ trên tường tích tắc đều đều, mưa lất phất rơi ngoài cửa sổ.", next: "s317" },
  { id: "s317", bg: "vanphong.jpg", char: "Heirlock.png", text: "Heirlock ngồi vắt chân lên bàn, nhấm nháp tách cà phê còn nóng, ánh mắt lướt qua quyển sổ tay ghi chú vụ án. Hắn bấm một dãy số trên điện thoại rồi đưa lên.", next: "s318" },
  { id: "s318", bg: "vanphong.jpg", char: "Heirlock.png", text: "Mainson, dậy đi. Chuẩn bị lên trường ngay. Chúng ta cần nói chuyện với Kael.", next: "s319" },
  { id: "s319", bg: "vanphong.jpg", char: "Narrator", text: "Tiếng điện thoại vang lên, Mainson cầm máy, giọng còn ngái ngủ.", next: "s320" },
  { id: "s320", bg: "vanphong.jpg", char: "Mainson.png", text: "Sớm vậy sao, mới có 5h mà. Thôi được rồi, tôi sẽ tới ngay.", next: "s321" },
  { id: "s321", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock mặc áo mưa, kéo dây kéo kín cổ, bước ra khỏi văn phòng, đôi giày bốt đen cắm vào vũng nước nhỏ, bùn văng tung tóe.", next: "s322" },
  { id: "s322", bg: "santruong.jpg", next: "s323" },
  { id: "s323", bg: "santruong.jpg", char: "Narrator", text: "Gió lạnh lướt qua mặt cùng làn mưa rơi lất phất, những hạt mưa nhỏ đọng trên mũ làm tóc hắn ướt, nặng trĩu.", next: "s324" },
  { id: "s324", bg: "santruong.jpg", char: "Narrator", text: "Heirlock đi trước, tay đút túi, ánh mắt nhìn đường, quan sát những vết bùn loang trên vỉa hè trong khi Mainson nhanh chóng chạy theo phía sau với chiếc ô trên tay, cố gắng che cho cả hai người.", next: "s325" },
  { id: "s325", bg: "santruong.jpg", char: "Narrator", text: "Bỗng nhiên một vết chân dính bùn in trên sàn gạch men thu hút sự chú ý của hắn.", next: "s326" },
  { id: "s326", bg: "santruong.jpg", char: "Heirlock.png", text: "Dấu giày này, hình như hơi quen quen…", next: "s327" },
  { id: "s327", bg: "santruong.jpg", char: "Mainson.png", text: "Thám tử?", next: "s328" },
  { id: "s328", bg: "santruong.jpg", char: "Narrator", text: "Heirlock chỉ vào dấu giày dính bùn đang in trên nền trắng kia mà lẩm bẩm.", next: "s329" },
  { id: "s329", bg: "santruong.jpg", char: "Heirlock.png", text: "Cậu có thấy nó giống với…", next: "s330" },
  { id: "s330", bg: "santruong.jpg", char: "Mainson.png", text: "Là dấu giày ở hiện trường.", next: "s331" },
  { id: "s331", bg: "santruong.jpg", char: "Narrator", text: "Mainson reo lên, tuy nhiên đây là dấu giày của một hãng giày da khá phổ biến, và cỡ 41 cũng là cỡ chân của nhiều nam giới nữa. Chỉ điều này vẫn chẳng nói lên thứ gì cả.", next: "s332" },
  { id: "s332", bg: "santruong.jpg", char: "Heirlock.png", text: "Giày của Kael…", next: "s333" },
  { id: "s333", bg: "santruong.jpg", char: "Narrator", text: "Hắn suy nghĩ một lúc rồi nhìn về phía cộng sự của mình.", next: "s334" },
  { id: "s334", bg: "santruong.jpg", char: "Heirlock.png", text: "Mainson, chút nữa để ý giúp tôi chi tiết này khi gặp Kael nhé.", next: "s335" },
  { id: "s335", bg: "santruong.jpg", char: "Mainson.png", text: "Được thôi, thưa ngài thám tử.", next: "s336" },
  { id: "s336", bg: "phongsinhhoc.jpg", next: "s337" },
  { id: "s337", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Xin chào thầy Kael, lại là chúng tôi đây.", next: "s338" },
  { id: "s338", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Lần này Kael không bực bội nữa mà chỉ lười biếng ngồi lên ghế, thong thả tiếp tục công việc lắc cái ống nghiệm nhỏ trên tay mình, hoàn toàn chẳng thèm để tâm tới hai người vừa bước vào.", next: "s339" },
  { id: "s339", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Vâng thưa ngài thám tử và anh cảnh sát, hôm nay hai người lại muốn nói chuyện gì nữa đây. Xin hãy kết thúc việc tra hỏi này sớm, tôi cần trở lại nhịp sống thường ngày để đạt hiệu quả tốt nhất trong các bài dạy của mình.", next: "s340" },
  { id: "s340", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Không để Kael phải chờ đợi lâu, Mainson trực tiếp lôi ra cuốn sổ tay, chất vấn.", next: "s341" },
  { id: "s341", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thưa thầy Kael, tôi đã hỏi mấy người ở bến bus hôm qua, họ nói thầy và học trò Yanna có gặp nhau vào khoảng 17h30 tới 18h tối, đây là cuộc đi dạo mà thầy đã nói hôm trước sao?", next: "s342" },
  { id: "s342", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael tỏ rõ vẻ ấp úng nhưng rồi nhanh chóng bật lại, thể hiện sự bực bội rõ rệt.", next: "s343" },
  { id: "s343", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Thì đúng là tôi có nói chuyện với Yanna, nhưng việc tôi nói chuyện cùng học sinh nữ thuộc lớp mình sau giờ học ở bến xe bus thì cũng đâu có vấn đề gì.", next: "s344" },
  { id: "s344", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Hơn nữa, việc tôi làm gì từ bao giờ lại bắt buộc phải nói ra cho mấy người vậy.", next: "s345" },
  { id: "s345", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock không bực bội, hắn đã quá quen với những nghi phạm luôn trở nên cáu gắt mỗi khi bị hỏi chuyện rồi.", next: "s346" },
  { id: "s346", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Được rồi, thầy Kael, chúng tôi mới chỉ đang hỏi chuyện thôi, còn chưa có gì mà, thầy đừng căng thẳng như thế, trừ khi…", next: "s347" },
  { id: "s347", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Nói tới đây hắn ngưng lại một chút khiến cho Kael tò mò.", next: "s348" },
  { id: "s348", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Trừ khi hung thủ chính là thầy, thầy Kael.", next: "s349" },
  { id: "s349", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Mấy người bị điên sao, chỉ vì mỗi việc tôi nói dối rằng đã đi dạo thay vì nói chuyện với cô học trò của mình mà mấy người cũng suy diễn được tới thế.", next: "s350" },
  { id: "s350", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael gằn giọng bực tức, mắt gã long lên sòng sọc tựa như có thể nắm cổ hai người họ và ném ra khỏi phòng bất cứ lúc nào vậy.", next: "s351" },
  { id: "s351", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Mẹ kiếp, đúng là cái thứ ăn trên đầu trên cổ dân rồi chỉ biết kết tội cho nhanh, cần gì đúng sai.", next: "s352" },
  { id: "s352", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Nghe tới đây khiến Mainson bực bội, cậu vừa định đứng lên đối chất thì đã bị Heirlock cản lại, lắc đầu ra hiệu.", next: "s353" },
  { id: "s353", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Đủ rồi thầy Kael, vậy là thầy đã nói chuyện to tiếng với học sinh Yanna tại bến xe bus lúc 17h30 tới tầm khoảng 18h tối, phải chứ?", next: "s354" },
  { id: "s354", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Khi này Kael mới dịu đi phần nào, gã vắt chéo chân ngồi trên chiếc ghế sofa giữa phòng sinh học.", next: "s355" },
  { id: "s355", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Phải, nhưng thế thì sao?", next: "s356" },
  { id: "s356", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Vậy từ 18h tới 20h thầy đã đi đâu?", next: "s357" },
  { id: "s357", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Tôi đi dạo, để khuây khoả sau khi bị Yanna từ chối, mấy cái này cũng phải hỏi kĩ đến thế à?", next: "s358" },
  { id: "s358", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock gật đầu, tay hắn cầm cuốn sổ, ánh mắt lạnh lùng.", next: "s359" },
  { id: "s359", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Phải rồi thầy Kael, không biết thầy có thể đứng lên và bước lên trước vài bước không?", next: "s360" },
  { id: "s360", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael nhăn mặt tỏ vẻ không hiểu chuyện gì, gã đứng lên rồi tiến lên, ngay lập tức cả Mainson và Heirlock đều nhận ra dấu giày quen thuộc.", next: "s361" },
  { id: "s361", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Xin phép thầy Kael chúng tôi sẽ nói thẳng, từ dấu giày, động cơ ghen ghét với Axton, vì không có được Yanna, cuộc cãi nhau trước bến xe bus và thời gian thầy không ở nhà khi Axton chết… tất cả đều hướng về duy nhất một điều.", next: "s362" },
  { id: "s362", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thầy Kael, thầy là hung thủ đã giết hại Axton.", next: "s363" },
  { id: "s363", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael gằn giọng, gã đứng bật dậy khỏi ghế, hằm hằm lao tới định túm lấy cổ áo cậu nhưng Mainson đã nhanh chóng lùi lại vài bước.", next: "s364" },
  { id: "s364", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Đề nghị thầy không manh động, những vết cắt trên thi thể nạn nhân quá chuyên nghiệp để có thể là kẻ nghiệp dư, và thầy biết chứ? Một giáo viên sinh học là người dễ dàng làm ra điều đó nhất.", next: "s365" },
  { id: "s365", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson tiến thêm vài bước.", next: "s366" },
  { id: "s366", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Hơn nữa, vết siết cổ trên thi thể lặp đi lặp lại nhiều lần, chứng tỏ nạn nhân đã giãy giụa rất kịch liệt khi bị siết, điều đó lại càng phù hợp với thầy, một người có vóc dáng nhỏ con hơn hẳn nạn nhân.", next: "s367" },
  { id: "s367", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock muốn ngăn lại bởi vì vẫn còn vấn đề căn phòng khóa nữa nhưng rồi hắn lại quyết định ngồi yên. Kiểu người như Mainson rất bốc đồng, và lí do duy nhất hắn dẫn theo cậu đi trong mọi vụ án chính là để sự bốc đồng này khiến hung thủ không thể kiềm chế cơn tức giận và lộ diện.", next: "s368" },
  { id: "s368", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Bây giờ chính là lúc đó, khi mà Kael bực mình, giọng gã cao dần, mặt đỏ lên.", next: "s369" },
  { id: "s369", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Chỉ có vậy thì không thể kết tội tôi được. Tôi không làm gì cả.", next: "s370" },
  { id: "s370", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Hắn đứng lên, tay vung mạnh, định đuổi hai người ra khỏi phòng.", next: "s371" },
  { id: "s371", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Trong lúc vung tay, một chiếc chìa khoá rơi xuống sàn, ánh kim loại lóe lên dưới ánh đèn phòng.", next: "s372" },
  { id: "s372", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Khoan đã!", next: "s373" },
  { id: "s373", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael đột nhiên trở lên hốt hoảng, vội vàng muốn cúi xuống nhặt nó lên nhưng đã bị Heirlock cản lại nhanh chóng.", next: "s374" },
  { id: "s374", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Hắn giữ chặt tay người thầy giáo sinh học này, vóc dáng nhỏ nhắn của Kael không thể đọ được với sức lực to lớn của Heirlock, chỉ có thể bất lực nhìn Mainson hành động.", next: "s375" },
  { id: "s375", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson cúi xuống, đeo găng tay, nhặt chìa khoá.", next: "s376" },
  { id: "s376", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thám tử, trông chìa khoá này rất quen… giống chìa khoá nhà Axton.", next: "s377" },
  { id: "s377", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Đây là chìa khoá phòng kho của tôi mà thôi.", next: "s378" },
  { id: "s378", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael cố gắng chen vào, muốn vươn tay ra cướp lấy chiếc chìa khóa nhưng lần nữa bị Heirlock giữ chặt.", next: "s379" },
  { id: "s379", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Tôi sẽ mang về kiểm tra. Nếu không phải, sẽ trả lại ngay.", next: "s380" },
  { id: "s380", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Không được! Không được tự ý lấy đồ của tôi.", next: "s381" },
  { id: "s381", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael gào lên, gã gầm lớn như muốn đe dọa đối phương, chỉ tiếc rằng trước mắt gã khi này lại là một thám tử tư lão luyện và một tay cảnh sát chẳng biết sợ là gì.", next: "s382" },
  { id: "s382", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Vậy đưa tôi về nhà anh. Tôi sẽ thử dùng chìa khoá mở phòng kho. Nếu đúng chúng tôi trả lại. Chìa khóa một cách nguyên vẹn và gửi lời xin lỗi sâu sắc tới anh. Nếu không, đây sẽ là bằng chứng buộc tội anh.", next: "s383" },
  { id: "s383", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael luống cuống, tay run, giọng lắp bắp.", next: "s384" },
  { id: "s384", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson, mau gọi cảnh sát tới, phong tỏa phòng sinh học.", next: "s385" },
  { id: "s385", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Ngay lập tức một nhóm cảnh sát tiến tới, một anh chàng nhìn thứ này ngắm nghĩa kĩ càng cùng với chiếc chìa khóa giống y hệt nằm trong túi zip mà họ đã lấy được từ hiện trường vụ án.", next: "s386" },
  { id: "s386", bg: "phongsinhhoc.jpg", char: "Cops.png", text: "Chìa khoá này giống hệt chìa khoá nhà nạn nhân.", next: "s387" },
  { id: "s387", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Kael giật mình, cố lắp bắp lời thanh minh.", next: "s388" },
  { id: "s388", bg: "phongsinhhoc.jpg", char: "Kael.png", text: "Thực ra đúng là Axton rớt chìa khoá trong lớp học vào tiết trước, tiết sau là tiết Sinh học của tôi. Tôi thấy nhưng chưa kịp trả, nghĩ tạm cầm về nhà để lần sau gặp lại rồi trả vì dạy xong tiết đó thầy Axton đã về thẳng nhà luôn. Việc tôi có chìa khoá tối hôm đó chỉ là trùng hợp mà thôi, mấy người phải tin tôi chứ.", next: "s389" },
  { id: "s389", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Heirlock đứng im, hắn không nói thêm điều gì nữa. Mọi bằng chứng rõ ràng đều quá rõ ràng chỉ về người thầy Sinh học Kael này, từ động cơ, cách thức gây án và cả kĩ thuật mổ xẻ chuyên môn được áp dụng lên thi thể nạn nhân nữa.", next: "s390" },
  { id: "s390", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Cảnh sát áp giải Kael ra khỏi phòng, từng bước chân vội vã vang trên sàn gạch ướt, vết bùn loang lổ theo sau.", next: "s391" },
  { id: "s391", bg: "phongsinhhoc.jpg", char: "Narrator", text: "Mainson thở dài, cậu nhìn theo bóng lưng của Kael đầy tiếc nuối.", next: "s392" },
  { id: "s392", bg: "phongsinhhoc.jpg", char: "Mainson.png", text: "Thám tử, ngài thấy không, chỉ vì người con gái mình yêu thân thiết với một người đàn ông khác, con người sẵn sàng làm những điều tàn bạo đến như vậy với chính giống loài của mình cơ mà.", next: "s393" },
  { id: "s393", bg: "phongsinhhoc.jpg", char: "Heirlock.png", text: "Heirlock quan sát, ánh mắt vẫn có chút gì đó lay động, hung thủ có thật sự là Kael không.", next: "s394" },
  { id: "s394", bg: "phongthamvan.jpg", next: "s395" },
  { id: "s395", bg: "phongthamvan.jpg", char: "Narrator", text: "Trời vẫn còn mưa lất phất. Tiếng nước rơi đều trên khung cửa kính loang mờ ánh đèn neon nhạt.", next: "s396" },
  { id: "s396", bg: "phongthamvan.jpg", char: "Narrator", text: "Trong phòng thẩm vấn, ánh sáng trắng rọi thẳng xuống mặt Kael. Gã ngồi cúi đầu, hai tay đan vào nhau, khuôn mặt mệt mỏi.", next: "s397" },
  { id: "s397", bg: "phongthamvan.jpg", char: "Narrator", text: "Heirlock và Mainson đứng phía bên kia bàn, im lặng vài giây trước khi bắt đầu.", next: "s398" },
  { id: "s398", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Kael, anh hiểu vì sao chúng tôi giữ anh lại, đúng chứ?", next: "s399" },
  { id: "s399", bg: "phongthamvan.jpg", char: "Kael.png", text: "Tôi đã nói rồi… tôi không làm gì cả. Tôi chỉ đến nói chuyện với cô ấy.", next: "s400" },
  { id: "s400", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Cô ấy, tức là Yanna?", next: "s401" },
  { id: "s401", bg: "phongthamvan.jpg", char: "Kael.png", text: "Ừ. Chúng tôi cãi nhau, rồi tôi về nhà, chỉ có vậy thôi.", next: "s402" },
  { id: "s402", bg: "phongthamvan.jpg", char: "Narrator", text: "Giọng Kael nhỏ dần, run nhẹ. Mainson cầm tập hồ sơ, đặt mạnh xuống bàn.", next: "s403" },
  { id: "s403", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Anh bảo về nhà lúc 18h hơn nhưng theo lời nhân chứng, Axton chết khoảng tám giờ tối. Hai tiếng trống đó, anh làm gì?", next: "s404" },
  { id: "s404", bg: "phongthamvan.jpg", char: "Kael.png", text: "Tôi… tôi đi dạo. Không nhớ rõ nữa.", next: "s405" },
  { id: "s405", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Đi dạo, lúc đó trời đang mưa lất phất đấy, anh đi dạo dưới trời mưa à?", next: "s406" },
  { id: "s406", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael im lặng. Ánh mắt gã lảng tránh đầy luống cuống. Heirlock bước tới, mở hồ sơ, giọng đều đều nhưng sắc lạnh.", next: "s407" },
  { id: "s407", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Vết siết cổ trên cổ nạn nhân cho thấy hung thủ không có chuyên môn.", next: "s408" },
  { id: "s408", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Hướng lực lệch phải nhưng không đều. Có tới ba vết chồng nhau.", next: "s409" },
  { id: "s409", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Điều này chỉ xảy ra khi người ra tay thấp bé hơn nạn nhân và vật lộn trong trạng thái hoảng loạn, và người đó thuận tay phải.", next: "s410" },
  { id: "s410", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael siết chặt hai tay lại, cơ thể hắn khẽ run lên.", next: "s411" },
  { id: "s411", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Trùng hợp thay, anh thấp bé hơn Axton, và cũng là người thuận tay phải.", next: "s412" },
  { id: "s412", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Còn vết thương ở vùng kín được rạch quá gọn gàng để là một cơn mất kiểm soát hoàn toàn. Dụng cụ sắc, lực tay vừa đủ, cho thấy hung thủ biết mình đang làm gì, đây không phải hành động ngẫu nhiên.", next: "s413" },
  { id: "s413", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Và chìa khoá.", next: "s414" },
  { id: "s414", bg: "phongthamvan.jpg", char: "Narrator", text: "Cậu tiếp lời, lật tờ biên bản, giọng rõ ràng, từng chữ nện xuống mặt bàn.", next: "s415" },
  { id: "s415", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Chìa khoá nhà nạn nhân nằm trong túi áo anh. Trên chìa có dấu vân tay của hai người, anh và Axton, không ai khác.", next: "s416" },
  { id: "s416", bg: "phongthamvan.jpg", char: "Kael.png", text: "Tôi nói rồi. Tôi nhặt được nó trong lớp!", next: "s417" },
  { id: "s417", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael nói lớn như đang cố gắng thanh minh cho chính mình.", next: "s418" },
  { id: "s418", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Trùng hợp lạ lùng thật. Một người tình cờ có chìa khoá nhà nạn nhân, tình cờ không có chứng cứ ngoại phạm, tình cờ có dấu giày trùng hiện trường, tình cờ là người cuối cùng gặp nạn nhân còn sống và tình cờ trùng với mọi dấu vết trên thi thể nạn nhân.", next: "s419" },
  { id: "s419", bg: "phongthamvan.jpg", char: "Narrator", text: "Không khí trong phòng im lặng. Tiếng mưa bên ngoài như đang dội vào tâm trí Kael khiến gã tỏ rõ sự mệt mỏi, chỉ còn tiếng đồng hồ tích tắc.", next: "s420" },
  { id: "s420", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Ngoài ra, chúng tôi đã kiểm tra lịch làm việc của nạn nhân. Axton và anh có hẹn gặp riêng chiều hôm đó, tại nhà Axton. Đúng không?", next: "s421" },
  { id: "s421", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael ngập ngừng một lúc rồi trả lời.", next: "s422" },
  { id: "s422", bg: "phongthamvan.jpg", char: "Kael.png", text: "Phải. Anh ta gọi tôi đến để trả vài tài liệu.", next: "s423" },
  { id: "s423", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Vậy là anh có lý do đến đó. Cửa không bị phá, không có dấu cạy. Nghĩa là hung thủ được mời vào. Anh ngồi xuống và Axton đã rót trà mời anh Kael như một chủ nhà lịch thiệp. Rồi sau một cuộc cãi vã, mọi thứ vượt quá giới hạn và anh ra tay.", next: "s424" },
  { id: "s424", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael run lên, ánh mắt mờ đi, đôi môi mấp máy nhưng không phát ra tiếng. Gã nửa như muốn nói lại, nửa lại như đã buông bỏ.", next: "s425" },
  { id: "s425", bg: "phongthamvan.jpg", char: "Narrator", text: "Heirlock chậm rãi đóng hồ sơ lại, tiếng bìa da khép vang lên giữa phòng im ắng.", next: "s426" },
  { id: "s426", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Mainson, hung thủ…", 
    choice: true, options: [ 
      { text: "Bắt giữ Kael", goto: "s427" }, 
      { text: "Điều tra thêm", goto: "s489" } 
    ] 
  },
  { id: "s427", bg: "phongthamvan.jpg", char: "Narrator", text: "Hắn dừng lại, nhìn thẳng Kael, ánh mắt lạnh lùng như lưỡi dao.", next: "s428" },
  { id: "s428", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Hung thủ chính là người đang ngồi trước mặt chúng ta.", next: "s429" },
  { id: "s429", bg: "phongthamvan.jpg", char: "Narrator", text: "Kael ngẩng phắt đầu, đôi mắt giật mạnh, hơi thở gấp gáp, gã gào lớn.", next: "s430" },
  { id: "s430", bg: "phongthamvan.jpg", char: "Kael.png", text: "Mẹ kiếp, lũ cảnh sát khốn nạn các người, tao không phải hung thủ, Lũ chết tiệt, chúng mày có nghe thấy không, tao không phải hung thủ.", next: "s431" },
  { id: "s431", bg: "phongthamvan.jpg", char: "Narrator", text: "Mainson im lặng, cúi xuống ký vào biên bản, giọng dứt khoát.", next: "s432" },
  { id: "s432", bg: "phongthamvan.jpg", char: "Mainson.png", text: "Tôi đồng ý với kết luận của Heirlock, tất cả đều khớp. Anh Kael, anh có thể mời luật sư sau và chúng ta sẽ nói chuyện rõ ràng hơn ở phiên tòa, còn bây giờ thì anh chịu khó bị tạm giam vậy.", next: "s433" },
  { id: "s433", bg: "phongthamvan.jpg", char: "Narrator", text: "Tiếng cửa mở, hai cảnh sát bước vào, còng tay Kael lại. Gã vùng vẫy, hét lên trong tuyệt vọng nhưng giọng nói dần bị tiếng mưa nuốt mất ngoài hành lang.", next: "s434" },
  { id: "s434", bg: "phongthamvan.jpg", char: "Narrator", text: "Heirlock vẫn đứng im, mắt dõi theo qua tấm kính mờ.", next: "s435" },
  { id: "s435", bg: "vanphong.jpg", next: "s436" },
  { id: "s436", bg: "vanphong.jpg", char: "Narrator", text: "Hai tuần sau.", next: "s437" },
  { id: "s437", bg: "vanphong.jpg", char: "Narrator", text: "Trời vẫn mưa, như chưa bao giờ tạnh kể từ vụ án trước. Bên ngoài cửa sổ văn phòng, Heirlock ngồi trầm ngâm trước tách cà phê đã nguội.", next: "s438" },
  { id: "s438", bg: "vanphong.jpg", char: "Narrator", text: "Mọi thứ tưởng như đã kết thúc. Kael đã nhận tội, hồ sơ khép lại, báo chí thôi không nhắc đến nữa.", next: "s439" },
  { id: "s439", bg: "vanphong.jpg", char: "Narrator", text: "Nhưng buổi sáng hôm ấy, tiếng chuông điện thoại reo vang phá tan sự im lặng.", next: "s440" },
  { id: "s440", bg: "vanphong.jpg", char: "Mainson.png", text: "Heirlock. Có án mạng mới. Nạn nhân tên… Yanna.", next: "s441" },
  { id: "s441", bg: "vanphong.jpg", char: "Narrator", text: "Tim hắn khựng lại.", next: "s442" },
  { id: "s442", bg: "vanphong.jpg", char: "Heirlock.png", text: "Yanna? Là con bé học sinh của Kael?", next: "s443" },
  { id: "s443", bg: "vanphong.jpg", char: "Mainson.png", text: "Phải. Là Yanna đó, anh mau tới đi, tôi rối quá, con bé nó chết thảm lắm, tôi sợ vụ án của Kael hình như không đơn giản tới vậy, rốt cuộc ai đã giết Yanna.", next: "s444" },
  { id: "s444", bg: "vanphong.jpg", char: "Narrator", text: "Hắn đứng bật dậy, khoác áo, vội vã rời khỏi văn phòng.", next: "s445" },
  { id: "s445", bg: "hientruongending1.jpg", next: "s446" },
  { id: "s446", bg: "hientruongending1.jpg", char: "Narrator", text: "Khi Heirlock đến, đèn công an lập loè hắt sáng lên hai thi thể nằm giữa căn phòng nhỏ.", next: "s447" },
  { id: "s447", bg: "hientruongending1.jpg", char: "Narrator", text: "Yanna nằm sấp xuống sàn, một thanh sắt gỉ xuyên qua ngực.", next: "s448" },
  { id: "s448", bg: "hientruongending1.jpg", char: "Narrator", text: "Bên cạnh là người đàn ông tầm 25, có vẻ là công nhân, thi thể gập lại, bộ phận sinh dục bị chém giống hệt Axton.", next: "s449" },
  { id: "s449", bg: "hientruongending1.jpg", char: "Narrator", text: "Phần đầu của người công nhân bị chặt rời đặt ngay trước mặt Yanna, phần thân nằm ở góc phòng, máu khô loang lổ.", next: "s450" },
  { id: "s450", bg: "hientruongending1.jpg", char: "Mainson.png", text: "Hai nạn nhân. Có vẻ như người đàn ông cố cứu cô gái, nhưng cả hai đều chết gần như cùng lúc.", next: "s451" },
  { id: "s451", bg: "hientruongending1.jpg", char: "Heirlock.png", text: "Giờ tử vong?", next: "s452" },
  { id: "s452", bg: "hientruongending1.jpg", char: "Mainson.png", text: "Có lẽ khoảng 23h đêm qua. Người báo tin là hàng xóm nghe tiếng động lớn.", next: "s453" },
  { id: "s453", bg: "hientruongending1.jpg", char: "Narrator", text: "Nói rồi Mainson liên tục nhìn vào chiếc đồng hồ trên tay mình.", next: "s454" },
  { id: "s454", bg: "hientruongending1.jpg", char: "Heirlock.png", text: "Cậu đang đợi ai à?", next: "s455" },
  { id: "s455", bg: "hientruongending1.jpg", char: "Mainson.png", text: "Là Kam đó, sáng nay đã liên lạc bảo anh ta tới khám nghiệm thi thể đi mà chẳng thấy đâu.", next: "s456" },
  { id: "s456", bg: "hientruongending1.jpg", char: "Narrator", text: "Heirlock cúi xuống, nhìn quanh căn phòng ngăn nắp, tuy nhiên những vệt máu vẫn trải dài khắp nơi như một trận tàn sát không nương tay.", next: "s457" },
  { id: "s457", bg: "hientruongending1.jpg", char: "Người dân 1.png", text: "Tối qua tôi thấy có người lạ vào đây, đẹp trai lắm, ăn mặc lịch sự, trắng trẻo nữa.", next: "s458" },
  { id: "s458", bg: "hientruongending1.jpg", char: "Người dân 2.png", text: "Phải đó, nhìn như dân văn phòng, tóc đen bóng, vuốt keo gọn gàng, dáng cao ráo, đeo kính.", next: "s459" },
  { id: "s459", bg: "hientruongending1.jpg", char: "Heirlock.png", text: "Cậu vừa nói... anh ta đeo kính à?", next: "s460" },
  { id: "s460", bg: "hientruongending1.jpg", char: "Người dân 2.png", text: "Phải. Đẹp trai lắm, nhìn kiểu tri thức ấy, tôi còn chẳng dám nghĩ sẽ gặp được một người như vậy ở cái khu này cơ.", next: "s461" },
  { id: "s461", bg: "hientruongending1.jpg", char: "Narrator", text: "Một hình ảnh thoáng vụt qua đầu Heirlock, chiếc áo blouse trắng, gọng kính bạc phản chiếu ánh đèn phòng khám nghiệm và nụ cười mệt mỏi nhưng hiền cùng đôi mắt đục ngầu.", next: "s462" },
  { id: "s462", bg: "hientruongending1.jpg", char: "Heirlock.png", text: "Không thể nào...", next: "s463" },
  { id: "s463", bg: "hientruongending1.jpg", char: "Narrator", text: "Heirlock quay người bỏ chạy, mặc cho Mainson gọi với theo.", next: "s464" },
  { id: "s464", bg: "hientruongending1.jpg", char: "Mainson.png", text: "Này! Heirlock, anh đi đâu vậy?", next: "s465" },
  { id: "s465", bg: "hientruongending1.jpg", char: "Narrator", text: "Nhưng Heirlock không đáp mà chỉ lao ra khỏi khu trọ, trời bắt đầu đổ mưa nặng hạt.", next: "s466" },
  { id: "s466", bg: "duongmua.jpg", next: "s467" },
  { id: "s467", bg: "duongmua.jpg", char: "Narrator", text: "Đường đầy sình lầy, Heirlock bắt đầu chạy bộ giữa mưa, áo choàng dính sát vào người, tóc rũ ướt đẫm.", next: "s468" },
  { id: "s468", bg: "duongmua.jpg", char: "Narrator", text: "Phía trước là con đường dẫn về Cục Cảnh sát.", next: "s469" },
  { id: "s469", bg: "duongmua.jpg", char: "Narrator", text: "Rồi bỗng một bóng người vụt ra từ ngõ. Tiếng kim loại va vào đầu vang lên chát chúa. Mọi thứ tối sầm.", next: "s470" },
  { id: "s470", bg: "niggascreen.jpg", next: "s471" },
  { id: "s471", bg: "tangham.jpg", next: "s472" },
  { id: "s472", bg: "tangham.jpg", char: "Heirlock.png", text: "Da…rius?", next: "s473" },
  { id: "s473", bg: "tangham.jpg", char: "Kam.png", text: "Tôi không định để mọi chuyện đi xa đến thế.", next: "s474" },
  { id: "s474", bg: "tangham.jpg", char: "Narrator", text: "Heirlock ngẩng đầu, Kam đứng đó, khoác áo blouse dính máu, ánh mắt lặng lẽ, nụ cười nhạt hệt như mọi khi trong phòng pháp y.", next: "s475" },
  { id: "s475", bg: "tangham.jpg", char: "Kam.png", text: "Kể ra nếu thám tử đừng dính vào vụ này, chúng ta đã có thể tiếp tục làm bạn tốt.", next: "s476" },
  { id: "s476", bg: "tangham.jpg", char: "Narrator", text: "Kam bước lại gần, rút con dao mổ sáng loáng từ túi áo. Mũi dao chạm nhẹ lên cổ Heirlock, để lại một vệt lạnh buốt.", next: "s477" },
  { id: "s477", bg: "tangham.jpg", char: "Kam.png", text: "Anh luôn giỏi hơn người khác, Heirlock. Nhưng có lẽ lần này, anh đoán sai rồi.", next: "s478" },
  { id: "s478", bg: "tangham.jpg", char: "Narrator", text: "Heirlock nhìn lên, đôi mắt mở to, kinh hoàng, hơi thở ngắt quãng. Dưới chân Kam là đôi giày da đen cỡ 44, in dấu bùn khô như hiện trường vụ Kael. Bàn tay phải Kam cầm con dao với vết sẹo nhỏ nơi cổ tay mà Heirlock nhớ rất rõ.", next: "s479" },
  { id: "s479", bg: "tangham.jpg", char: "Narrator", text: "Mọi thứ bỗng chậm lại. Trong đầu Heirlock vang lên những mảnh ký ức đứt đoạn, hung thủ thuận tay phải, thấp hơn nạn nhân, không có chuyên môn siết cổ.", next: "s480" },
  { id: "s480", bg: "tangham.jpg", char: "Kam.png", text: "Heirlock, căn phòng kín đó là do tôi, tôi đã lén đánh một chiếc chìa dự phòng giống hệt khi Axton để quên trên cục cảnh sát, sau đó trả lại. Kế hoạch trả thù đã được lập từ khi đó.", next: "s481" },
  { id: "s481", bg: "tangham.jpg", char: "Kam.png", text: "Và giờ nó đã hoàn thành rồi, em gái tôi cuối cùng cũng có thể thực sự yên nghỉ, xin lỗi anh rất nhiều, Heirlock.", next: "s482" },
  { id: "s482", bg: "tangham.jpg", char: "Kam.png", text: "Đáng lẽ ra tôi không nên giết anh, nhưng anh liên tục làm chậm tiến trình trả thù của tôi, nếu không phải tại anh thì tên Darius cũng không chết sớm như vậy, hắn xứng đáng phải chịu tra tấn ngày này qua ngày khác.", next: "s483" },
  { id: "s483", bg: "tangham.jpg", char: "Narrator", text: "Kam thở dài, anh nhẹ nhàng nhấn lên con dao khiến Heirlock đau nhói. Kael… bị đổ oan. Mọi âm thanh tắt lịm. Con dao vung lên, ánh sáng lướt qua trong tích tắc rồi tầm nhìn Heirlock tối đen.", next: "s484" },
  { id: "s484", bg: "tangham.jpg", char: "Narrator", text: "Kael… bị đổ oan.", next: "s485" },
  { id: "s485", bg: "tangham.jpg", char: "Narrator", text: "Mọi âm thanh tắt lịm. Con dao vung lên, ánh sáng lướt qua trong tích tắc rồi tầm nhìn Heirlock tối đen.", next: "s486" },
  { id: "s486", bg: "manhinhmau.jpg", next: "s487" },
  { id: "s487", bg: "lannayanhdoansairoi.jpg", ending: "A" },
  { id: "s488", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Mainson, hung thủ…", next: "s489" },
  { id: "s489", bg: "phongthamvan.jpg", char: "Narrator", text: "Mùi cà phê nguội lẫn trong không khí mệt mỏi. Heirlock ngồi dựa vào ghế, ngón tay gõ nhẹ lên mặt bàn kim loại.", next: "s490" },
  { id: "s490", bg: "phongthamvan.jpg", char: "Narrator", text: "Phòng điều tra chìm trong ánh sáng trắng nhợt nhạt. Trên bàn, những tập hồ sơ mở tung, vài tấm ảnh hiện trường còn dính vệt máu khô.", next: "s491" },
  { id: "s491", bg: "phongthamvan.jpg", char: "Narrator", text: "Hắn im lặng một hồi lâu rồi lên tiếng.", next: "s492" },
  { id: "s492", bg: "phongthamvan.jpg", char: "Heirlock.png", text: "Có lẽ cần phải điều tra thêm.", next: "s493" },
  { id: "s493", bg: "phongthamvan.jpg", char: "Narrator", text: "Nói rồi hắn đứng dậy, để mặc Kael ngơ ngác trong phòng thẩm vấn còn bản thân thì ra ngoài trước, Mainson cũng vội vã chạy theo sau.", next: "s494" },
  { id: "s494", bg: "cuccanhsat.jpg", next: "s495" },
  { id: "s495", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Heirlock, anh…", next: "s496" },
  { id: "s496", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson đứng bên cửa sổ, nhìn mưa trút xuống bên ngoài, chờ đợi hắn giải thích.", next: "s497" },
  { id: "s497", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Cậu nghĩ sao nếu tôi nói rằng Kael không phải hung thủ?", next: "s498" },
  { id: "s498", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson im lặng rồi đột nhiên cậu trở nên nghiêm túc, hoàn toàn khác với dáng vẻ hiền lành thường ngày.", next: "s499" },
  { id: "s499", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Tôi không ngạc nhiên lắm, Kael, gã ta không thể là hung thủ.", next: "s500" },
  { id: "s500", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Không có tên hung thủ nào lại bốc đồng, luống cuống, sợ hãi, ngu ngốc và bị động như Kael cả, nhất là đối với vụ án lần này.", next: "s501" },
  { id: "s501", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Mọi thứ ấy quá thuận lợi, quá khớp với bằng chứng, quá hợp lý đến mức giả tạo.", next: "s502" },
  { id: "s502", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson nhíu mày, chống hai tay lên lan can, giọng trầm xuống.", next: "s503" },
  { id: "s503", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Ý anh là sao?", next: "s504" },
  { id: "s504", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Một người như Kael nếu thực sự giết người trong lúc mất kiểm soát thì hiện trường đã không thể gọn gàng đến vậy.", next: "s505" },
  { id: "s505", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Hắn ta không phải kẻ máu lạnh, càng không có kinh nghiệm trong trải qua thẩm vấn, dù bằng chứng đây là vụ án bộc phát nhưng hiện trường, chứng cứ, mọi thứ đều quá trật tự.", next: "s506" },
  { id: "s506", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson gật gù, quả thực mọi thứ đều thuận lợi một cách kì lạ, như thể đã bị ai sắp xếp trước vậy, và chúng ta chỉ là những con bò ngu ngốc được dắt đi khắp nơi và dừng chân lại trước Kael.", next: "s507" },
  { id: "s507", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Còn chiếc chìa khóa thì sao? Dù sao nó cũng thực sự nằm trong túi áo Kael, không thể là trùng hợp.", next: "s508" },
  { id: "s508", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Chính điểm ấy mới khiến tôi thấy khó tin. Hung thủ giết người rồi khóa cửa từ trong, rõ ràng biết mình đang cầm vật chứng buộc tội vậy mà lại giữ nó?", next: "s509" },
  { id: "s509", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Nếu là Kael thật thì gã đã vứt đi từ lâu. Tôi nghi có ai đó cố tình lập kế hoạch để gã cầm chiếc chìa khóa đó trên trường, nhằm dồn mọi nghi ngờ về một hướng.", next: "s510" },
  { id: "s510", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson nghi hoặc, muốn nói gì đó nhưng rồi lại thôi, rất may hắn nhanh chóng cảm nhận được điều đó mà tiếp lời.", next: "s511" },
  { id: "s511", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Cũng có thể là Axton thực sự chỉ rơi chìa khóa nhà trên lớp hợp trong tiết dạy cuối cùng của mình vì sơ xuất, và Kael vô tình trở thay kẻ thay thế, gánh mọi trách nhiệm.", next: "s512" },
  { id: "s512", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Hoặc cũng có thể Axton đã mất chìa từ trước, và bằng nào đó, vào đúng tiết dạy của Kael thì nó vô tình xuất hiện trên bàn giáo viên, nhưng điều đó quá khó để xảy ra, dù sao một người lạ cũng không thể tự do ra vào trường được.", next: "s513" },
  { id: "s513", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson im lặng vài giây, ánh nhìn dừng lại trên tấm hình Axton trong tập hồ sơ, khuôn mặt tím tái, vết siết đỏ hằn trên cổ.", next: "s514" },
  { id: "s514", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Vậy để tôi đi hỏi Kam, có lẽ giờ này kết quả xét nghiệm ADN có hết cả rồi, tôi muốn xem ai có thể thao túng được cả bằng chứng để quay cảnh sát như chong chóng thế này.", next: "s515" },
  { id: "s515", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Để tôi đi cùng cậu.", next: "s516" },
  { id: "s516", bg: "cuccanhsat.jpg", char: "Narrator", text: "Đi được vài bước đột nhiên hắn đứng lại khiến Mainson khó hiểu.", next: "s517" },
  { id: "s517", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Đợi tôi chút, tôi đã hứa với Kam lần sau gặp lại sẽ mời anh ấy cafe rồi.", next: "s518" },
  { id: "s518", bg: "cuccanhsat.jpg", char: "Narrator", text: "Nói rồi hắn không đợi người kia kịp nói gì đã nhanh chóng xuống tầng 1, nơi có canteen dành cho những viên cảnh sát ở đây như thói quen, cứ như thể Heirlock đã quá quen thuộc với cục cảnh sát này, thậm chí còn thân thuộc hơn cả Mainson khiến cậu chỉ biết lắc đầu.", next: "s519" },
  { id: "s519", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson đã quen biết Heirlock từ khi cậu mới được phân công tới tổ 3 thực hiện điều tra các vụ án hình sự đặc biệt nghiêm trọng, vốn dĩ Heirlock đã từng kết hợp với những người đàn anh để hoàn thành nhiều trọng án.", next: "s520" },
  { id: "s520", bg: "cuccanhsat.jpg", char: "Narrator", text: "Có lẽ cục cảnh sát này còn giống nhà của hắn còn hơn cái văn phòng thám tử xuề xòa thuê tạm bợ trong ngõ ngách đó nữa.", next: "s521" },
  { id: "s521", bg: "cuccanhsat.jpg", char: "Narrator", text: "Heirlock nhanh chóng quay lại, trên tay là một cốc cafe sữa với… 2/3 chất lỏng trong đó là sữa đặc thay vì cafe khiến Mainson tròn mắt, cậu lắp bắp.", next: "s522" },
  { id: "s522", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Anh gọi đây là cafe sữa sao, nó phải là sữa đặc đá thêm hậu vị cafe mới đúng.", next: "s523" },
  { id: "s523", bg: "cuccanhsat.jpg", char: "Narrator", text: "Hắn bật cười, đôi mắt nheo lại khiến Mainson ngây người, đây là lần đầu tiên sau một thời gian dài quen biết cậu được thấy vị thám tử già đời này biết cười, có lẽ Heirlock rất thân thiết với vị pháp y Kam đó.", next: "s524" },
  { id: "s524", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Mainson của tôi, cậu không hiểu đâu, Kam nó thích kiểu cafe như này lắm đấy, thằng ngốc đó bao năm vẫn không bỏ được cái tật uống ngọt dù biết rõ là sẽ hại.", next: "s525" },
  { id: "s525", bg: "cuccanhsat.jpg", char: "Mainson.png", text: "Hai người có vẻ rất thân thiết?", next: "s526" },
  { id: "s526", bg: "cuccanhsat.jpg", char: "Heirlock.png", text: "Phải, tôi chứng kiến tên đó từ khi mới chân ướt chân ráo chuyển công tác sang thành phố của mình, lúc nào cũng trưng cái vẻ mặt tôi sẽ không thèm làm thân với bất kì ai vậy mà cuối cùng giờ cũng biết cười rồi.", next: "s527" },
  { id: "s527", bg: "cuccanhsat.jpg", char: "Narrator", text: "Mainson gật đầu, quả thực họ rất thân thiết, cậu nhanh chóng theo chân vị thám tử để tới phòng pháp y.", next: "s528" },
  { id: "s528", bg: "phongphapy.jpg", next: "s529" },
  { id: "s529", bg: "phongphapy.jpg", char: "Narrator", text: "Cánh cửa nặng nề mở ra, không kịp mở lời thì lần này Heirlock đã chủ động bước lên trước, đặt cốc sữa đặc đá lạnh thêm cafe của mình lên bàn rồi thong thả ngồi phịch xuống ghế.", next: "s530" },
  { id: "s530", bg: "phongphapy.jpg", char: "Kam.png", text: "Anh thám tử của tôi hôm nay cần gì đây?", next: "s531" },
  { id: "s531", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Xét nghiệm ADN chưa ra kết quả à?", next: "s532" },
  { id: "s532", bg: "phongphapy.jpg", char: "Kam.png", text: "Có rồi. Không tìm thấy dấu vết của Kael trên người nạn nhân, cả trong móng tay lẫn vết bầm. Anh còn nhớ không, Axton cao hơn Kael gần một cái đầu và thi thể lại có dấu hiệu chống cự.", next: "s533" },
  { id: "s533", bg: "phongphapy.jpg", char: "Kam.png", text: "Một người to lớn bị siết cổ như thế đáng ra phải để lại ít nhất vài tế bào da của kẻ tấn công, nhưng không có gì hết, trong khi rõ ràng nếu anh ta muốn, Axton hoàn toàn đủ sức phản kháng lại Kael.", next: "s534" },
  { id: "s534", bg: "phongphapy.jpg", char: "Mainson.png", text: "Có thể Kael đeo găng tay.", next: "s535" },
  { id: "s535", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock đột nhiên lên tiếng.", next: "s536" },
  { id: "s536", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Nếu vậy gã phải mang cả bộ đồ bảo hộ để tránh dính máu, và lau sạch hiện trường không một lỗi. Nhưng Kael chỉ là giảng viên, gã không có kỹ năng hay tâm lý để làm việc đó.", next: "s537" },
  { id: "s537", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Hơn nữa, cậu nghĩ xem, với cách nói chuyện mất bình tĩnh như Kael liệu gã có thể làm mọi chuyện hoàn hảo đến vậy không?", next: "s538" },
  { id: "s538", bg: "phongphapy.jpg", char: "Narrator", text: "Mainson khoanh tay, hít sâu một hơi, giọng thấp xuống.", next: "s539" },
  { id: "s539", bg: "phongphapy.jpg", char: "Mainson.png", text: "Còn dấu giày da cỡ 41?", next: "s540" },
  { id: "s540", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Loại giày đó bán đầy ngoài phố, tôi đếm sơ cũng có hàng trăm người mang cùng cỡ. Hơn nữa nếu như đã có thể thu dọn hiện trường một cách hoàn hảo như vậy tại sao không lau nốt dấu giày.", next: "s541" },
  { id: "s541", bg: "phongphapy.jpg", char: "Narrator", text: "Mainson ngưng lại một lúc.", next: "s542" },
  { id: "s542", bg: "phongphapy.jpg", char: "Mainson.png", text: "Vậy theo anh, ai là hung thủ?", next: "s543" },
  { id: "s543", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Tôi chưa biết. Nhưng chắc chắn có ai đó muốn chúng ta tin rằng Kael là kẻ giết người.", next: "s544" },
  { id: "s544", bg: "phongphapy.jpg", char: "Narrator", text: "Một khoảng lặng kéo dài. Chỉ còn tiếng mưa đập vào cửa kính. Heirlock rời ghế, bước chậm tới bàn hồ sơ, lấy cây bút đỏ khoanh quanh dòng 'chìa khóa của nạn nhân'.", next: "s545" },
  { id: "s545", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Mainson, cậu cho người đi hỏi hàng xóm quanh nhà nạn nhân. Tôi muốn biết ai nghe hay thấy điều gì trong khung giờ tử vong.", next: "s546" },
  { id: "s546", bg: "phongphapy.jpg", char: "Narrator", text: "Mainson gật đầu rồi nhanh chóng rời đi, trước khi bước ra tới cửa còn không quên cúi chào vị pháp y nãy giờ chỉ đang chăm chú khuấy cốc sữa đặc cafe của mình.", next: "s547" },
  { id: "s547", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Kam, cậu nghĩ sao về vụ án này?", next: "s548" },
  { id: "s548", bg: "phongphapy.jpg", char: "Narrator", text: "Anh ta nhún vai rồi uống một ngụm, đôi mắt đục ngầu đầy mệt mỏi đã dần có sức sống hơn.", next: "s549" },
  { id: "s549", bg: "phongphapy.jpg", char: "Kam.png", text: "Tôi không rõ, đó chẳng phải là việc của thám tử và cánh sát sao, công việc của tôi chỉ là cung cấp bằng chứng về thi thể cho những người như anh, Heirlock.", next: "s550" },
  { id: "s550", bg: "phongphapy.jpg", char: "Kam.png", text: "Anh thám tử của tôi, có lẽ anh nên về văn phòng nghỉ ngơi chút rồi đấy, nhìn quầng thâm trên mắt của anh kìa.", next: "s551" },
  { id: "s551", bg: "phongphapy.jpg", char: "Narrator", text: "Nói rồi Kam tiến lại gần gắn, một mùi hương tanh tưởi của máu thoang thoảng quanh người anh khiến Heirlock rùng mình, tên này đã sống cùng tử thi quá lâu tới mức ám cả mùi rồi.", next: "s552" },
  { id: "s552", bg: "phongphapy.jpg", char: "Kam.png", text: "Thám tử, thật sự cảm ơn anh.", next: "s553" },
  { id: "s553", bg: "phongphapy.jpg", char: "Narrator", text: "Hắn ngước lên, khó hiểu nhìn về phía người đàn ông trước mặt.", next: "s554" },
  { id: "s554", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Vì điều gì?", next: "s555" },
  { id: "s555", bg: "phongphapy.jpg", char: "Kam.png", text: "Có lẽ là vì anh là một thám tử chăng?", next: "s556" },
  { id: "s556", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Hả, cậu đang nói cái gì vậy?", next: "s557" },
  { id: "s557", bg: "phongphapy.jpg", char: "Kam.png", text: "Một thám tử chính trực luôn mong muốn sự thật được phơi bày ra ánh sáng, kẻ có tội phải bị trừng phạt và nạn nhân sẽ được yên nghỉ.", next: "s558" },
  { id: "s558", bg: "phongphapy.jpg", char: "Narrator", text: "Ánh mắt Kam rũ xuống, mái tóc lòa xòa rối bù che đi phần nào biểu cảm trên khuôn mặt anh khiến Heirlock có một cảm giác kì lạ, có lẽ chỉ là thứ trực giác của một vị thám tử lâu năm nhưng hắn đã thực sự nghĩ con người trước mắt này có lẽ đã giết người.", next: "s559" },
  { id: "s559", bg: "phongphapy.jpg", char: "Narrator", text: "Và có lẽ sẽ còn giết thêm rất nhiều người trong tương lai, Heirlock theo phản xạ mà đặt tay lên cán súng đang đeo trên thắt lưng mình như một phản xạ tự vệ.", next: "s560" },
  { id: "s560", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Kam, cậu…", next: "s561" },
  { id: "s561", bg: "phongphapy.jpg", char: "Kam.png", text: "Đừng lo lắng quá, ngài thám tử, hãy làm điều mà anh cho là đúng đắn đi, tôi ủng hộ anh, đó là lời thật lòng đấy.", next: "s562" },
  { id: "s562", bg: "phongphapy.jpg", char: "Narrator", text: "Nói rồi đột nhiên Kam mỉm cười vui vẻ, đôi mắt chết chóc kia như chưa từng tồn tại mà trở nên lười biếng và mệt mỏi chẳng khác nào vị pháp y quen thuộc thường ngày làm Heirlock khó hiểu, cứ như những gì hắn vừa trải qua chỉ là một giấc mộng.", next: "s563" },
  { id: "s563", bg: "phongphapy.jpg", char: "Kam.png", text: "Heirlock, anh thực sự nên về ngủ đó, tôi chỉ nói rằng tôi sẽ ủng hộ anh thôi mà, trông tôi đáng sợ lắm hay sao mà anh phải cầm súng thế kia?", next: "s564" },
  { id: "s564", bg: "phongphapy.jpg", char: "Heirlock.png", text: "À kh… không có gì, phải rồi, có lẽ tôi chỉ đang mệt mỏi thôi. Cảm ơn cậu nhé, tôi sẽ về văn phòng nghỉ ngơi một chút.", next: "s565" },
  { id: "s565", bg: "vanphong.jpg", next: "s566" },
  { id: "s566", bg: "vanphong.jpg", char: "Narrator", text: "Hắn vừa trở về đã lập tức nằm vật ra ghế mà ngủ thiếp đi tới khi có tiếng gõ cửa vang lên cùng giọng nói quen thuộc.", next: "s567" },
  { id: "s567", bg: "vanphong.jpg", char: "Mainson.png", text: "Là tôi, Mainson đây, anh đang làm gì thế, Heirlock?", next: "s568" },
  { id: "s568", bg: "vanphong.jpg", char: "Narrator", text: "Hắn vội vàng ngồi dậy chỉnh lại bộ trang phục rồi nói lớn.", next: "s569" },
  { id: "s569", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vào đi!", next: "s570" },
  { id: "s570", bg: "vanphong.jpg", char: "Narrator", text: "Mainson bước vào với một xấp giấy trên tay, có lẽ báo cáo đã về. Mainson đọc chậm rãi, giọng khàn hơn thường ngày.", next: "s571" },
  { id: "s571", bg: "vanphong.jpg", char: "Mainson.png", text: "Có người nghe tiếng lạch cạch vào khoảng 20h35, hình như là tiếng khóa cửa. Sau đó im bặt.", next: "s572" },
  { id: "s572", bg: "vanphong.jpg", char: "Heirlock.png", text: "Ước tính thời điểm tử vong là 20h30. Nếu 20h35 có tiếng khóa nghĩa là Kael thực sự đã trở về nhà 5 phút sau khi giết người? Không thể nào, vô lí, nhà gã và nạn nhân cách nhau tận 6km.", next: "s573" },
  { id: "s573", bg: "vanphong.jpg", char: "Mainson.png", text: "Khoảng thời gian ấy không thể đủ nếu Kael phải lau chùi, xóa dấu vết rồi chạy về nhà. Anh nói đúng, có điều gì đó không khớp.", next: "s574" },
  { id: "s574", bg: "vanphong.jpg", char: "Narrator", text: "Hắn im lặng nhìn vào mốc thời gian được Mainson khoanh tròn trên cuốn sổ tay rồi lên tiếng.", next: "s575" },
  { id: "s575", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy đã khám xét nhà Kael chưa, kể cả thùng rác hay mọi nơi có thể xung quanh tòa nhà, có thứ gì đáng nghi không?", next: "s576" },
  { id: "s576", bg: "vanphong.jpg", char: "Mainson.png", text: "Không, tôi đã lục soát toàn bộ căn hộ, thùng rác và cả khu vực quanh nhà. Không có dao phẫu thuật, không khăn lau, không găng tay.", next: "s577" },
  { id: "s577", bg: "vanphong.jpg", char: "Mainson.png", text: "Dù đã dùng luminol quét kỹ cũng không phát hiện vết máu nào, đây thực sự chỉ là một tòa nhà bình thường, không ai thấy điều gì khác trong ngày hôm đó hết.", next: "s578" },
  { id: "s578", bg: "vanphong.jpg", char: "Mainson.png", text: "Hung thủ giấu hung khí quá kỹ, hoặc đã mang theo đi xa.", next: "s579" },
  { id: "s579", bg: "vanphong.jpg", char: "Heirlock.png", text: "Và chỉ một người đủ hiểu quy trình điều tra mới biết cần xóa sạch đến thế. Kael không có kiến thức đó.", next: "s580" },
  { id: "s580", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock dừng lại, giọng ông trầm xuống, như đang tự nói với chính mình.", next: "s581" },
  { id: "s581", bg: "vanphong.jpg", char: "Heirlock.png", text: "Chưa đủ bằng chứng để kết tội Kael. Mọi thứ quá hoàn hảo, như thể ai đó đã sắp đặt sẵn từng chi tiết.", next: "s582" },
  { id: "s582", bg: "vanphong.jpg", char: "Narrator", text: "Mainson ngẩng lên, nhìn hắn với vẻ lo lắng. Ngoài kia sấm rền nhẹ, mưa vẫn chưa dứt.", next: "s583" },
  { id: "s583", bg: "vanphong.jpg", char: "Mainson.png", text: "Anh định nói… có kẻ khác đang đứng sau chuyện này sao?", next: "s584" },
  { id: "s584", bg: "vanphong.jpg", char: "Heirlock.png", text: "Tôi không định nói, tôi chắc chắn là vậy. Ai đó đã khiến chúng ta nhìn sai hướng.", next: "s585" },
  { id: "s585", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock chỉ vào mục bằng chứng trên tài liệu.", next: "s586" },
  { id: "s586", bg: "vanphong.jpg", char: "Heirlock.png", text: "Một kẻ nào đó đủ cẩn thận để dọn mọi ADN có thể sót lại trong một hiện trường giết người tàn khốc nhưng có thể vô ý tới nỗi để lại dấu giày da, cuốn sổ tay có ghi lịch trình gặp một người khác vào tối hôm đó.", next: "s587" },
  { id: "s587", bg: "vanphong.jpg", char: "Heirlock.png", text: "Nếu là Kael, ít nhất hắn cũng phải biết xóa dấu giày của mình chứ, hay là sổ tay ghi bên ngoài rõ là lịch trình rồi, vậy lại có kẻ nằm trong kế hoạch gặp mặt nào đó ngu ngốc tới nỗi để nguyên lại mà không thủ tiêu sao.", next: "s588" },
  { id: "s588", bg: "vanphong.jpg", char: "Heirlock.png", text: "Để đó cho tất cả mọi người đều biết ngày hôm ấy, nạn nhân đã hẹn gặp mình tại nhà riêng?", next: "s589" },
  { id: "s589", bg: "vanphong.jpg", char: "Narrator", text: "Mainson gật đầu, quả thực, đang có kẻ nào đó điều khiển mọi chuyện.", next: "s590" },
  { id: "s590", bg: "vanphong.jpg", char: "Narrator", text: "Ánh sáng lóe qua cửa kính, soi rõ khuôn mặt của hai người, cả hai đều hiểu rằng vụ án này mới chỉ bắt đầu mở ra.", next: "s591" },
  { id: "s591", bg: "duongmua.jpg", next: "s592" },
  { id: "s592", bg: "duongmua.jpg", char: "Narrator", text: "Mưa rả rích ngoài cửa sổ, ánh đèn vàng nhạt hắt xuống nền nhà ẩm mốc. Heirlock và Mainson bước nhẹ trên bậc thang sảnh tòa nhà rồi dừng bước ở trước căn hộ số 12, chủ của căn nhà mà nạn nhân Axton đã thuê, đồng thời cũng là hiện trường vụ án.", next: "s593" },
  { id: "s593", bg: "duongmua.jpg", char: "Narrator", text: "Mainson gõ lên cánh cửa gỗ sẫm màu vài lần.", next: "s594" },
  { id: "s594", bg: "duongmua.jpg", char: "Narrator", text: "Cánh cửa mở ra từ bên trong, một người đàn ông trung niên bước ra. Ánh mắt ông dừng lại trên những vị khách không mời mà đến, tỏ rõ vẻ bất ngờ và sự nghi ngại.", next: "s595" },
  { id: "s595", bg: "duongmua.jpg", char: "Người dân 1.png", text: "Hai người là…", next: "s596" },
  { id: "s596", bg: "duongmua.jpg", char: "Mainson.png", text: "Chào ông, tôi là Mainson, cảnh sát đang điều tra vụ án Axton còn đây là anh Heirlock, thám tử tư. Chúng tôi chỉ muốn hỏi một số thông tin liên quan đến thầy Axton, hy vọng ông có thể giúp.", next: "s597" },
  { id: "s597", bg: "duongmua.jpg", char: "Narrator", text: "Người đàn ông cau mày, ánh mắt thoáng lạnh lùng như đang nhớ lại những lần bị quấy rầy không ngớt sau khi vụ án đó xảy ra.", next: "s598" },
  { id: "s598", bg: "duongmua.jpg", char: "Người dân 1.png", text: "Ồ, tôi biết rồi, trước đây đã có cảnh sát tới hỏi rất nhiều lần.", next: "s599" },
  { id: "s599", bg: "duongmua.jpg", char: "Người dân 1.png", text: "Mấy người không thể hỏi một lượt hết được à, dăm bữa lại tới một lần, thật sự rất phiền đấy anh cảnh sát à.", next: "s600" },
  { id: "s600", bg: "duongmua.jpg", char: "Narrator", text: "Ông ta nhíu mày cùng ánh mắt bực bội hướng về phía cậu làm Mainson lúng túng.", next: "s601" },
  { id: "s601", bg: "duongmua.jpg", char: "Narrator", text: "Cậu cố gắng giữ bình tĩnh và sự chuyên nghiệp mà mỉm cười nhẹ, điềm tĩnh nói.", next: "s602" },
  { id: "s602", bg: "duongmua.jpg", char: "Mainson.png", text: "Xin thông cảm thưa ông. Chúng tôi hiểu cảm giác đó. Tuy nhiên vụ án này còn nhiều khúc mắc và chúng tôi chỉ muốn tìm ra sự thật, sẽ không làm phiền ông quá nhiều đâu.", next: "s603" },
  { id: "s603", bg: "duongmua.jpg", char: "Người dân 1.png", text: "Ừ thôi được rồi. Các anh cứ vào, tôi sẽ nói hết những gì tôi biết.", next: "s604" },
  { id: "s604", bg: "canhochunhaAxton.jpg", next: "s605" },
  { id: "s605", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Cậu gật đầu, lịch sự cúi chào trước khi bước vào còn hắn đi theo sau, ánh mắt lướt qua căn hộ ấm áp nhưng tĩnh lặng ấy.", next: "s606" },
  { id: "s606", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Nơi này trưng bày đơn giản, người đàn ông vừa đóng cửa lại liền lớn tiếng gọi vọng vào trong nhà.", next: "s607" },
  { id: "s607", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "Em ơi, ra đây, cảnh sát tới hỏi về cậu trai đó kìa.", next: "s608" },
  { id: "s608", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Một người phụ nữ với vóc dáng mảnh mai cùng mái tóc đen tuyền được búi gọn dáng phía sau đầu xuất hiện, giọng nói mềm mại.", next: "s609" },
  { id: "s609", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Vâng, hai anh cảnh sát muốn hỏi về người thuê Axton, phải chứ?", next: "s610" },
  { id: "s610", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Ngồi xuống sofa, Mainson nhẹ nhàng bắt đầu câu hỏi, giọng điềm tĩnh khiến người đàn ông bớt căng thẳng.", next: "s611" },
  { id: "s611", bg: "canhochunhaAxton.jpg", char: "Mainson.png", text: "Chúng tôi muốn biết thời gian gần đây Axton có gặp ai thường xuyên không, có ai tới nhà anh ta không hay có điều gì khác thường xung quanh nạn nhân?", next: "s612" },
  { id: "s612", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "À… cậu ấy hiền lành, ít nói nhưng tử tế. Không bao giờ làm ồn hay gây rắc rối.", next: "s613" },
  { id: "s613", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Cũng có vài lần cậu ta dẫn một cô bé mặc đồng phục học sinh tới, nhưng hai người chỉ ngồi cafe và dạy học thôi.", next: "s614" },
  { id: "s614", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Như đã nắm bắt được điểm khác lạ trong câu chuyện, Heirlock nhìn về hướng người vợ.", next: "s615" },
  { id: "s615", bg: "canhochunhaAxton.jpg", char: "Heirlock.png", text: "Bà có nhớ cô bé đó ngoại hình ra sao không?", next: "s616" },
  { id: "s616", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Để tôi nhớ lại một chút.", next: "s617" },
  { id: "s617", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Bà ta trầm ngâm một hồi rồi chợt à lên một tiếng.", next: "s618" },
  { id: "s618", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Phải rồi, cô bé đó mặc đồng phục trường K.K ở rìa thành phố, với cả có mái tóc màu hồng rất nổi bật nữa. Còn lại tôi cũng không nhớ rõ lắm.", next: "s619" },
  { id: "s619", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Mainson quay sang nhìn Heirlock như ra hiệu, hắn gật đầu. Là Yanna, cô bé ngoan ngoãn bị người thầy giáo Kael đeo bám đó.", next: "s620" },
  { id: "s620", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Vậy là đúng theo những gì Kael nói, thầy Axton và Yanna thực sự thân thiết vượt mức bình thường rất nhiều.", next: "s621" },
  { id: "s621", bg: "canhochunhaAxton.jpg", char: "Heirlock.png", text: "Cô bé đó thường tới khi nào?", next: "s622" },
  { id: "s622", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Tầm sau 17h chiều, có lẽ là giờ tan học chăng. Nhưng cậu ấy không đưa cô bé lên căn hộ đâu, chỉ ra quán cà phê đầu ngõ, ngồi tí rồi thôi.", next: "s623" },
  { id: "s623", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Mainson ghi chép, nhẹ nhàng đặt câu tiếp.", next: "s624" },
  { id: "s624", bg: "canhochunhaAxton.jpg", char: "Mainson.png", text: "Ngoài cô bé đó, Axton có bao giờ đưa ai khác vào nhà không?", next: "s625" },
  { id: "s625", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "Không hề. Cậu ấy sống khép kín, chỉ duy nhất vài lần như thế thôi.", next: "s626" },
  { id: "s626", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Heirlock nghĩ có lẽ đã hết thứ để hỏi ở cặp vợ chồng này rồi thì đột nhiên người chồng như nhớ ra điều gì đó, ồ lên một tiếng.", next: "s627" },
  { id: "s627", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "À, tầm mấy tháng trước Axton có than bị mất chìa khóa căn hộ, phải gọi thợ khóa vì tìm mãi không ra.", next: "s628" },
  { id: "s628", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Mainson nhíu mày, ánh mắt sáng lên sự chú ý, mất chìa khóa và vụ án phòng kín, cuối cùng thì cũng có lời lí giải cho việc Kael bị oan rồi.", next: "s629" },
  { id: "s629", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Nếu như khoảng thời gian Axton mất chìa là do có kẻ chủ đích trộm thì nó hoàn toàn giải thích được cho vụ án căn phòng kín này.", next: "s630" },
  { id: "s630", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "Sau đó tôi cũng có tới tìm giúp cậu ấy, dù sao phải đền chìa cũng mất thời gian và tiền bạc nữa nhưng không thấy, một hôm Axton nói với tôi cậu ấy lại vô tình thấy chìa khóa rơi gần bệ cửa.", next: "s631" },
  { id: "s631", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "Nơi ai cũng nhìn thấy dễ dàng như vậy tại sao trước giờ không ai thấy. Axton bảo không hiểu sao tìm mãi không ra, thậm chí thợ khóa cũng không thấy.", next: "s632" },
  { id: "s632", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Kể từ đó cậu ấy có thêm một chìa khóa mới. Còn cái chìa cũ bây giờ lại xuất hiện.", next: "s633" },
  { id: "s633", bg: "canhochunhaAxton.jpg", char: "Heirlock.png", text: "Vậy là trước vụ án, cậu ấy có hai chìa khóa, một cũ một mới.", next: "s634" },
  { id: "s634", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Người đàn ông lặng đi một chút, ông ta nâng chiếc tách sứ trên bàn lên nhấp một ngụm trà, ánh mắt nhìn ra cửa sổ mưa.", next: "s635" },
  { id: "s635", bg: "canhochunhaAxton.jpg", char: "Người dân 1.png", text: "Axton hiếm khi tiếp xúc với hàng xóm. Cậu ấy lịch sự, yên lặng và chưa hề gây mâu thuẫn gì với ai, cũng chỉ duy nhất có cô bé đó là xuất hiện cùng với cậu ta.", next: "s636" },
  { id: "s636", bg: "canhochunhaAxton.jpg", char: "Narrator", text: "Heirlock nhìn về phía cộng sự của mình để ra hiệu, câu nhanh chóng hiểu ý mà đứng dậy, mỉm cười chìa tay ra.", next: "s637" },
  { id: "s637", bg: "canhochunhaAxton.jpg", char: "Mainson.png", text: "Cảm ơn ông bà đã chia sẻ, những thông tin này rất quan trọng.", next: "s638" },
  { id: "s638", bg: "canhochunhaAxton.jpg", char: "Người dân 2.png", text: "Chúng tôi hy vọng các anh sẽ tìm ra sự thật. Axton là người tốt, cậu ta không đáng xảy ra chuyện này.", next: "s639" },
  { id: "s639", bg: "duongmua.jpg", next: "s640" },
  { id: "s640", bg: "duongmua.jpg", char: "Narrator", text: "Mainson và Heirlock rời khỏi căn hộ, tiếng mưa vẫn rơi đều đặn dội vào tai hắn. Có quá nhiều nghi vấn xung quanh cái chết của Axton, hiện giờ chẳng khác nào phải điều tra lại từ đầu.", next: "s641" },
  { id: "s641", bg: "duongmua.jpg", char: "Narrator", text: "Heirlock nhìn Mainson, ánh mắt trầm tư nhìn trời mưa lất phất bên ngoài.", next: "s642" },
  { id: "s642", bg: "duongmua.jpg", char: "Heirlock.png", text: "Chúng ta nên tới trường, hỏi cô bé Yanna về Axton nhiều hơn, dù sao hiện giờ mối quan hệ duy nhất còn lại của Axton có vẻ thân thiết hơn cũng chỉ còn con bé này.", next: "s643" },
  { id: "s643", bg: "duongmua.jpg", char: "Narrator", text: "Heirlock thở dài một hơi, cuộc điều tra càng cố tiến vào sâu hơn hình như lại càng trở nên bế tắc khiến hắn mệt mỏi.", next: "s644" },
  { id: "s644", bg: "duongmua.jpg", char: "Mainson.png", text: "Đồng ý, mọi mối quan hệ của Axton gần đây đều cần làm rõ, chúng ta sẽ lên trường thôi.", next: "s645" },
  { id: "s645", bg: "duongmua.jpg", char: "Narrator", text: "Heirlock nhìn lên trên, hình như trời đang tạnh mưa, những tia nắng bắt đầu le lói phía sau lớp mây đen mù mịt kia. Hắn nghĩ liệu đây có phải là dấu hiệu tốt hay không.", next: "s646" },
  { id: "s646", bg: "santruong.jpg", next: "s647" },
  { id: "s647", bg: "santruong.jpg", char: "Narrator", text: "Buổi chiều muộn phủ màu vàng nhạt lên sân trường, nắng nghiêng qua hành lang khiến bóng hai người kéo dài trên nền gạch.", next: "s648" },
  { id: "s648", bg: "santruong.jpg", char: "Narrator", text: "Không khí ồn ào xung quanh dường như không thể hòa vào bước chân chậm rãi ấy như thể hắn và cậu đang đi ngược với dòng người nơi này.", next: "s649" },
  { id: "s649", bg: "lophoc.jpg", next: "s650" },
  { id: "s650", bg: "lophoc.jpg", char: "Narrator", text: "Heirlock và Mainson bước vào lớp học, học sinh nhìn họ với ánh mắt tò mò, vài người còn xì xào sau lưng.", next: "s651" },
  { id: "s651", bg: "lophoc.jpg", char: "Heirlock.png", text: "Yanna, em có thể ra ngoài một chút được không, chúng tôi muốn trao đổi vài câu hỏi về Axton.", next: "s652" },
  { id: "s652", bg: "lophoc.jpg", char: "Narrator", text: "Yanna khẽ ngượng khi lại gặp hai vị cảnh sát này thêm một lần nữa liền nhanh chóng bước tới, mái tóc hồng khẽ bay theo làn gió mát sau cơn mưa khiến Heirlock ngây người, trong một chốc hắn cũng hiểu tại sao cô bé này lại được nhiều người yêu quý tới vậy.", next: "s653" },
  { id: "s653", bg: "lophoc.jpg", char: "Narrator", text: "Khuôn mặt xinh đẹp dễ mến, mái tóc hồng ấn tượng, thành tích học tập xuất sắc và cả thứ mùi hương của hoa nhài khó quên ấy cứ lảng vảng xung quanh hắn.", next: "s654" },
  { id: "s654", bg: "lophoc.jpg", char: "Yanna.png", text: "Dạ!", next: "s655" },
  { id: "s655", bg: "lophoc.jpg", char: "Mainson.png", text: "Chúng tôi sẽ lên phòng giáo viên trước, em cứ chuẩn bị ra đó khi xong việc của mình, được không?", next: "s656" },
  { id: "s656", bg: "lophoc.jpg", char: "Yanna.png", text: "Vâng, em sẽ đợi ở ngoài.", next: "s657" },
  { id: "s657", bg: "lophoc.jpg", char: "Narrator", text: "Heirlock và Mainson đi về hướng phòng giáo viên, bước qua hành lang lớp học lác đác tiếng học sinh thì thầm về việc Kael vẫn đang bị tạm giam.", next: "s658" },
  { id: "s658", bg: "lophoc.jpg", char: "Mainson.png", text: "Không khí ở đây căng thẳng hơn dự đoán, có vẻ Kael trở thành tâm điểm mọi ánh mắt rồi, nhất là còn bị học sinh nói ra vào nữa.", next: "s659" },
  { id: "s659", bg: "lophoc.jpg", char: "Heirlock.png", text: "Ừ vậy nên chúng ta phải nhanh chóng giải quyết được vụ án này và trả lại sự trong sạch cho gã ta.", next: "s660" },
  { id: "s660", bg: "lophoc.jpg", char: "Narrator", text: "Heirlock thở dài một tiếng rồi tiếp.", next: "s661" },
  { id: "s661", bg: "lophoc.jpg", char: "Heirlock.png", text: "Kael, gã là một tên ngu ngốc nhưng không thể là kẻ giết người. Lần này nói chuyện với Yanna chúng ta cần biết rõ Axton thời gian gần đây như thế nào, ai tiếp xúc với cậu ấy và có biểu hiện gì lạ không.", next: "s662" },
  { id: "s662", bg: "phonggiaovien.jpg", next: "s663" },
  { id: "s663", bg: "phonggiaovien.jpg", char: "Narrator", text: "Hai người lên tới phòng giáo viên, Heirlock nhìn quanh, căn phòng yên tĩnh hơn lớp học với ánh sáng nhạt hắt qua cửa sổ.", next: "s664" },
  { id: "s664", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Là em, Yanna đây ạ!", next: "s665" },
  { id: "s665", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Yanna à, vào đi!", next: "s666" },
  { id: "s666", bg: "phonggiaovien.jpg", char: "Narrator", text: "Yanna bước vào phòng rồi như một thói quen mà ngồi xuống chiếc ghế ở đối diện với Heirlock, hoàn toàn không liếc mắt nhìn sang Mainson dù chỉ một chút khiến cậu dài mặt tiếc nuối.", next: "s667" },
  { id: "s667", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Dạ, anh cảnh sát và ngài thám tử cần hỏi thêm về chuyện gì ạ?", next: "s668" },
  { id: "s668", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Em có gặp thầy Axton thường xuyên không, thời gian gần đây có điều gì khác thường không, hay cậu ấy có mối quan hệ với ai khác mà em cảm thấy bất thường không?", next: "s669" },
  { id: "s669", bg: "phonggiaovien.jpg", char: "Narrator", text: "Một loạt câu hỏi dồn dập ập tới khiến cô bé tóc hồng choáng váng mà ấp úng làm Heirlock quay sang lườm người cộng sự của mình thay cho lời đe dọa.", next: "s670" },
  { id: "s670", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Em cứ bình tĩnh thôi, thời gian gần đây Axton có biểu hiện gì kì lạ không, như suy sụp, mệt mỏi, sợ hãi hay kể cả hưng phấn chẳng hạn?", next: "s671" },
  { id: "s671", bg: "phonggiaovien.jpg", char: "Narrator", text: "Người con gái ấy suy nghĩ một hồi rồi lắc đầu.", next: "s672" },
  { id: "s672", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Không, thầy Axton vẫn luôn như thế mà.", next: "s673" },
  { id: "s673", bg: "phonggiaovien.jpg", char: "Mainson.png", text: "Vậy mối quan hệ giữa em và thầy Axton thì sao, tôi thấy mỗi khi em nhắc về người thầy này đều lộ ra chút vui vẻ.", next: "s674" },
  { id: "s674", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Em với thầy Axton chỉ là thầy trò thôi, em có tới nhà thầy ấy vì đã nhờ thầy giảng bài nhưng chưa bao giờ có bất kì điều gì vượt qua ngưỡng thầy trò cả.", next: "s675" },
  { id: "s675", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Em có biết Axton có tiếp xúc với ai khác ngoài em không, hay có ai tới nhà thầy ấy gần đây chẳng hạn?", next: "s676" },
  { id: "s676", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Không, em không biết gì, em chỉ gặp thầy ở lớp hoặc quán cà phê, không có gì khác.", next: "s677" },
  { id: "s677", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mainson bận ghi chép cẩn thận, ánh mắt Heirlock quan sát Yanna, dù cho những điều con bé này nói đều khiến hắn cảm thấy sự thẳng thắn và không hề né tránh, với kinh nghiệm của một thám tử lâu năm như Heirlock, hắn có thể chắc rằng những gì Yanna nói ra đều là sự thật.", next: "s678" },
  { id: "s678", bg: "phonggiaovien.jpg", char: "Narrator", text: "Nhưng đằng sau sự thật đó còn những gì thì có vẻ dù cho có gặng hỏi bao nhiêu Yanna cũng sẽ không trả lời.", next: "s679" },
  { id: "s679", bg: "phonggiaovien.jpg", char: "Narrator", text: "Bỗng có tiếng gõ cửa, một cậu học sinh với mái tóc ngắn gọn gàng bước vào, giọng khẩn trương gọi Yanna.", next: "s680" },
  { id: "s680", bg: "phonggiaovien.jpg", char: "Crow.png", text: "Yanna cần về không, nếu trễ chuyến bus là muộn mất.", next: "s681" },
  { id: "s681", bg: "phonggiaovien.jpg", char: "Narrator", text: "Ngay khi vừa nhìn cậu trai kia xuất hiện trước cửa, Yanna liền đứng bật dậy mặc cho Heirlock và Mainson còn chưa kịp phản ứng.", next: "s682" },
  { id: "s682", bg: "phonggiaovien.jpg", char: "Yanna.png", text: "Crow! Em về đây, xin phép hai người, em cần phải về sớm trước khi muộn chuyến bus cuối cùng. Nếu anh cảnh sát và quý ngài thám tử đây cần thêm điều gì xin hãy chọn tới trường vào buổi sáng.", next: "s683" },
  { id: "s683", bg: "phonggiaovien.jpg", char: "Narrator", text: "Yanna quay về phía Heirlock và Mainson, cúi đầu nhẹ rồi rời đi.", next: "s684" },
  { id: "s684", bg: "phonggiaovien.jpg", char: "Narrator", text: "Crow là tên cậu trai vừa rồi toan đi theo nhưng Heirlock đã gọi lại.", next: "s685" },
  { id: "s685", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Crow, cậu là bạn cùng lớp của Yanna?", next: "s686" },
  { id: "s686", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mái tóc ngắn được cắt gọn gàng cùng với khuôn mặt hiền lành và đôi mắt lớn khiến cho thân hình Crow dù có to lớn cũng không gây ra sự căng thẳng cho đối phương.", next: "s687" },
  { id: "s687", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Crow, cậu có biết gì về quan hệ giữa Yanna và Axton không?", next: "s688" },
  { id: "s688", bg: "phonggiaovien.jpg", char: "Narrator", text: "Đôi mắt nâu nhạt ấy khẽ co lại như đang cố gắng nhờ về thứ gì đó nhưng rồi lại lắc đầu.", next: "s689" },
  { id: "s689", bg: "phonggiaovien.jpg", char: "Crow.png", text: "Anh cảnh sát, em chỉ biết Yanna ở trong nhóm thi đua của trường vậy nên thường xuyên nhờ thầy Axton dạy kèm thôi.", next: "s690" },
  { id: "s690", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Vậy Yanna đã ở đâu vào lúc 20h…", next: "s691" },
  { id: "s691", bg: "phonggiaovien.jpg", char: "Narrator", text: "Lời nói của Heirlock còn chưa hết hẳn đã hoàn toàn bị chặn lại bởi ánh mắt soi xét từ phía Crow, cậu ta nhíu mày tỏ vẻ không thoải mái.", next: "s692" },
  { id: "s692", bg: "phonggiaovien.jpg", char: "Crow.png", text: "Ý anh cảnh sát là bạn gái tôi giết thầy Axton, một người giáo viên luôn hết mức giúp đỡ em ấy?", next: "s693" },
  { id: "s693", bg: "phonggiaovien.jpg", char: "Narrator", text: "Mainson vội vàng muốn chữa cháy nhưng đã bị Heirlock ngăn lại, hắn tiếp tục hỏi.", next: "s694" },
  { id: "s694", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Cậu trai trẻ, thám tử và cảnh sát là công việc phải nghi ngờ tất cả những sinh vật sống ở xung quanh nạn nhân, tất nhiên cậu có lựa chọn không đưa ra thông tin nhưng vậy thì cũng phải chấp nhận rằng Yanna có thể có tin đồn giết thầy Axton vì đã không dám chứng minh chứng cứ ngoại phạm.", next: "s695" },
  { id: "s695", bg: "phonggiaovien.jpg", char: "Narrator", text: "Crow nắm chặt lòng bàn tay nhưng rồi rất nhanh trở về trạng thái bình thường mà cười trừ, một tay vò rối tung mái tóc của mình lên.", next: "s696" },
  { id: "s696", bg: "phonggiaovien.jpg", char: "Crow.png", text: "Em không rõ lắm, nhưng chắc chắn Yanna không ở hiện trường khi Axton chết, em ấy rời khỏi đó rồi. Yanna sẽ không đời nào làm những việc như vậy.", next: "s697" },
  { id: "s697", bg: "phonggiaovien.jpg", char: "Narrator", text: "Nói rồi Crow nắm tay Yanna và dẫn cô ra khỏi phòng giáo viên, để lại Heirlock và Mainson.", next: "s698" },
  { id: "s698", bg: "phonggiaovien.jpg", char: "Heirlock.png", text: "Mọi chi tiết tưởng bình thường cũng có thể quan trọng, thời gian và mối quan hệ của Axton cần đối chiếu thêm với nhiều đối tượng nữa, nhưng xem ra quả thực Yanna cũng chẳng có dính dáng gì tới cái chết này rồi.", next: "s699" },
  { id: "s699", bg: "vanphong.jpg", next: "s700" },
  { id: "s700", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock và Mainson ngồi dưới sàn giữa đống hồ sơ, vò đầu bứt tóc giữa đống hồ sơ chồng chất, giấy tờ rải khắp sàn, thông tin thì dày đặc nhưng chẳng có gì thực sự đáng để phân tích hay khai thác, mỗi chi tiết đều dừng lại ở mức suy đoán.", next: "s701" },
  { id: "s701", bg: "vanphong.jpg", char: "Mainson.png", text: "Anh biết không, Kael đã được thả rồi, không còn bị tạm giam nữa.", next: "s702" },
  { id: "s702", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy nghĩa là không còn bằng chứng buộc tội cậu ta nữa.", next: "s703" },
  { id: "s703", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock gật gù, tay vẫn không quên đọc đi đọc lại báo cáo pháp y trên tay như muốn học thuộc từng dấu chấm dấy phẩy trong đó.", next: "s704" },
  { id: "s704", bg: "vanphong.jpg", char: "Mainson.png", text: "Đúng vậy, nhưng ở trường thì vẫn loạn lắm, học sinh thích tin đồn hơn là nghe sự thật, gã ta chưa quay lại dạy mà đang ở nhà tĩnh dưỡng.", next: "s705" },
  { id: "s705", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy là Kael chưa chịu trở lại giảng dạy và mọi chuyện vẫn đang rối ren trong đầu học sinh, phải chứ?", next: "s706" },
  { id: "s706", bg: "vanphong.jpg", char: "Mainson.png", text: "Đúng, tin đồn tràn lan, chẳng ai biết đâu là thật đâu là giả khiến không khí ở trường càng thêm căng thẳng.", next: "s707" },
  { id: "s707", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock mệt mỏi dựa lưng ra phía sau nhưng rồi nhận ra hắn chỉ có thể nằm ngửa ra sàn chứ đã chẳng còn miệng đệm êm ái nào để tựa nữa.", next: "s708" },
  { id: "s708", bg: "vanphong.jpg", char: "Heirlock.png", text: "Chúng ta cần phải tính tới khả năng những tin đồn này sẽ ảnh hưởng tới quá trình điều tra, đặc biệt khi Kael chưa thể trở lại làm việc.", next: "s709" },
  { id: "s709", bg: "vanphong.jpg", char: "Mainson.png", text: "Tôi lo là học sinh còn bàn tán có thể tạo thêm hiểu lầm hoặc suy diễn sai, và mọi chuyện sẽ càng phức tạp.", next: "s710" },
  { id: "s710", bg: "vanphong.jpg", char: "Narrator", text: "Hai người nhìn nhau, im lặng một lúc giữa đống hồ sơ và ánh sáng nhạt hắt qua cửa sổ, cả hai đều hiểu rằng tình hình chưa hề sáng tỏ, Kael vừa được giải thoát khỏi tạm giam nhưng những vấn đề từ vụ án vẫn còn chất đống.", next: "s711" },
  { id: "s711", bg: "vanphong.jpg", char: "Mainson.png", text: "Anh nghĩ sao, Heirlock, chúng ta đã hỏi hết hàng xóm, thẩm vấn Yanna, đối chiếu mọi dữ liệu, nhưng vẫn chẳng tìm ra manh mối gì đáng kể cả.", next: "s712" },
  { id: "s712", bg: "vanphong.jpg", char: "Heirlock.png", text: "Quá nhiều khoảng trống, mỗi manh mối đều ngắn và rời rạc, như một trò chơi xếp hình mà thiếu mất mảnh quan trọng.", next: "s713" },
  { id: "s713", bg: "vanphong.jpg", char: "Narrator", text: "Đột nhiên điện thoại reo, Mainson nhanh tay nhấc máy, giọng trầm trầm của Thượng tá Darius vang lên.", next: "s714" },
  { id: "s714", bg: "vanphong.jpg", char: "Darius.png", text: "Heirlock, Mainson, tôi có tin xấu, Kael vừa chết.", next: "s715" },
  { id: "s715", bg: "vanphong.jpg", char: "Mainson.png", text: "Cái gì, Kael chết sao, không phải cậu ta vừa được thả à?", next: "s716" },
  { id: "s716", bg: "vanphong.jpg", char: "Narrator", text: "Thông tin kì lạ khiến cho cả Heirlock và Mainson đều phải ngạc nhiên mà nhìn nhau như đang muốn đối phương nói rằng mình chỉ đang nghe nhầm thôi, và Kael mới được thả tạm giam 2 ngày trước không thể nào đã chết nhanh như vậy được.", next: "s717" },
  { id: "s717", bg: "vanphong.jpg", char: "Heirlock.png", text: "Kael, gã ta chết vì lí do gì, tại sao lại chết được. Rõ ràng gã vừa mới được thả thôi mà?", next: "s718" },
  { id: "s718", bg: "vanphong.jpg", char: "Narrator", text: "Tiếng thở dài phát ra từ loa của chiếc điện thoại để bàn của Darius làm tâm trí hắn trùng xuống, lại một người chết nữa, rốt cuộc liệu đây có phải là một vụ án giết người hàng loạt đặc biệt nghiêm trọng hay không.", next: "s719" },
  { id: "s719", bg: "vanphong.jpg", char: "Darius.png", text: "Ngã từ ban công tầng 6 xuống, lý do là chứng kiến một cảnh tượng quá đáng sợ.", next: "s720" },
  { id: "s720", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock và Mainson im lặng, mắt nhìn nhau, cả hai đều chưa hiểu cảnh tượng gì mà có thể khiến Kael mất mạng.", next: "s721" },
  { id: "s721", bg: "vanphong.jpg", char: "Heirlock.png", text: "Cảnh tượng kinh hoàng nghĩa là gì? Anh có thể nói rõ hơn được không, Darius?", next: "s722" },
  { id: "s722", bg: "vanphong.jpg", char: "Darius.png", text: "Tôi không thể nói qua điện thoại hết, tốt nhất là các cậu tới hiện trường tự xem thì biết.", next: "s723" },
  { id: "s723", bg: "vanphong.jpg", char: "Narrator", text: "Không hề chần chừ, chẳng ai nói với ai nhưng Heirlock và Mainson gần như đứng bật dậy cùng lúc, hắn vớ lấy chiếc áo khoác dạ của mình còn Mainson nhanh chóng đút cuốn sổ tay điều tra vào túi áo ngực trên chiếc sơ mi.", next: "s724" },
  { id: "s724", bg: "vanphong.jpg", char: "Mainson.png", text: "Ngài thám tử, chúng ta đi thôi.", next: "s725" },
  { id: "s725", bg: "hientruongKael.jpg", next: "s726" },
  { id: "s726", bg: "hientruongKael.jpg", char: "Narrator", text: "Chỉ vài giờ sau, Heirlock và Mainson có mặt tại tòa nhà, tầng trệt mở ra trước mắt họ là một cảnh tượng khủng khiếp, thi thể Kael nằm ngửa dưới sân, thân bị thanh sắt hàng rào xuyên qua, máu lan ra nền xi măng ướt mưa, mọi thứ im lìm mà vẫn đầy ám ảnh.", next: "s727" },
  { id: "s727", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Đây là… Kael?", next: "s728" },
  { id: "s728", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Thật sự là không thể tin nổi, chỉ mới hai ngày trước cậu ta còn ở đây bình thường.", next: "s729" },
  { id: "s729", bg: "hientruongKael.jpg", char: "Narrator", text: "Cảnh tượng mưa rả rích ngoài tòa nhà, ánh sáng nhạt của đèn đường chiếu xuống sân, thi thể Kael nằm đó, lạnh lẽo và vô tình.", next: "s730" },
  { id: "s730", bg: "hientruongKael.jpg", char: "Narrator", text: "Nhìn lên tòa nhà, Heirlock theo ánh mắt Mainson, tầng 12 nơi Kael từng thấy cảnh tượng kinh hoàng, rốt cuộc là thứ gì có thể giết được Kael chỉ vì sợ hãi, nếu như có thì thứ ở trên tầng 12 có lẽ phải rất đáng sợ.", next: "s731" },
  { id: "s731", bg: "hientruongKael.jpg", char: "Narrator", text: "Cả hai bước lên cầu thang, cúi đầu luồn qua tấm bạt màu vàng đánh dấu hiện trường, Mainson xuất trình thẻ cảnh sát và hai người nhanh chóng di chuyển lên trên.", next: "s732" },
  { id: "s732", bg: "hientruongYanna.jpg", next: "s733" },
  { id: "s733", bg: "hientruongYanna.jpg", char: "Narrator", text: "Trước mắt Heirlock khi này là một căn phòng đầy máu, cảnh sát, phóng viên, nhà báo và thậm chí là cả Kam cũng đang có mặt bên cạnh một thi thể đã sớm chẳng còn hình người.", next: "s734" },
  { id: "s734", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock tiến lại gần, định hỏi Kam lần này nạn nhân là ai và lí do tử vong là gì như đã thành thói quen, nhưng rồi những mẩu vụn sợi tóc màu hồng sáng rơi vãi trên sàn nhà cùng mùi hoa nhài ấy khiến hắn sững lại.", next: "s735" },
  { id: "s735", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Mainson, nói cho tôi biết là không phải đi.", next: "s736" },
  { id: "s736", bg: "hientruongYanna.jpg", char: "Narrator", text: "Khi này vị pháp y Kam mới chịu đứng dậy, ngay khi bóng người đàn ông cao lớn ấy di chuyển, không còn che khuất thi thể nữa thì hắn mới được thấy tận mắt hai người đang nằm trên sàn ở ngay chính giữa căn phòng dính đầy máu đỏ tươi như thể được phun sơn.", next: "s737" },
  { id: "s737", bg: "hientruongYanna.jpg", char: "Kam.png", text: "Heirlock của tôi, rất tiếc nhưng tôi sẽ phải nói điều này với anh, thi thể vừa được nhận dạng xong và xác định là Yanna lớp 12A1 trường trung học phổ thông K.K, bên cạnh là một người con trai tên Crow, bạn cùng lớp của Yanna.", next: "s738" },
  { id: "s738", bg: "hientruongYanna.jpg", char: "Narrator", text: "Không khí như trở nên đông đặc khiến đầu óc hắn choáng váng, cô bé tóc hồng mới 2 ngày trước còn nói nếu muốn tìm hiểu thêm về vụ án Axton thì hãy tới buổi sáng ấy giờ đây đã lạnh ngắt trong tình trạng thảm thương.", next: "s739" },
  { id: "s739", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Heirlock, là thật, là Yanna và Crow.", next: "s740" },
  { id: "s740", bg: "chuong2.jpg", next: "s741" },
  { id: "s741", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock và Mainson bước vào căn phòng, ánh sáng mờ chiếu thẳng vào hiện trường, không gian nặng nề, mùi tanh tưởi của máu tươi tràn ngập.", next: "s742" },
  { id: "s742", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Đây là Yanna phải không?", next: "s743" },
  { id: "s743", bg: "hientruongYanna.jpg", char: "Narrator", text: "Mainson ấp úng khi chứng kiến cảnh tượng kinh hoàng trước mặt, cậu cố gắng nhìn sang vị thám tử như muốn tìm cho mình một cái lắc đầu.", next: "s744" },
  { id: "s744", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Rất tiếc phải nói với cậu điều này, Mainson, đó thật sự là con bé.", next: "s745" },
  { id: "s745", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Kh... không thể nào, mới mấy hôm trước, mới 2 ngày trước thôi con bé còn ở trước mắt chúng ta nói chuyện, cười đùa, thậm chí còn có cả một người bạn trai đi cùng.", next: "s746" },
  { id: "s746", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Vậy tại sao bây giờ...", next: "s747" },
  { id: "s747", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock không đáp lời, hắn hiểu tại sao Mainson lại khó khăn để vượt qua sự thật này tới vậy.", next: "s748" },
  { id: "s748", bg: "hientruongYanna.jpg", char: "Narrator", text: "Cậu ta vẫn chỉ là một cảnh sát mới vào nghề được một thời gian ngắn, có lẽ thấy những hiện trường máu me như thế này là hơi quá sức.", next: "s749" },
  { id: "s749", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Mainson, rồi cậu sẽ phải quen với những thứ này sớm thôi. Tới đây xem kĩ thi thể đi, trước khi nó được bên pháp y nhận lại.", next: "s750" },
  { id: "s750", bg: "hientruongYanna.jpg", char: "Narrator", text: "Mainson chậm rãi bước tới từng bước khó nhọc, cậu nhìn lên cái thi thể đã nát tươm đang được ghim trên tường tựa như bức tranh chúa bị đóng trên thánh giá ấy mà mở to mắt.", next: "s751" },
  { id: "s751", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Yanna, hai tay cô bé dang rộng như hình thánh giá, mắt đã bị lấy đi, con ngươi bị vứt trên sàn ngay cạnh chân.", next: "s752" },
  { id: "s752", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Bụng bị rạch thẳng làm cho toàn bộ phần ruột trào ra theo vết rách đó, nội tạng dính lên tường trượt dần xuống đất.", next: "s753" },
  { id: "s753", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Tóc Yanna bị cạo sát, móng tay mất hết, răng và lưỡi cũng không còn nhưng da lại không một vết bầm, anh nghĩ sao về điều này?", next: "s754" },
  { id: "s754", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Mainson, cách đặt cơ thể, cách tổ chức hiện trường, không một chi tiết nào là ngẫu nhiên.", next: "s755" },
  { id: "s755", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock nhìn một hồi rồi ngẫm nghĩ.", next: "s756" },
  { id: "s756", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Cô bé không hề phản kháng, có thể bị vô hiệu hóa từ trước hoặc hung thủ dùng phương pháp nào đó khiến nạn nhân hoàn toàn bất động.", next: "s757" },
  { id: "s757", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Và những thứ còn lại, từng chi tiết nhỏ đều để lại thông điệp nào đó, nhất là thứ kia.", next: "s758" },
  { id: "s758", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock nói rồi nhìn sang vật thể lạ được đặt trên ghế, đối diện với thi thể của Yanna mà thì thầm.", next: "s759" },
  { id: "s759", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Phải rồi, là Crow.", next: "s760" },
  { id: "s760", bg: "hientruongYanna.jpg", char: "Narrator", text: "Mainson rùng mình khi nhận ra thứ được đặt trên ghế, quay về phía Yanna là một cái đầu của cậu trai Crow họ đã gặp ngày hôm ấy, vẫn là mái tóc gọn gàng cùng khuôn mặt quen thuộc kia nhưng chỉ khác là đôi mắt ấy giờ đỡ trợn trừng lên đầy sợ hãi.", next: "s761" },
  { id: "s761", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Đầu bị tách ra khỏi cơ thể và đặt gọn gàng trên ghế, nhìn thẳng về Yanna, da thịt đầy vết bầm, xước, vết cứa nhưng không bị tách rời.", next: "s762" },
  { id: "s762", bg: "hientruongYanna.jpg", char: "Narrator", text: "Cậu cố gắng kiềm chế cơn buồn nôn của bản thân khi phải đối mặt với hiện trường và thi thể ghê rợn này, tiếp lời.", next: "s763" },
  { id: "s763", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Điều này thể hiện sự tàn nhẫn khác biệt với Yanna, tôi không hiểu nổi hung thủ đang muốn nói gì với hai thi thể được đặt trong vị trí kì lạ này.", next: "s764" },
  { id: "s764", bg: "hientruongYanna.jpg", char: "Narrator", text: "Khi này Heirlock nghe một hồi rồi mới lên tiếng, hắn nhìn về phía chiếc đầu tách rời của Crow trên ghế, đặt một cách trịnh trọng ở vị trí bên dưới và đối diện như thể một tín đồ đang tôn thờ vị thần của mình.", next: "s765" },
  { id: "s765", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Hung thủ muốn tạo sự tương phản rõ rệt, Yanna bị trừng phạt cực kỳ man rợ, từng chi tiết đều được tinh chỉnh còn Crow thì lại không có nhiều dấu vết của sự tra tấn tới vậy.", next: "s766" },
  { id: "s766", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Phải, những vết bầm này là do phản ứng chống lại khi tự vệ mà thành, không phải bởi vì bị tra tấn thụ động.", next: "s767" },
  { id: "s767", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Và với từng đó việc hắn ta đã làm với thi thể và vẫn còn đủ thời gian để che giấu vết tích hiện trường tới nỗi không một ai biết cho tới khi Kael xuất hiện thì không thể là hành động bộc phát.", next: "s768" },
  { id: "s768", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Rõ ràng có thể thấy, hung thủ nhắm tới việc trả thù Yanna, còn cậu trai Crow này...", next: "s769" },
  { id: "s769", bg: "hientruongYanna.jpg", char: "Narrator", text: "Hắn ngưng lại một lúc rồi thở dài.", next: "s770" },
  { id: "s770", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Có lẽ chỉ là vô tình bị cuốn vào thôi, vết thương trên người cậu ta hầu hết là vết cào do móng tay và bị vật sắc nhọn cứa vào, nhưng nhìn này.", next: "s771" },
  { id: "s771", bg: "hientruongYanna.jpg", char: "Narrator", text: "Nói rồi Heirlock chỉ vào vết xước gần tay của Crow.", next: "s772" },
  { id: "s772", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Là sơn hồng?", next: "s773" },
  { id: "s773", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Đúng vậy, móng tay hồng, Mainson, cậu nghĩ xem là ai gây ra vết thương này.", next: "s774" },
  { id: "s774", bg: "hientruongYanna.jpg", char: "Narrator", text: "Mainson quay ngoắt lại phía sau nhìn vào phần thi thể đang bị ghim trên tường, mấp máy.", next: "s775" },
  { id: "s775", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Là... Yanna?", next: "s776" },
  { id: "s776", bg: "hientruongYanna.jpg", char: "Narrator", text: "Heirlock gật đầu.", next: "s777" },
  { id: "s777", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Đúng, đó là móng tay của Yanna, chắc cậu cũng nhớ rồi chứ, hôm trước mình nói chuyện với cô bé, nó đã có cái móng tay hồng này rồi.", next: "s778" },
  { id: "s778", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Với cả xem kỹ này, các chi tiết nhỏ như móng tay bị cắt sạch, tóc cạo sát, răng và lưỡi của Yanna biến mất là hung thủ muốn tước đi hết dấu hiệu nhận dạng.", next: "s779" },
  { id: "s779", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Nhưng tên đó vẫn giữ nguyên da không một vết bầm cho thấy kỹ năng chuyên nghiệp, hoặc ít nhất, tên đó có khả năng khống chế con bé tuyệt đối khi tra tấn", next: "s780" },
  { id: "s780", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Nhìn Crow, đầu bị chặt ra nhưng được đặt gọn trên ghế, hai mắt mở to nhìn về phía Yanna, cơ thể đầy bầm tím nhiều vết xước và vết cứ.", next: "s781" },
  { id: "s781", bg: "hientruongYanna.jpg", char: "Mainson.png", text: "Ngoài trừ phần đầu thì cơ thể không bị tách rời bất cứ bộ phận nào khác, nghĩa là hung thủ không muốn khiến Crow đau đớn thống khổ, phải chứ, dù sao mất đầu là cũng mất ý thức rồi.", next: "s782" },
  { id: "s782", bg: "hientruongYanna.jpg", char: "Heirlock.png", text: "Chính xác, chúng ta đang đứng trước kịch bản có chủ đích, Mainson, từng chi tiết, từng vết máu và từng vị trí thi thể đều cho thấy sự tinh vi và sự sắp xếp chuẩn xác, hung thủ muốn khiến những người còn sống, kể cả chúng ta phải sợ hãi và rối loạn tinh thần.", next: "s783" },
  { id: "s783", bg: "hientruongYanna.jpg", char: "Narrator", text: "Hai người đứng giữa phòng, mắt quét khắp hiện trường, yên lặng mà căng thẳng, mùi tanh sắt và máu khiến không khí nặng nề, từng chi tiết đều là manh mối cho những suy đoán tiếp theo", next: "s784" },
  { id: "s784", bg: "hientruongYanna.jpg", char: "Narrator", text: "Hiện giờ có lẽ họ nên tới xem thi thể của Kael, kẻ đã bị dọa bởi cảnh tượng này tới mức sợ hãi ngã ra khỏi ban công.", next: "s785" },
  { id: "s785", bg: "hientruongKael.jpg", next: "s786" },
  { id: "s786", bg: "hientruongKael.jpg", char: "Narrator", text: "Heirlock và Mainson bước tới hiện trường ban công nơi Kael gặp nạn, ánh sáng sớm chiếu qua từng kẽ lá, tiếng gió xào xạc bên tai khiến khung cảnh quá đỗi yên bình, trái ngược hoàn toàn với những gì xảy ra trước mắt hắn.", next: "s787" },
  { id: "s787", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Kael rơi từ đây, tầm cao là 12 tầng trúng hàng rào sắt nhọn phía dưới, tử vong chủ yếu là do mất máu, mắt mở to, rõ ràng sợ hãi đến cùng cực trước khi chết, không có dấu hiệu tác động từ người khác và chỉ có duy nhất dấu chân của Kael quanh ban công lần hiện trường nơi Yanna và Crow chết.", next: "s788" },
  { id: "s788", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Tất cả dấu vết khác đều được che giấu, không một dấu chân hay vết di chuyển nào khác, ngay cả những mảnh vụn nhỏ, mọi thứ đều như thể hung thủ không muốn để lại bất kỳ dấu tích nào cho cảnh sát vậy.", next: "s789" },
  { id: "s789", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Trái ngược hoàn toàn với vụ án của Axton, hung thủ đã để lại rất nhiều dấu vết, thậm chí còn không quên đặt thêm dấu giày và cuốn sổ tay để mọi nghi ngờ nhắm vào Kael nữa.", next: "s790" },
  { id: "s790", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Nhìn quanh không có vật dụng nào bị xáo trộn, lan can, khung cửa, tất cả đều nguyên vẹn, chỉ có Kael là nạn nhân duy nhất, điều này nói lên rằng hắn không phải là kẻ bị ép mà là người chứng kiến cảnh tượng quá kinh hoàng khiến mất thăng bằng và rơi xuống.", next: "s791" },
  { id: "s791", bg: "hientruongKael.jpg", char: "Narrator", text: "Heirlock trầm ngâm một lúc, biết đâu hung thủ có thể chủ động đẩy Kael xuống nhưng rồi xóa đi mọi dấu vết chăng, nhưng điều đó quá khó, vết máu trên quần áo, vân tay, dấu giày dính máu, quá nhiều thứ để có thể dọn dẹp hoàn hảo không tì vết tới nỗi cảnh sát cũng tìm chẳng ra như hiện tại.", next: "s792" },
  { id: "s792", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Kael thể hiện rõ sự sợ hãi qua cách mắt mở to, cơ thể co giật khi va chạm với hàng rào và một điều nữa, Mainson, hung thủ đã tính toán kỹ, che giấu mọi dấu vết, chứng tỏ hắn hiểu rõ các bước điều tra để có thể không để lại bất cứ một sơ hở nào như vậy.", next: "s793" },
  { id: "s793", bg: "hientruongKael.jpg", char: "Narrator", text: "Hai người đứng im giữa ban công, mắt quét quanh khu vực, chỉ còn lại cơn gió lạnh và mùi sắt tanh nhẹ thoang thoảng trong không khí.", next: "s794" },
  { id: "s794", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Chúng ta quay lại văn phòng thôi, Heirlock, tôi sẽ gọi điện xác minh thêm một lượt nữa.", next: "s795" },
  { id: "s795", bg: "hientruongKael.jpg", char: "Narrator", text: "Heirlock gật đầu, hiện giờ cũng đã có thể coi là xong việc ở hiện trường rồi, phần còn lại là công đoạn của Kam, pháp y cùng với những đội điều tra khác.", next: "s796" },
  { id: "s796", bg: "vanphong.jpg", next: "s797" },
  { id: "s797", bg: "vanphong.jpg", char: "Mainson.png", text: "Alo, tôi vừa tới hiện trường Kael, kiểm tra kỹ xung quanh, không còn dấu tích nào của người thứ ba, đúng là tất cả đều đã bị xóa, khu nhà đó là nhà của Crow, dạng nhà thuê, Yanna từng ở chung với Crow, trước đó cô bé thuê nhà ở khu UMF, tất cả khớp với những gì chúng ta đoán.", next: "s798" },
  { id: "s798", bg: "vanphong.jpg", char: "Narrator", text: "Mainson ngắt cuộc điện thoại, vẻ mặt nghiêm túc nhìn về phía vị thám tử.", next: "s799" },
  { id: "s799", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy thì mọi chuyện càng phức tạp hơn, hung thủ không chỉ dọn sạch dấu vết ở hiện trường Kael mà còn biết rõ mối quan hệ giữa Yanna và Crow.", next: "s800" },
  { id: "s800", bg: "vanphong.jpg", char: "Heirlock.png", text: "Nhưng một câu hỏi khiến tôi đặt ra, sao Kael lại tới nơi ở của Crow?", next: "s801" },
  { id: "s801", bg: "vanphong.jpg", char: "Mainson.png", text: "Tôi có hỏi mọi người trong trại tạm giam, thấy bảo gã vừa được thả đi đã lập tức đòi tìm kiếm Yanna để chất vấn vì tại cô bé mà gã mới nằm trong diện tình nghi. Nhưng rồi kết cục của cuộc chất vấn đó ra sao thì chúng ta đều biết rồi.", next: "s802" },
  { id: "s802", bg: "vanphong.jpg", char: "Heirlock.png", text: "Bằng một cái chết đầy thảm thương, khổ thân Kael của tôi.", next: "s803" },
  { id: "s803", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock cười nhạt, hắn nắm chặt lòng bàn tay, tự trách bản thân vô dụng nên mới không thể bảo vệ được người vô tội như Kael, dù cho gã là một người sốc nổi, bốc đồng và nóng nẩy nhưng gã chưa từng làm điều gì vi phạm pháp luật.", next: "s804" },
  { id: "s804", bg: "vanphong.jpg", char: "Narrator", text: "Vậy mà giờ đây chỉ vì chút sai sót của hắn, Kael đã không còn trên cuộc đời này nữa.", next: "s805" },
  { id: "s805", bg: "vanphong.jpg", char: "Mainson.png", text: "Có lẽ cái chết của Kael là do còn đang định hủng hổ xông vào chất vấn thì nhìn thấy cảnh tượng này nên ngã chết chăng?", next: "s806" },
  { id: "s806", bg: "vanphong.jpg", char: "Heirlock.png", text: "Không thể nào, một người bình thường bước vào từ cửa lớn, dù có hoảng sợ và rối loạn thì cũng ngã khụy xuống sàn chứ không thể lùi về tận phía ban công để ngã xuống được. Trừ khi…", next: "s807" },
  { id: "s807", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock dừng lại một lúc khiến Mainson tò mò, cậu nhìn lên hắn như đang chờ đợi câu trả lời.", next: "s808" },
  { id: "s808", bg: "vanphong.jpg", char: "Heirlock.png", text: "Trừ khi lúc Kael đến, hung thủ vẫn còn ở đó và đang lau dọn hiện trường, hoặc đang ra tay với nạn nhân nên gã mới hoảng loạn tới vậy.", next: "s809" },
  { id: "s809", bg: "vanphong.jpg", char: "Narrator", text: "Mainson rùng mình, như thế có một luồng điện chạy dọc sống lưng cậu, quả thực những gì Heirlock nói rất đúng, vậy Kael trước khi chết đã thực sự thấy được mặt của hung thủ, có lẽ từng mang suy nghĩ trốn chạy nhưng bị kẻ kia dồn tới mức hoảng loạn mà đi sang ban công rồi ngã xuống.", next: "s810" },
  { id: "s810", bg: "vanphong.jpg", char: "Heirlock.png", text: "Nếu như vậy thì khó rồi đây, người có liên kết với Axton và Yanna quá nhiều, thậm chí nói cả ngôi trường K.K đều là nghi phạm cũng chẳng sai.", next: "s811" },
  { id: "s811", bg: "vanphong.jpg", char: "Narrator", text: "Một cuộc gọi tới, đầu dây bên kia vang lên giọng nói khàn đặc.", next: "s812" },
  { id: "s812", bg: "vanphong.jpg", char: "Darius.png", text: "Hai người có ở đó chứ, thám tử Heirlock?", next: "s813" },
  { id: "s813", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock nhíu mày, hắn không thích trao đổi với tên thượng tá này bởi ông ta luôn là kẻ tham thành tích, vụ án của Axton trước đó cũng hướng vào việc nhanh nhanh chóng chóng kết tội Kael để có được thưởng chứ chẳng thực sự quan tâm tới sự thật phía sau.", next: "s814" },
  { id: "s814", bg: "vanphong.jpg", char: "Heirlock.png", text: "Có chuyện gì vậy, ngài thượng tá?", next: "s815" },
  { id: "s815", bg: "vanphong.jpg", char: "Darius.png", text: "Heirlock, cậu mau chóng tìm ra hung thủ và kết thúc vụ án đi, nếu kéo dài lâu bên trên sẽ đánh giá xấu cục của tôi mất.", next: "s816" },
  { id: "s816", bg: "vanphong.jpg", char: "Narrator", text: "Hắn còn đang định lên tiếng phản bác Darius thì đã bị ông ta chặn lại.", next: "s817" },
  { id: "s817", bg: "vanphong.jpg", char: "Darius.png", text: "Phải rồi, bên pháp y có kết quả giám định rồi đấy, hai người có thể qua.", next: "s818" },
  { id: "s818", bg: "vanphong.jpg", char: "Narrator", text: "Cuộc gọi nhanh chóng kết thúc, Mainson nhìn về phía Heirlock rồi thở dài.", next: "s819" },
  { id: "s819", bg: "vanphong.jpg", char: "Mainson.png", text: "Xin lỗi anh, thượng tá vẫn luôn như vậy, ông ấy có chút…", next: "s820" },
  { id: "s820", bg: "vanphong.jpg", char: "Heirlock.png", text: "Thôi được rồi, không quan trọng lắm, mặc kệ ông ta là được. Giờ thì cậu đi điều tra thêm về những mối quan hệ xung quanh Yanna và Crow trong trường đi, biết đâu thêm được manh mối gì đó còn tôi sẽ tới phòng pháp y xem sao.", next: "s821" },
  { id: "s821", bg: "vanphong.jpg", char: "Narrator", text: "Hắn thở dài, tiếp tục.", next: "s822" },
  { id: "s822", bg: "vanphong.jpg", char: "Heirlock.png", text: "Dù sao cũng bị giục rồi, chúng ta không thể chậm trễ nữa.", next: "s823" },
  { id: "s823", bg: "hientruongKael.jpg", next: "s824" }, 
  { id: "s824", bg: "hientruongKael.jpg", char: "Narrator", text: "Heirlock và Mainson bước tới hiện trường ban công nơi Kael gặp nạn, ánh sáng sớm chiếu qua từng kẽ lá, tiếng gió xào xạc bên tai khiến khung cảnh quá đỗi yên bình, trái ngược hoàn toàn với những gì đay xảy ra trước mắt hắn.", next: "s825" },
  { id: "s825", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Kael rơi từ đây, tầm cao là 12 tầng trúng hàng rào sắt nhọn phía dưới, tử vong chủ yếu là do mất máu, mắt mở to, rõ ràng sợ hãi đến cùng cực trước khi chết, không có dấu hiệu tác động từ người khác và chỉ có duy nhất dấu chân của Kael quanh ban công lần hiện trường nơi Yanna và Crow chết.", next: "s826" },
  { id: "s826", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Tất cả dấu vết khác đều được che giấu, không một dấu chân hay vết di chuyển nào khác, ngay cả những mảnh vụn nhỏ, mọi thứ đều như thể hung thủ không muốn để lại bất kỳ dấu tích nào cho cảnh sát vậy.", next: "s827" },
  { id: "s827", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Trái ngược hoàn toàn với vụ án của Axton, hung thủ đã để lại rất nhiều dấu vết, thậm chí còn không quên đặt thêm dấu giày và cuốn sổ tay để mọi nghi ngờ nhắm vào Kael nữa.", next: "s828" },
  { id: "s828", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Nhìn quanh không có vật dụng nào bị xáo trộn, lan can, khung cửa, tất cả đều nguyên vẹn, chỉ có Kael là nạn nhân duy nhất, điều này nói lên rằng hắn không phải là kẻ bị ép mà là người chứng kiến cảnh tượng quá kinh hoàng khiến mất thăng bằng và rơi xuống.", next: "s829" },
  { id: "s829", bg: "hientruongKael.jpg", char: "Narrator", text: "Heirlock trầm ngâm một lúc, biết đâu hung thủ có thể chủ động đẩy Kael xuống nhưng rồi xóa đi mọi dấu vết chăng, nhưng điều đó quá khó, vết máu trên quần áo, vân tay, dấu giày dính máu, quá nhiều thứ để có thể dọn dẹp hoàn hảo không tì vết tới nỗi cảnh sát cũng tìm chẳng ra như hiện tại.", next: "s830" },
  { id: "s830", bg: "hientruongKael.jpg", char: "Heirlock.png", text: "Kael thể hiện rõ sự sợ hãi qua cách mắt mở to, cơ thể co giật khi va chạm với hàng rào và một điều nữa, Mainson, hung thủ đã tính toán kỹ, che giấu mọi dấu vết, chứng tỏ hắn hiểu rõ các bước điều tra để có thể không để lại bất cứ một sơ hở nào như vậy.", next: "s831" },
  { id: "s831", bg: "hientruongKael.jpg", char: "Narrator", text: "Hai người đứng im giữa ban công, mắt quét quanh khu vực, chỉ còn lại cơn gió lạnh và mùi sắt tanh nhẹ thoang thoảng trong không khí.", next: "s832" },
  { id: "s832", bg: "hientruongKael.jpg", char: "Mainson.png", text: "Chúng ta quay lại văn phòng thôi, Heirlock, tôi sẽ gọi điện xác minh thêm một lượt nữa.", next: "s833" },
  { id: "s833", bg: "vanphong.jpg", next: "s834" },
  { id: "s834", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock gật đầu, hiện giờ cũng đã có thể coi là xong việc ở hiện trường rồi, phần còn lại là công đoạn của Kam, pháp y cùng với những đội điều tra khác.", next: "s835" },
  { id: "s835", bg: "vanphong.jpg", char: "Mainson.png", text: "Alo, tôi vừa tới hiện trường Kael, kiểm tra kỹ xung quanh, không còn dấu tích nào của người thứ ba, đúng là tất cả đều đã bị xóa, khu nhà đó là nhà của Crow, dạng nhà thuê, Yanna từng ở chung với Crow, trước đó cô bé thuê nhà ở khu UMF, tất cả khớp với những gì chúng ta đoán.", next: "s836" },
  { id: "s836", bg: "vanphong.jpg", char: "Narrator", text: "Mainson ngắt cuộc điện thoại, vẻ mặt nghiêm túc nhìn về phía vị thám tử.", next: "s837" },
  { id: "s837", bg: "vanphong.jpg", char: "Heirlock.png", text: "Vậy thì mọi chuyện càng phức tạp hơn, hung thủ không chỉ dọn sạch dấu vết ở hiện trường Kael mà còn biết rõ mối quan hệ giữa Yanna và Crow.", next: "s838" },
  { id: "s838", bg: "vanphong.jpg", char: "Heirlock.png", text: "Nhưng một câu hỏi khiến tôi đặt ra, sao Kael lại tới nơi ở của Crow?", next: "s839" },
  { id: "s839", bg: "vanphong.jpg", char: "Mainson.png", text: "Tôi có hỏi mọi người trong trại tạm giam, thấy bảo gã vừa được thả đi đã lập tức đòi tìm kiếm Yanna để chất vấn vì tại cô bé mà gã mới nằm trong diện tình nghi. Nhưng rồi kết cục của cuộc chất vấn đó ra sao thì chúng ta đều biết rồi.", next: "s840" },
  { id: "s840", bg: "vanphong.jpg", char: "Heirlock.png", text: "Bằng một cái chết đầy thảm thương, khổ thân Kael của tôi.", next: "s841" },
  { id: "s841", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock cười nhạt, hắn nắm chặt lòng bàn tay, tự trách bản thân vô dụng nên mới không thể bảo vệ được người vô tội như Kael, dù cho gã là một người sốc nổi, bốc đồng và nóng nẩy nhưng gã chưa từng làm điều gì vi phạm pháp luật.", next: "s842" },
  { id: "s842", bg: "vanphong.jpg", char: "Narrator", text: "Vậy mà giờ đây chỉ vì chút sai sót của hắn, Kael đã không còn trên cuộc đời này nữa.", next: "s843" },
  { id: "s843", bg: "vanphong.jpg", char: "Mainson.png", text: "Có lẽ cái chết của Kael là do còn đang định hủng hổ xông vào chất vấn thì nhìn thấy cảnh tượng này nên ngã chết chăng?", next: "s844" },
  { id: "s844", bg: "vanphong.jpg", char: "Heirlock.png", text: "Không thể nào, một người bình thường bước vào từ cửa lớn, dù có hoảng sợ và rối loạn thì cũng ngã khụy xuống sàn chứ không thể lùi về tận phía ban công để ngã xuống được. Trừ khi…", next: "s845" },
  { id: "s845", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock dừng lại một lúc khiến Mainson tò mò, cậu nhìn lên hắn như đang chờ đợi câu trả lời.", next: "s846" },
  { id: "s846", bg: "vanphong.jpg", char: "Heirlock.png", text: "Trừ khi lúc Kael đến, hung thủ vẫn còn ở đó và đang lau dọn hiện trường, hoặc đang ra tay với nạn nhân nên gã mới hoảng loạn tới vậy.", next: "s847" },
  { id: "s847", bg: "vanphong.jpg", char: "Narrator", text: "Mainson rùng mình, như thế có một luồng điện chạy dọc sống lưng cậu, quả thực những gì Heirlock nói rất đúng, vậy Kael trước khi chết đã thực sự thấy được mặt của hung thủ, có lẽ từng mang suy nghĩ trốn chạy nhưng bị kẻ kia dồn tới mức hoảng loạn mà đi sang ban công rồi ngã xuống.", next: "s848" },
  { id: "s848", bg: "vanphong.jpg", char: "Heirlock.png", text: "Nếu như vậy thì khó rồi đây, người có liên kết với Axton và Yanna quá nhiều, thậm chí nói cả ngôi trường K.K đều là nghi phạm cũng chẳng sai.", next: "s849" },
  { id: "s849", bg: "vanphong.jpg", char: "Narrator", text: "Một cuộc gọi tới, đầu dây bên kia vang lên giọng nói khàn đặc.", next: "s850" },
  { id: "s850", bg: "vanphong.jpg", char: "Darius.png", text: "Hai người có ở đó chứ, thám tử Heirlock?", next: "s851" },
  { id: "s851", bg: "vanphong.jpg", char: "Narrator", text: "Heirlock nhíu mày, hắn không thích trao đổi với tên thượng tá này bởi ông ta luôn là kẻ tham thành tích, vụ án của Axton trước đó cũng hướng vào việc nhanh chóng kết tội Kael để có được thưởng chứ chẳng thực sự quan tâm tới sự thật phía sau.", next: "s852" },
  { id: "s852", bg: "vanphong.jpg", char: "Heirlock.png", text: "Có chuyện gì vậy, ngài thượng tá?", next: "s853" },
  { id: "s853", bg: "vanphong.jpg", char: "Darius.png", text: "Heirlock, cậu mau chóng tìm ra hung thủ và kết thúc vụ án đi, nếu kéo dài lâu bên trên sẽ đánh giá xấu cục của tôi mất.", next: "s854" },
  { id: "s854", bg: "vanphong.jpg", char: "Narrator", text: "Hắn còn đang định lên tiếng phản bác Darius thì đã bị ông ta chặn lại.", next: "s855" },
  { id: "s855", bg: "vanphong.jpg", char: "Darius.png", text: "Phải rồi, bên pháp y có kết quả giám định rồi đấy, hai người có thể qua.", next: "s856" },
  { id: "s856", bg: "vanphong.jpg", char: "Narrator", text: "Cuộc gọi nhanh chóng kết thúc, Mainson nhìn về phía Heirlock rồi thở dài.", next: "s857" },
  { id: "s857", bg: "vanphong.jpg", char: "Mainson.png", text: "Xin lỗi anh, thượng tá vẫn luôn như vậy, ông ấy có chút…", next: "s858" },
  { id: "s858", bg: "vanphong.jpg", char: "Heirlock.png", text: "Thôi được rồi, không quan trọng lắm, mặc kệ ông ta là được. Giờ thì cậu đi điều tra thêm về những mối quan hệ xung quanh Yanna và Crow trong trường đi, biết đâu thêm được manh mối gì đó còn tôi sẽ tới phòng pháp y xem sao.", next: "s859" },
  { id: "s859", bg: "vanphong.jpg", char: "Narrator", text: "Hắn thở dài, tiếp tục.", next: "s860" },
  { id: "s860", bg: "vanphong.jpg", char: "Heirlock.png", text: "Dù sao cũng bị giục rồi, chúng ta không thể chậm trễ nữa.", next: "s861" },
  { id: "s861", bg: "phongphapy.jpg", next: "s862" },
  { id: "s862", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock bước vào phòng pháp y, ánh sáng huỳnh quang lạnh lẽo phủ khắp mặt bàn kính, mùi kháng sinh trộn với tanh của máu khô khiến không khí nặng nề đến nghẹt thở.", next: "s863" },
  { id: "s863", bg: "phongphapy.jpg", char: "Narrator", text: "Chỉ còn tiếng đồng hồ tích tắc nhịp nhàng, người đàn ông với mái tóc đen cùng cặp kính đang ngồi trên chiếc ghế sắt giữa căn phòng, tay chậm rãi lật từng trang giấy.", next: "s864" },
  { id: "s864", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Kam.", next: "s865" },
  { id: "s865", bg: "phongphapy.jpg", char: "Narrator", text: "Anh ngẩng lên nhìn về phía hắn rồi mỉm cười, đặt xấp tài liệu xuống.", next: "s866" },
  { id: "s866", bg: "phongphapy.jpg", char: "Kam.png", text: "Chào vị thám tử đại tài, tôi đã phân tích xong hiện trường Yanna và Crow. Anh muốn tôi đi theo thứ tự nạn nhân nào trước?", next: "s867" },
  { id: "s867", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock rùng mình, lại là cảm giác khó chịu kì lạ này, rõ ràng trước kia khi tiếp xúc với Kam, hắn hoàn toàn không nhận ra ánh mắt của người đàn ông trước mặt lại sắc lạnh đến vậy.", next: "s868" },
  { id: "s868", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Đừng gọi tôi như vậy.", next: "s869" },
  { id: "s869", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Bắt đầu với Crow đi, tôi muốn nghe thật chi tiết từng vết thương và vết bầm trên người cậu ta.", next: "s870" },
  { id: "s870", bg: "phongphapy.jpg", char: "Narrator", text: "Kam gật đầu, anh chỉ vào vết cắt trên cổ nạn nhân, chậm rãi nói.", next: "s871" },
  { id: "s871", bg: "phongphapy.jpg", char: "Kam.png", text: "Cổ Crow bị cắt nham nhở, vết cắt không gọn gàng, so với vụ án Axton hoàn toàn khác. Chứng tỏ người thực hiện không chuyên, hành vi thiếu kỹ năng.", next: "s872" },
  { id: "s872", bg: "phongphapy.jpg", char: "Kam.png", text: "Trên cơ thể nạn nhân có các vết bầm khắp lưng, ngực, vai đều là do Yanna gây ra, DNA đã xác nhận điều này.", next: "s873" },
  { id: "s873", bg: "phongphapy.jpg", char: "Kam.png", text: "Có lẽ khi Crow ôm Yanna vào lòng trong lúc bị tấn công, cô bé đã vô thức bám chặt vào cậu nên tạo ra các vết bầm này.", next: "s874" },
  { id: "s874", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock nhìn vào còn một vài vết xước cong cong của dấu vết móng tay cùng chút sơn hồng nhạt ở trên rồi gật đầu.", next: "s875" },
  { id: "s875", bg: "phongphapy.jpg", char: "Kam.png", text: "Những vết bầm chỉ xuất hiện do phản xạ của Yanna, không phải do hung thủ tra tấn.", next: "s876" },
  { id: "s876", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Được rồi, còn Yanna thì sao?", next: "s877" },
  { id: "s877", bg: "phongphapy.jpg", char: "Kam.png", text: "Yanna thì lại khác hẳn, vết rạch bụng quá gọn, rất chuyên nghiệp, chứng tỏ người giết nạn nhân không phải là kẻ đã ra tay với Crow.", next: "s878" },
  { id: "s878", bg: "phongphapy.jpg", char: "Kam.png", text: "Máu đông lại thành vệt thẫm cho thấy thời gian ra tay khá sớm, đủ để cô bé còn giữ được tỉnh táo để ra tay với bạn trai mình.", next: "s879" },
  { id: "s879", bg: "phongphapy.jpg", char: "Kam.png", text: "Các bộ phận như răng, lưỡi, móng tay và tóc bị lấy đi tạo thành vệt máu đậm màu hơn cả vết thương trên người Crow.", next: "s881" },
  { id: "s881", bg: "phongphapy.jpg", char: "Narrator", text: "Anh ngừng một lúc rồi tiếp lời.", next: "s882" },
  { id: "s882", bg: "phongphapy.jpg", char: "Kam.png", text: "Chứng tỏ Crow bị ép nhìn cảnh Yanna bị tra tấn, trên người cậu ta có vết dây thừng, chắc là đã bị trói vào ghế để nhìn Yanna bị hung thủ hành hạ.", next: "s883" },
  { id: "s883", bg: "phongphapy.jpg", char: "Kam.png", text: "Rồi không biết vì lí do gì, con bé được hung thủ trả sự tự do, và giết bạn trai mình bằng một cái cưa cùn.", next: "s884" },
  { id: "s884", bg: "phongphapy.jpg", char: "Narrator", text: "Nói rồi Kam chỉ vào chiếc cưa tay với phần lưỡi đã bị mẻ, gỉ sắt ấy.", next: "s885" },
  { id: "s885", bg: "phongphapy.jpg", char: "Kam.png", text: "Là nó, hung khi kết liễu cuộc đời của Crow, còn sau đó tôi cũng không rõ tại sao Yanna lại bị hung thủ treo lên trên tường và rạch bụng, chảy máu cho tới chết.", next: "s886" },
  { id: "s886", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Yanna... làm theo lời hung thủ?", next: "s887" },
  { id: "s887", bg: "phongphapy.jpg", char: "Narrator", text: "Kam gật đầu, quả thực chính Heirlock cũng không hiểu tại sao từ đầu tới cuối chỉ có Crow là có dấu vết của việc bị hạn chế hành động trong khi Yanna hoàn toàn chưa từng bị trói.", next: "s888" },
  { id: "s888", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Tại sao nhỉ, hay là con bé có quen biết với hung thủ?", next: "s889" },
  { id: "s889", bg: "phongphapy.jpg", char: "Kam.png", text: "Cũng có khả năng này, tuy nhiên người đó nắm được thứ gì mới có thể điều khiển được nạn nhân đây?", next: "s890" },
  { id: "s890", bg: "phongphapy.jpg", char: "Narrator", text: "Hắn yên lặng suy nghĩ, có lẽ kẻ đó phải nắm giữ một bí mật rất quan trọng với con bé thì mới có thể khiến cho Yanna tự tay giết bạn trai mình rồi để mặc cho người đó rạch bụng bản thân, trói trên tường mất máu tới khi chết.", next: "s891" },
  { id: "s891", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Thế thời gian tử vong hai người thì sao?", next: "s892" },
  { id: "s892", bg: "phongphapy.jpg", char: "Kam.png", text: "Crow mất lúc 23h11, Yanna 1h43 sáng. Yanna chịu tra tấn trước sự chứng kiến của bạn trai, tự tay giết cậu ta và rồi bị rạch bụng sau khi Crow chết.", next: "s893" },
  { id: "s893", bg: "phongphapy.jpg", char: "Kam.png", text: "Nguyên nhân tử vong của cả hai đều do mất máu, có lẽ hiện trường hỗn loạn dấu vân tay trên chiếc cưa đó là do Yanna phải giết bạn trai, nhưng khi làm thì quá run vậy nên Crow đã tự tay cầm cưa, giúp con bé đó một đoạn, anh xem này.", next: "s894" },
  { id: "s894", bg: "phongphapy.jpg", char: "Narrator", text: "Nói rồi Kam chỉ vào vết cắt gọn gàng ở phần đầu của lớp da dày bên ngoài trên chiếc cổ Crow.", next: "s895" },
  { id: "s895", bg: "phongphapy.jpg", char: "Kam.png", text: "Đoạn đầu gọn gàng là do Crow đã tự cứa, sau đó có lẽ mất máu nên không còn sức lực nên Yanna đã làm nốt phần còn lại nên mới nham nhở tới vậy.", next: "s896" },
  { id: "s896", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock im lặng, hắn nhìn về phía vết cắt loạn xạ trên thi thể trước mắt.", next: "s897" },
  { id: "s897", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Vậy hung thủ thao túng cả hai, cả về hành vi và thời gian, kẻ đó sắp xếp đủ kĩ lưỡng để 2 nạn nhân tàn sát lẫn nhau, và tự nguyện chờ đợi cái chết.", next: "s898" },
  { id: "s898", bg: "phongphapy.jpg", char: "Kam.png", text: "Đúng. Cả hai được xếp hình thức, có thể là thông điệp hay sự trả thù. Về Kael tử vong lúc 7h32 sáng, nghĩa là sáng hôm sau tới hiện trường, chứng kiến cảnh tượng kinh hoàng và ngã từ ban công, và có lẽ anh cũng đoán được rồi, không có người bình thường nào đang đứng ở cửa lại lùi dần về phía ban công để rồi ngã thẳng xuống hàng rào sắt như vậy cả.", next: "s899" },
  { id: "s899", bg: "phongphapy.jpg", char: "Kam.png", text: "Chỉ có thể là do khi đó gã đã gặp hung thủ, người vẫn chưa rời khỏi hiện trường nên hoảng loạn, bị đối phương dồn ép nên chạy lung tung và ngã xuống, gây ra cái chết vô tình, có lẽ hung thủ ngay từ ban đầu cũng không định ra tay tới Kael, gã chỉ là nạn nhân nằm ngoài kế hoạch.", next: "s900" },
  { id: "s900", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Hung thủ đã ở hiện trường gần 6 tiếng đồng hồ, dọn dẹp dấu tích một cách tỉ mỉ. Mẹ kiếp, tên đó thực sự là một người bệnh hoạn và liều lĩnh, không tên giết người nào dám ở hiện trường lâu đến như vậy hết trừ khi mọi thứ đều đã được kẻ đó đảm bảo không thể sai sót dù chỉ một chút.", next: "s901" },
  { id: "s901", bg: "phongphapy.jpg", char: "Narrator", text: "Hắn thở dài một hơi, tiếp tục hỏi.", next: "s902" },
  { id: "s902", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Hiện trường thì sao, có những dấu vết nào?", next: "s903" },
  { id: "s903", bg: "phongphapy.jpg", char: "Kam.png", text: "Dưới gầm giường có cưa tay, lưỡi, móng dính máu của Yanna, con bé còn bị cạo trọc đầu nữa nhưng không tìm thấy tóc ở đâu quanh hiện trường.", next: "s904" },
  { id: "s904", bg: "phongphapy.jpg", char: "Kam.png", text: "Vân tay của con bé chỉ cầm chuôi, vân tay Crow cầm cả chuôi lẫn lưỡi cưa, chứng tỏ cậu cố gắng tự sát để Yanna không phải tự mình ra tay giết người.", next: "s905" },
  { id: "s905", bg: "phongphapy.jpg", char: "Kam.png", text: "Khác với Axton có vẻ là do thù oán về tình dục thì vùng kín cả hai nguyên vẹn, Yanna bị ép giết bạn trai, bị tra tấn thảm thương trước khi chết.", next: "s906" },
  { id: "s906", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Nghĩa là Yanna là mục tiêu thực sự, Crow và Kael chỉ là nạn nhân vô tình, bởi xem qua thì kể cả việc bắt con bé giết bạn trai chẳng khác nào một dạng tra tấn tâm lí cả, suy cho cùng tới cuối Yanna mới giống người bị nhắm vào.", next: "s907" },
  { id: "s907", bg: "phongphapy.jpg", char: "Kam.png", text: "Tôi cũng nghĩ vậy. Hung thủ cực kỳ tỉ mỉ, hiểu tâm lý, biết rõ mối quan hệ Yanna và Crow, thao túng cô bé, tận dụng cơ hội cửa không khóa để ra tay hành hạ cô bé.", next: "s908" },
  { id: "s908", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock dựa vào bàn, mắt nhìn chăm chú vào hồ sơ, chi tiết vết bầm trên Crow, vết rạch gọn trên bụng Yanna, thời gian tử vong, cái cưa tay, vân tay.", next: "s909" },
  { id: "s909", bg: "phongphapy.jpg", char: "Yanna.png", text: "Tất cả chỉ ra hung thủ là người có kiến thức, khả năng tiếp cận hiện trường và có khả năng ra vào khu nhà mà không bị nghi ngờ. Khả năng cao là một người trong trường, người quen Yanna và am hiểu cả thói quen Crow.", next: "s910" },
  { id: "s910", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Nếu đúng là người trong trường, điều này giải thích cách thao túng Yanna và tiếp cận căn hộ mà không bị nghi ngờ, có lẽ bình thường người này cũng thường xuyên ra vào nơi ở của Yanna và bạn trai.", next: "s911" },
  { id: "s911", bg: "phongphapy.jpg", char: "Narrator", text: "Heirlock nói rồi nhanh chóng bấm điện thoại gửi đi một tin nhắn cho người cộng sự của mình.", next: "s912" },
  { id: "s912", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Mainson sẽ lập danh sách những nghi phạm có cơ hội và động cơ, tôi sẽ dựng lại chuỗi sự kiện theo thời gian và phân tích kỹ các dấu vết sau, cảm ơn cậu nhiều.", next: "s913" },
  { id: "s913", bg: "phongphapy.jpg", char: "Kam.png", text: "Tôi sẽ hỗ trợ anh, quý ngài thám tử, chỉ cần anh nhờ thì tôi sẵn sàng giúp đỡ.", next: "s914" },
  { id: "s914", bg: "phongphapy.jpg", char: "Narrator", text: "Ánh đèn phòng pháp y lạnh lẽo chiếu xuống từng vết máu đông lại đặc quánh, đen thẫm trên thi thể nạn nhân. Heirlock biết, trò chơi tinh vi này chưa kết thúc, nếu như thực sự kẻ ra tay cả hai vụ là một người trong trường thì hung thủ vẫn còn ở gần, đang theo dõi và chuẩn bị cho bước đi tiếp theo.", next: "s915" },
  { id: "s915", bg: "phongphapy.jpg", char: "Narrator", text: "Có thể là trốn chạy, có thể là một mạng người khác hoặc cũng có thể đưa ra manh mối mới cho cuộc điều tra.", next: "s916" },
  { id:"s916", bg:"phongphapy.jpg", char:"Narrator", text:"Trong khi còn đang suy nghĩ miên man thì điện thoại Heirlock rung lên, màn hình hiện tên của Mainson, hắn bắt máy, trầm tĩnh gọi tên đối phương.", next:"s917" },
  { id:"s917", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Sao rồi, Mainson?", next:"s918" },
  { id:"s918", bg:"phongphapy.jpg", char:"Narrator", text:"Giọng Mainson vang lên lẫn trong tiếng gió và bước chân gấp gáp.", next:"s919" },
  { id:"s919", bg:"phongphapy.jpg", char:"Mainson.png", text:"Anh nghe rõ không, tôi nghĩ tôi sẽ chuẩn bị tìm gặp chủ nhà của nơi Crow và Yanna đã ở.", next:"s920" },
  { id:"s920", bg:"phongphapy.jpg", char:"Mainson.png", text:"Tôi vừa hỏi gần như toàn bộ mọi người trong trường K.K, kể cả học sinh lẫn giáo viên nhưng có vẻ không có nhiều thông tin, cả 4 người đã chết đều là học sinh và giáo viên của ngôi trường này, vậy mà chẳng thể tìm được gì.", next:"s921" },
  { id:"s921", bg:"phongphapy.jpg", char:"Narrator", text:"Cậu yên lặng một chút, tiếng mưa rơi lộp độp trên mái tôn tạo thành một bản giao hưởng êm tai, Heirlock đáp lời.", next:"s922" },
  { id:"s922", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Không có bất cứ manh mối nào sao?", next:"s923" },
  { id:"s923", bg:"phongphapy.jpg", char:"Mainson.png", text:"Cũng không hẳn là vậy, sau cả buổi gặng hỏi thì cuối cùng cũng moi ra được một chuyện mà trước giờ chẳng mấy ai để ý. Có một người bình thường hay trò chuyện với Crow, mà lại còn là người chúng ta đều biết nữa.", next:"s924" },
  { id:"s924", bg:"phongphapy.jpg", char:"Narrator", text:"Heirlock im lặng, ngoại trừ 4 nạn nhân gồm Axton, Kael, Yanna và Crow ra thì hắn và Mainson có thể quen biết thêm ai nữa ngoại trừ hiệu trưởng, nhưng ông ta hầu như chẳng có mặt ở trường.", next:"s925" },
  { id:"s925", bg:"phongphapy.jpg", char:"Narrator", text:"Hơn nữa, nếu có xích mích thật thì ông ta cũng không ngu ngốc tới mức khiến ngôi trường mình dày công gây dựng bị mang tiếng vì những cái chết kì lạ ấy. Vậy thì chỉ còn một khả năng nữa: người giáo viên mà lần đầu Heirlock và Mainson nói chuyện cùng.", next:"s926" },
  { id:"s926", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Có phải là cô ta, tên gì nhỉ, cái người phụ nữ đã nói cho chúng ta về mối quan hệ giữa Kael và Yanna ấy.", next:"s927" },
  { id:"s927", bg:"phongphapy.jpg", char:"Narrator", text:"Mainson ừm một tiếng, cậu tiếp tục.", next:"s928" },
  { id:"s928", bg:"phongphapy.jpg", char:"Mainson.png", text:"Đúng vậy, là một giáo viên nữ dạy văn tên Jessica, cũng chính là kẻ mà hôm đó chúng ta hỏi chuyện. Dạo gần đây người ta thấy Jessica đi cùng Crow nhiều tới bất thường.", next:"s929" },
  { id:"s929", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Ý cậu là họ thân thiết tới mức gây cả sự chú ý sao, vậy thì cô ta có lẽ cũng không hẳn là không liên quan tới vụ việc đâu.", next:"s930" },
  { id:"s930", bg:"phongphapy.jpg", char:"Mainson.png", text:"Đúng vậy, tôi còn nghe nói Jessica ở chung nhà với Crow nữa, hai người thường xuyên cùng nhau đi về trong khi Yanna phải ở lại câu lạc bộ mỹ thuật để chuẩn bị cho bài thi tài năng.", next:"s931" },
  { id:"s931", bg:"phongphapy.jpg", char:"Mainson.png", text:"Tôi cũng có hỏi nhưng mọi người chỉ bảo Crow và Jessica có vẻ rất hiểu nhau, như có chuyện gì đó kín đáo nhưng không đủ lộ liễu để ai dám khẳng định.", next:"s932" },
  { id:"s932", bg:"phongphapy.jpg", char:"Narrator", text:"Mainson im lặng một lúc khiến hắn hiểu ngay ý cậu.", next:"s933" },
  { id:"s933", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Ý cậu là Crow ngoại tình với Jessica trong khi vẫn đang có bạn gái là Yanna?", next:"s934" },
  { id:"s934", bg:"phongphapy.jpg", char:"Mainson.png", text:"Ừm, tôi suy đoán có khi Crow ngoại tình với Jessica trong lúc vẫn yêu Yanna. Và nếu đúng vậy thì việc hai người thường xuyên về cùng nhau là hợp lý. Nhưng cũng chỉ là suy đoán tạm thời.", next:"s935" },
  { id:"s935", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Ừ, chỉ như vậy thì chưa đủ để đặt nghi vấn giết người lên một phụ nữ đâu, Mainson.", next:"s936" },
  { id:"s936", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Khả năng Jessica là hung thủ rất thấp. Một giáo viên văn khó có thể treo thi thể Yanna lên tường, trói chặt rồi rạch bụng chuẩn xác đến vậy.", next:"s937" },
  { id:"s937", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Ngay cả nếu có động cơ thì một phụ nữ mảnh mai như Jessica khó lòng nâng cả trọng lượng thi thể lên cao như thế. Nếu cô ta thật sự dính líu thì chắc chắn phải có đồng phạm.", next:"s938" },
  { id:"s938", bg:"phongphapy.jpg", char:"Narrator", text:"Mainson ừm một tiếng. Quả thật nếu Jessica có đồng phạm thì điều đó giải thích vì sao Yanna chịu làm theo ý hung thủ.", next:"s939" },
  { id:"s939", bg:"phongphapy.jpg", char:"Narrator", text:"Với mối quan hệ thân thiết giữa cả ba người, Jessica hoàn toàn có thể nắm giữ điểm yếu của Yanna và Crow khiến họ không thể phản kháng.", next:"s940" },
  { id:"s940", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Vậy Mainson, cậu định làm gì tiếp theo?", next:"s941" },
  { id:"s941", bg:"phongphapy.jpg", char:"Mainson.png", text:"Tôi sẽ đào sâu hơn về Jessica, gia đình, các mối quan hệ, nơi ở khác. Sáng mai tôi sẽ tới văn phòng mang cho anh bản thông tin chi tiết. Ngài thám tử, anh nên nghỉ đi, hôm nay mệt rồi.", next:"s942" },
  { id:"s942", bg:"phongphapy.jpg", char:"Narrator", text:"Mainson thở dài. Cậu biết Heirlock phải làm việc trực tiếp dưới quyền Darius, gã thượng tá khó tính luôn giục khép án nhanh nhất có thể.", next:"s943" },
  { id:"s943", bg:"phongphapy.jpg", char:"Narrator", text:"Và có lẽ hắn cũng cảm nhận được cơn đau đầu cùng cái bụng cồn cào đang gào thét trong cơ thể.", next:"s944" },
  { id:"s944", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Ừ, vậy tôi sẽ nghỉ ngơi đây, hẹn gặp cậu ngày mai, Mainson.", next:"s945" },
  { id:"s945", bg:"phongphapy.jpg", char:"Narrator", text:"Cuộc gọi kết thúc. Heirlock vừa tắt màn hình thì Kam bước ra từ cửa phòng pháp y, một tay cởi áo blouse dính mùi thuốc sát trùng rồi khoác chiếc áo dạ màu xám.", next:"s946" },
  { id:"s946", bg:"phongphapy.jpg", char:"Narrator", text:"Anh ta xoa cổ, thở nhẹ như đã chờ hết giờ làm từ lâu.", next:"s947" },
  { id:"s947", bg:"phongphapy.jpg", char:"Kam.png", text:"Tôi chuẩn bị về đây, hôm nay cũng đủ nặng đầu rồi. Anh có muốn sang nhà tôi uống chút rượu cho thư giãn không, ngài thám tử? Trông anh có vẻ rất mệt mỏi.", next:"s948" },
  { id:"s948", bg:"phongphapy.jpg", char:"Narrator", text:"Heirlock thoáng ngạc nhiên. Bao năm làm việc cùng con người này nhưng chưa từng đến nhà nhau lần nào.", next:"s949" },
  { id:"s949", bg:"phongphapy.jpg", char:"Heirlock.png", text:"Cậu rủ tôi tới nhà?", next:"s950" },
  { id:"s950", bg:"phongphapy.jpg", char:"Narrator", text:"Kam bật cười trước vẻ mặt ngạc nhiên của đối phương, tiến lại gần chỉ còn cách vài cm.", next:"s951" },
  { id:"s951", bg:"phongphapy.jpg", char:"Kam.png", text:"Sao vậy, chê nhà của pháp y sẽ có mùi thuốc sát trùng thay vì nước hoa à? Tôi sống sạch sẽ lắm. Dạo này anh áp lực rồi, tới chỗ tôi làm ly rượu xả hơi một chút.", next:"s952" },
  { id:"s952", bg:"phongphapy.jpg", char:"Narrator", text:"Kam mỉm cười, mái tóc đen mềm mại khiến Heirlock không kiềm được ánh nhìn.", next:"s953" },
  { id:"s953", bg:"phongphapy.jpg", char:"Narrator", text:"Anh ta vẫn như thế, vẻ thong thả, quầng thâm xám xịt, nụ cười như một tên ngốc chứ chẳng phải vị pháp y đại tài phá biết bao vụ án.", next:"s954" },
  { id: "s954", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Vậy thì…", 
    choice: true, options: [ 
      { text: "Đi tới nhà Kam", goto: "s955" }, 
      { text: "Không tới nhà Kam", goto: "s985986963" } 
    ] 
  },
  { id: "s955", bg: "phongphapy.jpg", char: "Heirlock.png", text: "Cho tôi tới nhà cậu đi, tôi cũng muốn được uống chút gì đó thay vì đối mặt với đống tài liệu chưa dọn trên sàn ở văn phòng, có lẽ tôi thật sự cần một trợ lý rồi.", next: "s956" },
  { id: "s956", bg: "phongphapy.jpg", char: "Narrator", text: "Hắn thở dài mệt mỏi, nghĩ bụng Kam vốn hiền lành, điềm tĩnh lại dễ nói chuyện nên Heirlock cũng chẳng nghĩ ngợi lâu, chỉ cảm thấy một chén rượu tối nay có lẽ sẽ làm nhẹ đầu óc sau chuỗi vụ án bế tắc.", next: "s957" },
  { id: "s957", bg: "duongmua.jpg", next: "s958" },
  { id: "s958", bg: "duongmua.jpg", char: "Narrator", text: "Trên xe, Heirlock nhìn qua cửa thấy trời mưa nặng hạt hơn, kính xe phủ đặc những vệt nước loang. Kam lái chậm rãi, giọng anh ta nhẹ và có phần thoải mái hiếm thấy.", next: "s959" },
  { id: "s959", bg: "duongmua.jpg", char: "Kam.png", text: "Kể cũng hay thật, gần chục năm làm việc chung mà đây là lần đầu anh tới nhà tôi.", next: "s960" },
  { id: "s960", bg: "duongmua.jpg", char: "Heirlock.png", text: "Thế còn cậu, cậu cũng chưa ghé văn phòng thám tử tôi lần nào.", next: "s961" },
  { id: "s961", bg: "duongmua.jpg", char: "Narrator", text: "Kam mỉm cười, một tay nắm lấy chiếc vô lăng mà xoay một vòng chậm rãi, chiếc xe hướng về phía bên phải khiến nó hơi nghiêng ngả, Heirlock theo đó mà phải chống một tay lên lớp kính để bản thân không va phải cánh cửa bên cạnh.", next: "s962" },
  { id: "s962", bg: "duongmua.jpg", char: "Kam.png", text: "Bận mà, công việc của tôi vắt kiệt thời gian, đi làm về là chỉ muốn nằm xuống giường. Hơn nữa, quý ngài thám tử cũng thấy rồi đó, tôi cũng hơi kém giao tiếp nên chẳng có mấy bạn bè.", next: "s963" },
  { id: "s963", bg: "duongmua.jpg", char: "Heirlock.png", text: "Vậy cậu sống một mình luôn à?", next: "s964" },
  { id: "s964", bg: "duongmua.jpg", char: "Narrator", text: "Heirlock hỏi chuyện, dù cho quen biết đã lâu, tới mức đủ để quen thuộc với khẩu vị đồ uống của người bạn pháp y này nhưng hắn vẫn chưa từng thật sự biết về cuộc sống riêng phía sau lớp áo blouse trong căn phòng sặc mùi tử khí kia.", next: "s965" },
  { id: "s965", bg: "duongmua.jpg", char: "Narrator", text: "Liệu khi cởi bỏ cặp kính dày, chiếc bút được vài trên túi áo ngực, cặp găng tay y tế trắng và những cái xác không nguyên vẹn đó thì Kam là một người thế nào, hắn thực sự không giấu nổi sự tò mò.", next: "s966" },
  { id: "s966", bg: "duongmua.jpg", char: "Kam.png", text: "Ừ. À không, trước đây không hẳn. Tôi có một người em gái. Con bé dễ thương, tốt bụng, lúc nào cũng cười, cứ như mang cả mặt trời phía sau lưng vậy.", next: "s967" },
  { id: "s967", bg: "duongmua.jpg", char: "Kam.png", text: "Mỗi tối tôi về là nó chạy ra hỏi hôm nay có vui không, có gặp người tốt không, hỏi đến mức khiến cái đầu tôi quay cuồng cả lên nhưng lại chẳng thể chịu được nếu không có.", next: "s968" },
  { id: "s968", bg: "duongmua.jpg", char: "Kam.png", text: "Con bé ấy tựa như một người họa sĩ đang ra sức vẽ lên những bức tranh sống động, tươi sáng và đầy màu sắc lên cuộc đời toàn xác chết và máu me của tôi. Anh biết đấy, tôi sống hơi khép kín nên con bé là người duy nhất khiến tôi mở miệng nhiều hơn vài câu.", next: "s969" },
  { id: "s969", bg: "duongmua.jpg", char: "Heirlock.png", text: "Em gái cậu tên gì thế?", next: "s970" },
  { id: "s970", bg: "duongmua.jpg", char: "Kam.png", text: "Violet, một bông hoa tím, cái tên này là tôi đặt đó.", next: "s971" },
  { id: "s971", bg: "duongmua.jpg", char: "Heirlock.png", text: "Nghe dễ thương thật. Thế giờ em gái cậu có ở nhà không, để lát tôi ghé tiệm mua ít bánh ngọt cho nó, trẻ con thích đồ ngọt mà.", next: "s972" },
  { id: "s972", bg: "duongmua.jpg", char: "Kam.png", text: "Con bé mất rồi. Tự sát.", next: "s973" },
  { id: "s973", bg: "duongmua.jpg", char: "Heirlock.png", text: "Cái đó, xin lỗi, tôi không biết.", next: "s974" },
  { id: "s974", bg: "duongmua.jpg", char: "Kam.png", text: "Không sao, cũng đâu phải lỗi của anh.", next: "s975" },
  { id: "s975", bg: "nhacuaKam.jpg", next: "s976" },
  { id: "s976", bg: "nhacuaKam.jpg", char: "Narrator", text: "Trong nhà, Kam rót hai ly rượu vang rồi đặt một ly trước mặt Heirlock, hắn mỉm cười nhẹ như cố xua bớt không khí nặng nề.", next: "s977" },
  { id: "s977", bg: "nhacuaKam.jpg", char: "Heirlock.png", text: "Chưa ăn gì mà uống rượu ngay à?", next: "s978" },
  { id: "s978", bg: "nhacuaKam.jpg", char: "Narrator", text: "Kam lắc đầu rồi lấy từ trong tủ ra một miếng thịt bò tảng lớn và bật bếp, những đốm lửa xanh đỏ bập bùng trên chiếc bếp ga như thay cho câu trả lời.", next: "s979" },
  { id: "s979", bg: "nhacuaKam.jpg", char: "Kam.png", text: "Tôi nấu cho. Anh cứ đi xem nhà, thích vào đâu thì cứ vào.", next: "s980" },
  { id: "s980", bg: "nhacuaKam.jpg", char: "Narrator", text: "Hắn ngại ngùng ậm ừ rồi gật đầu, Heirlock biết Kam đang cố gắng tránh nói về chủ đề của em gái vậy nên chỉ đành im lặng nhìn xung quanh một vòng.", next: "s981" },
  { id: "s981", bg: "nhacuaKam.jpg", char: "Narrator", text: "Heirlock đi dọc hành lang, ánh đèn vàng tạo cảm giác ấm cúng nhưng chẳng hiểu sao lại thấy âm thầm bất an. Hắn bước ngang qua vài phòng rồi dừng lại trước một cánh cửa dẫn xuống tầng hầm.", next: "s982" },
  { id: "s982", bg: "nhacuaKam.jpg", char: "Narrator", text: "Heirlock nên…", 
    choice: true, options: [ 
      { text: "Không vào tầng hầm", goto: "s5675765559" }, 
      { text: "Vào tầng hầm", goto: "s983" }
    ] 
  },
  { id: "s983", bg: "nhacuaKam.jpg", char: "Narrator", text: "Nhớ lại lời Kam nói thích vào đâu thì vào nên Heirlock nghĩ chắc chẳng sao, và ngạc nhiên là cánh cửa lại không hề được khóa.", next: "s984" },
  { id: "s984", bg: "tangham.jpg", next: "s985" },
  { id: "s985", bg: "tangham.jpg", char: "Narrator", text: "Heirlock mở ra. Một luồng khí lạnh phả thẳng lên, mùi của máu và tử thi chỉ thoáng qua nhưng đủ khiến hắn như nghẹn lại, Heirlock luống cuống vớ lấy cái công tắc điện bên cạnh cửa ra vào căn hầm, bật đèn.", next: "s986" },
  { id: "s986", bg: "tangham.jpg", char: "Narrator", text: "Ở giữa căn phòng trống lạnh ấy là một mô hình người bằng nhựa, đầu đội một mảng tóc hồng còn dính máu ở phần ngọn, thậm chí còn có vài giọt máu đông lại thành vệt đỏ thẫm thoang thoảng mùi chết chóc phảng phất quanh căn phòng này chảy dài trên khuôn mặt trắng bệch của con hình nhân.", next: "s987" },
  { id: "s987", bg: "tangham.jpg", char: "Narrator", text: "Màu tóc quen thuộc đến mức làm Heirlock lạnh buốt sống lưng. Đó là tóc của Yanna, thứ mà cả đội chưa từng tìm thấy ở hiện trường dù cho đầu nạn nhân bị cạo trọc. Hắn lập tức quay người định chạy khỏi nơi này.", next: "s988" },
  { id: "s988", bg: "tangham.jpg", char: "Narrator", text: "Heirlock rùng mình, từng suy nghĩ điên rồ lần lượt lướt qua đại não hắn khiến cho cả cơ thể cứng đờ, Kam là hung thủ, là tên sát nhân bản thân vẫn luôn tìm kiếm lâu nay, hóa ra kẻ giết người lại ở gần bản thân tới vậy.", next: "s989" },
  { id: "s989", bg: "tangham.jpg", char: "Narrator", text: "Hắn định báo cho Mainson trong khi trốn thoát để nhanh chóng cử người tới bắt lấy hung thủ nhưng chưa kịp rút điện thoại thì Kam đã đứng ngay sau lưng từ lúc nào, ánh mắt anh trầm xuống, hàng lông mi dài hơi cụp xuống, giọng tiếc nuối.", next: "s990" },
  { id: "s990", bg: "tangham.jpg", char: "Kam.png", text: "Heirlock, anh không nên xuống đây.", next: "s991" },
  { id: "s991", bg: "tangham.jpg", char: "Narrator", text: "Kam giật mạnh điện thoại từ tay Heirlock rồi xịt thứ gì đó thẳng vào mặt hắn, mùi hóa chất xộc vào mũi khiến đầu óc Heirlock quay cuồng. Hắn ngã xuống, mất ý thức.", next: "s992" },
  { id: "s992", bg: "tangham.jpg", char: "Narrator", text: "Khi mở mắt lại, cơ thể Heirlock đã trói chặt vào một chiếc ghế đặt giữa tầng hầm ẩm lạnh. Kam ngồi trước mặt hắn, khuôn mặt không còn vẻ hiền lành quen thuộc mà thay bằng một sự bình thản đáng sợ, anh ta ngắm nghĩa con dao sắc nhọn trên tay mình, thở dài.", next: "s993" },
  { id: "s993", bg: "tangham.jpg", char: "Kam.png", text: "Anh biết không, thật sự chúng ta đã quen biết nhau quá lâu, tới mức tôi không nỡ ra tay dù biết rõ để anh lại thì sẽ chẳng thể hoàn thành đích đến.", next: "s994" },
  { id: "s994", bg: "tangham.jpg", char: "Kam.png", text: "Quý ngài thám tử à, lời cảm ơn khi đó là lời thật lòng đấy, tôi đã mong anh có thể tìm ra mình, còng tay tôi và nói rằng pháp luật sẽ luôn trừng phạt kẻ ác và mang lại công bằng cho người vô tội.", next: "s995" },
  { id: "s995", bg: "tangham.jpg", char: "Narrator", text: "Kam bật cười, lưỡi dao sắc nhọn cứa lên đầu ngón tay anh làm máu rỉ ra.", next: "s996" },
  { id: "s996", bg: "tangham.jpg", char: "Kam.png", text: "Tôi đã tự nhủ rằng nếu số phận để anh không bước vào căn phòng này thì sẽ để anh sống, để chúng ta vẫn sẽ tiếp tục là bạn và khi tôi hoàn thành xong việc rồi sẽ tự mình ra đầu thú. Nhưng anh lại xuống đây. Thám tử, anh thấy thứ không được thấy và giờ tôi phải giải quyết thôi.", next: "s997" },
  { id: "s997", bg: "tangham.jpg", char: "Heirlock.png", text: "Kam, cậu…", next: "s998" },
  { id: "s998", bg: "tangham.jpg", char: "Narrator", text: "Người đàn ông trước mắt ngước lên, Heirlock có thể cảm nhận được sự day dứt và nỗi buồn nơi đáy mắt nhưng cũng đồng thời thấy rùng mình trước sự lạnh lẽo của lưỡi dao kề nơi cổ.", next: "s999" },
  { id: "s999", bg: "tangham.jpg", char: "Heirlock.png", text: "Kam, dừng lại. Chúng ta không còn cách nào khác sao?", next: "s1000" },
  { id: "s1000", bg: "tangham.jpg", char: "Kam.png", text: "Tôi xin lỗi. Quý ngài thám tử, chờ tôi chút thôi, tôi sẽ nhanh chóng xong việc và tới tìm anh, kiếp sau có thể khao tôi ly cafe sữa đó thêm lần nữa không?", next: "s1001" },
  { id: "s1001", bg: "tangham.jpg", char: "Narrator", text: "Kam đưa lưỡi dao lên kề vào cổ Heirlock, tay anh ta vững tới đáng sợ. Một đường cứa lạnh lẽo lên da thịt hắn, máu nóng trào xuống.", next: "s1002" },
  { id: "s1002", bg: "tangham.jpg", char: "Narrator", text: "Tầm nhìn của Heirlock nhòe đi, căn phòng xoay vòng, tiếng Kam gọi tên anh xa dần rồi tan vào bóng tối, màn đêm khép lại.", next: "s1003" },
  { id: "s1003", bg: "manhinhmau.jpg", next: "s1004" },
  { id: "s1004", bg: "niggascreen.jpg", next: "s1005" },
  { id: "s1005", bg: "kiepsaukhaotoicafesuanhe.jpg", ending: "B" },


      

];
